setup_jstree([
{id: "3961", parent: "كِتَابُ الْإِيمَانَ", text: "" },
{id: "4017", parent: "3961", text: "بَابُ معرفة الْإِيمَانِ، وَالْإِسْلَامِ، والقَدَرِ وَعَلَامَةِ السَّاعَةِ قَالَ أَبُو الْحُسَيْنِ مُسْلِمُ بْنُ الْحَجَّاجِ الْقُشَيْرِيُّ رَحِمَهُ اللهُ: «بِعَوْنِ اللهِ نَبْتَدِئُ، وَإِيَّاهُ نَسْتَكْفِي، وَمَا تَوْفِيقُنَا إِلَّا بِاللهِ جَلَّ جَلَالُهُ» " },
{id: "4018", parent: "3961", text: "بَابٌ: الْإِيمَانُ مَا هُوَ وَبَيَانُ خِصَالِهِ " },
{id: "4019", parent: "3961", text: "بَابُ الْإِسْلَامِ مَا هُوَ وَبَيَانُ خِصَالِهِ " },
{id: "4020", parent: "3961", text: "بَابُ بَيَانِ الصَّلَوَاتِ الَّتِي هِيَ أَحَدُ أَرْكَانِ الْإِسْلَامِ " },
{id: "4021", parent: "3961", text: "بَابٌ فِي بَيَانِ الْإِيمَانِ بِاللهِ وَشَرَائِعِ الدِّينِ " },
{id: "4022", parent: "3961", text: "بَابُ بَيَانِ الْإِيمَانِ الَّذِي يُدْخَلُ بِهِ الْجَنَّةَ، وَأَنَّ مَنْ تَمَسَّكَ بِمَا أُمِرَ بِهِ دَخَلَ الْجَنَّةَ " },
{id: "4023", parent: "3961", text: "بَابُ قول النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بُنِيَ الْإِسْلَامُ عَلَى خَمْسٍ " },
{id: "4024", parent: "3961", text: "بَابُ الْأَمْرِ بِالْإِيمَانِ بِاللهِ وَرَسُولِهِ، وَشَرَائِعِ الدِّينِ، وَالدُّعَاءِ إِلَيْهِ " },
{id: "4025", parent: "3961", text: "باب الدعاء إلى الشهادتين وشرائع الإسلام " },
{id: "4026", parent: "3961", text: "بَابُ الْأَمْرِ بِقِتَالِ النَّاسِ حَتَّى يَقُولُوا: لَا إِلَهَ إِلَّا اللهُ مُحَمَّدٌ رَسُولُ اللهِ " },
{id: "4027", parent: "3961", text: "بَابُ أَوَّلُ الْإِيمَانِ قَوْلُ لَا إِلَهَ إِلَّا اللهُ " },
{id: "4028", parent: "3961", text: "بَابُ مَنْ لَقِي اللهَ بِالْإِيمَانِ وَهُو غَيْرُ شَاكٍّ فِيهِ دَخَلَ الْجَنَّةَ وَحُرِّمَ عَلَى النَّارِ " },
{id: "4029", parent: "3961", text: "بَابُ ذَاقَ طَعْمَ الْإِيمَانِ مَنْ رَضِي بِاللهِ رَبًّا " },
{id: "4030", parent: "3961", text: "بَابُ شُعَبِ الْإِيمَانِ " },
{id: "4031", parent: "3961", text: "بَابُ جَامِعِ أَوْصَافِ الْإِسْلَامِ " },
{id: "4032", parent: "3961", text: "بَابُ بَيَانِ تَفَاضُلِ الْإِسْلَامِ، وَأَيُّ أُمُورِهِ أَفْضَلُ " },
{id: "4033", parent: "3961", text: "بَابُ بَيَانِ خِصَالٍ مَنِ اتَّصفَ بِهِنَّ وَجَدَ حَلَاوَةَ الْإِيمَانِ " },
{id: "4034", parent: "3961", text: "بَابُ وُجُوبِ مَحَبَّةِ رَسُولِ اللهِ صلَّى الله عَلَيْهِ وَسَلَّمَ أَكْثَرَ مِنَ الْأَهْلِ وَالْوَلَدِ، وَالْوَالِدِ والنَّاسِ أجْمَعِينَ، وَإِطْلَاقِ عَدَمِ الْإِيمَانِ عَلَى مَنْ لمْ يُحِبَّهُ هَذِهِ الْمَحَبَّةَ " },
{id: "4035", parent: "3961", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ مِنْ خِصَالِ الْإِيمَانِ أَنْ يُحِبَّ لِأَخِيهِ الْمُسْلِمِ مَا يُحِبُّ لِنَفْسِهِ مِنَ الْخَيْرِ " },
{id: "4036", parent: "3961", text: "بَابُ بَيَانِ تَحْرِيمِ إِيذَاءِ الْجَارِ " },
{id: "4037", parent: "3961", text: "بَابُ الْحَثِّ عَلَى إِكْرَامِ الْجَارِ وَالضَّيْفِ، وَلُزُومِ الصَّمْتِ إِلَّا عَنِ الْخَيْرِ وَكَوْنِ ذَلِكَ كُلِّهِ مِنَ الْإِيمَانِ " },
{id: "4038", parent: "3961", text: "بَابُ بَيَانِ كَوْنِ النَّهْيِ عَنِ الْمُنْكَرِ مِنَ الْإِيمَانِ، وَأَنَّ الْإِيمَانَ يَزِيدُ وَيَنْقُصُ، وَأَنَّ الْأَمْرَ بِالْمَعْرُوفِ وَالنَّهْيَ عَنِ الْمُنْكَرِ وَاجِبَانِ " },
{id: "4039", parent: "3961", text: "بَابُ تَفَاضُلِ أَهْلِ الْإِيمَانِ فِيهِ، وَرُجْحَانِ أَهْلِ الْيَمَنِ فِيهِ " },
{id: "4040", parent: "3961", text: "بَابُ بَيَانِ أَنَّهُ لَا يَدْخُلُ الْجَنَّةَ إِلَّا الْمُؤْمِنُونَ، وَأَنَّ مَحَبَّةَ الْمُؤْمِنِينَ مِنَ الْإِيمَانِ، وَأَنَّ إِفْشَاءَ السَّلَامِ سَبَبًا لِحُصُولِهَا " },
{id: "4041", parent: "3961", text: "بَابُ بَيَانِ أَنَّ الدِّينَ النَّصِيحَةُ " },
{id: "4042", parent: "3961", text: "باب بيان نقصان الإيمان بالمعاصي ونفيه عن المتلبس بالمعصية على إرادة نفي كماله " },
{id: "4043", parent: "3961", text: "بَابُ بَيَانِ خِصَالِ الْمُنَافِقِ " },
{id: "4044", parent: "3961", text: "بَابُ بَيَانِ حَالِ إِيمَانِ مَنْ قَالَ لِأَخِيهِ الْمُسْلِمِ: يَا كَافِرُ " },
{id: "4045", parent: "3961", text: "بَابُ بَيَانِ حَالِ إِيمَانِ مَنْ رَغِبَ عَنْ أَبِيهِ وَهُوَ يَعْلَمُ " },
{id: "4046", parent: "3961", text: "بَابُ بَيَانِ قَوْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «سِبَابُ الْمُسْلِمِ فُسُوقٌ وَقِتَالُهُ كُفْرٌ» " },
{id: "4047", parent: "3961", text: "بَابُ «لَا تَرْجِعُوا بَعْدِي كُفَّارًا يَضْرِبُ بَعْضُكُمْ رِقَابَ بَعْضٍ» " },
{id: "4048", parent: "3961", text: "بَابُ إِطْلَاقِ اسْمِ الْكُفْرِ عَلَى الطَّعْنِ فِي النَّسَبِ وَالنِّيَاحَةِ عَلَى الْمَيِّتِ " },
{id: "4049", parent: "3961", text: "بَابُ تَسْمِيَةِ الْعَبْدِ الْآبِقِ كَافِرًا " },
{id: "4050", parent: "3961", text: "بَابُ بَيَانِ كُفْرِ مَنْ قَالَ: مُطِرْنَا بِالنَّوْءِ " },
{id: "4051", parent: "3961", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ حُبَّ الْأنْصَارِ وَعَلِيٍّ رَضِيَ اللهُ عَنْهُمْ مِنَ الْإِيمَانِ وَعَلَامَاتِهِ، وَبُغْضِهِمْ مِنْ عَلَامَاتِ النِّفَاقِ " },
{id: "4052", parent: "3961", text: "بَابُ بَيَانِ نُقْصَانِ الْإِيمَانِ بِنَقْصِ الطَّاعَاتِ، وَبَيَانِ إِطْلَاقِ لَفْظِ الْكُفْرِ عَلَى غَيْرِ الْكُفْرِ بِاللهِ، كَكُفْرِ النِّعْمَةِ وَالْحُقُوقِ " },
{id: "4053", parent: "3961", text: "بَابُ بَيَانِ إِطْلَاقِ اسْمِ الْكُفْرِ عَلَى مَنْ تَرَكَ الصَّلَاةَ " },
{id: "4054", parent: "3961", text: "بَابُ بَيَانِ كَوْنِ الْإِيمَانِ بِاللهِ تَعَالَى أَفْضَلَ الْأَعْمَالِ " },
{id: "4055", parent: "3961", text: "بَابُ كَوْنِ الشِّرْكِ أَقْبَحَ الذُّنُوبِ، وَبَيَانِ أَعْظَمِهَا بَعْدَهُ " },
{id: "4056", parent: "3961", text: "بَابُ بَيَانِ الْكَبَائِرِ وَأَكْبَرِهَا " },
{id: "4057", parent: "3961", text: "بَابُ تَحْرِيمِ الْكِبْرِ وَبَيَانِهِ " },
{id: "4058", parent: "3961", text: "بَابُ مَنْ مَاتَ لَا يُشْرِكُ بِاللهِ شَيْئًا دَخَلَ الْجَنَّةَ، وَمَنْ مَاتَ مُشْرِكًا دَخَلَ النَّارَ " },
{id: "4059", parent: "3961", text: "بَابُ تَحْرِيمِ قَتْلِ الْكَافِرِ بَعْدَ أَنْ قَالَ: لَا إِلَهَ إِلَّا اللهُ " },
{id: "4060", parent: "3961", text: "بَابُ قَوْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «مَنْ حَمَلَ عَلَيْنَا السِّلَاحَ فَلَيْسَ مِنَّا» " },
{id: "4061", parent: "3961", text: "بَابُ قوْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «مَنْ غَشَّنَا فَلَيْسَ مِنَّا» " },
{id: "4062", parent: "3961", text: "بَابُ تَحْرِيمِ ضَرْبِ الْخُدُودِ وَشَقِّ الْجُيُوبِ وَالدُّعَاءِ بِدَعْوَى الْجَاهِلِيَّةِ " },
{id: "4063", parent: "3961", text: "بَابُ بَيَانِ غِلَظِ تَحْرِيمِ النَّمِيمَةِ " },
{id: "4064", parent: "3961", text: "بَابُ بَيَانِ غِلَظِ تَحْرِيمِ إِسْبَالِ الْإِزَارِ، وَالْمَنِّ بِالْعَطِيَّةِ، وَتَنْفِيقِ السِّلْعَةِ بِالْحَلِفِ، وَبَيَانِ الثَّلَاثَةِ الَّذِينَ لَا يُكَلِّمُهُمُ اللهُ يَوْمَ الْقِيَامَةِ، وَلَا يَنْظُرُ إِلَيْهِمْ، وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ " },
{id: "4065", parent: "3961", text: "بَابُ غِلَظِ تَحْرِيمِ قَتْلِ الْإِنْسَانِ نَفْسَهُ، وَأَنَّ مَنْ قَتَلَ نَفْسَهُ بِشَيْءٍ عُذِّبَ بِهِ فِي النَّارِ، وَأَنَّهُ لَا يَدْخُلُ الْجَنَّةَ إِلَّا نَفْسٌ مُسْلِمَةٌ " },
{id: "4066", parent: "3961", text: "بَابُ غِلَظِ تَحْرِيمِ الْغُلُولِ، وَأَنَّهُ لَا يَدْخُلُ الْجَنَّةَ إِلَّا الْمُؤْمِنُونَ " },
{id: "4067", parent: "3961", text: "بَابُ الدَّلِيلِ عَلَى أَنْ قَاتِلَ نَفْسِهُ لَا يَكْفُرُ " },
{id: "4068", parent: "3961", text: "بَابٌ فِي الرِّيحِ الَّتِي تَكُونُ قُرْبَ الْقِيَامَةِ، تَقْبِضُ مَنْ فِي قَلْبِهِ شَيْءٌ مِنَ الْإِيمَانِ " },
{id: "4069", parent: "3961", text: "بَابُ الْحَثِّ عَلَى الْمُبَادَرَةِ بِالْأَعْمَالِ قَبْلَ تَظَاهُرِ الْفِتَنِ " },
{id: "4070", parent: "3961", text: "بَابُ مَخَافَةِ الْمُؤْمِنِ أَنْ يَحْبَطَ عَمَلُهُ " },
{id: "4071", parent: "3961", text: "بَابُ هَلْ يُؤَاخَذُ بِأَعْمَالِ الْجَاهِلِيَّةِ؟ " },
{id: "4072", parent: "3961", text: "بَابُ كَوْنِ الْإِسْلَامِ يَهْدِمُ مَا قَبْلَهُ وَكَذَا الْهِجْرَةِ وَالْحَجِّ " },
{id: "4073", parent: "3961", text: "بَابُ بَيَانِ حُكْمِ عَمَلِ الْكَافِرِ إِذَا أَسْلَمَ بَعْدَهُ " },
{id: "4074", parent: "3961", text: "بَابُ صِدْقِ الْإِيمَانِ وَإِخْلَاصِهِ " },
{id: "4075", parent: "3961", text: "بَابُ بَيَانِ قَوْلِهِ تَعَالَى: {وَإِنْ تُبْدُوا مَا فِي أَنْفُسِكُمْ أَوْ تُخْفُوهُ} [البقرة: 284] " },
{id: "4076", parent: "3961", text: "بَابُ تَجَاوُزِ اللهِ عَنْ حَدِيثِ النَّفْسِ وَالْخَوَاطِرِ بِالْقَلْبِ، إِذَا لَمْ تَسْتَقِرَّ " },
{id: "4077", parent: "3961", text: "بَابُ إِذَا هَمَّ الْعَبْدُ بِحَسَنَةٍ كُتِبَتْ، وَإِذَا هَمَّ بِسَيِّئَةٍ لَمْ تُكْتَبْ " },
{id: "4078", parent: "3961", text: "بَابُ بَيَانِ الْوَسْوَسَةِ فِي الْإِيمَانِ وَمَا يَقُولُهُ مَنْ وَجَدَهَا " },
{id: "4079", parent: "3961", text: "بَابُ وَعِيدِ مَنِ اقْتَطَعَ حَقَّ مُسْلِمٍ بِيَمِينٍ فَاجِرَةٍ بِالنَّارِ " },
{id: "4080", parent: "3961", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ مَنْ قَصَدَ أَخْذَ مَالِ غَيْرِهِ بِغَيْرِ حَقٍّ، كَانَ الْقَاصِدُ مُهْدَرَ الدَّمِ فِي حَقِّهِ، وَإِنْ قُتِلَ كَانَ فِي النَّارِ، وَأَنَّ مَنْ قُتِلَ دُونَ مَالِهِ فَهُوَ شَهِيدٌ " },
{id: "4081", parent: "3961", text: "بَابُ اسْتِحْقَاقِ الْوَالِي الْغَاشِّ لِرَعِيَّتِهِ النَّارَ " },
{id: "4082", parent: "3961", text: "بَابُ رَفْعِ الْأَمَانَةِ وَالْإِيمَانِ مِنْ بَعْضِ الْقُلُوبِ، وَعَرْضِ الْفِتَنِ عَلَى الْقُلُوبِ " },
{id: "4083", parent: "3961", text: "بَابُ بَيَانِ أَنَّ الْإِسْلَامِ بَدَأَ غَرِيبًا وَسَيَعُودُ غَرِيبًا، وَأَنَّهُ يَأْرِزُ بَيْنَ الْمَسْجِدَيْنِ " },
{id: "4084", parent: "3961", text: "باب بدأ الإسلام غريبًا " },
{id: "4085", parent: "3961", text: "بَابُ ذَهَابِ الْإِيمَانِ آخَرِ الزَّمَانِ " },
{id: "4086", parent: "3961", text: "بَابُ الِاسْتِسْرَارِ لِلْخَائِفِ " },
{id: "4087", parent: "3961", text: "بَابُ تَأَلُّفِ قَلْبِ مَنْ يَخَافُ عَلَى إِيمَانِهِ لِضَعْفِهِ، وَالنَّهْيِ عَنِ الْقَطْعِ بِالْإِيمَانِ مِنْ غَيْرِ دَلِيلٍ قَاطِعٍ " },
{id: "4088", parent: "3961", text: "بَابُ زِيَادَةِ طُمَأْنِينَةِ الْقَلْبِ بِتَظَاهُرِ الْأَدِلَّةِ " },
{id: "4089", parent: "3961", text: "بَابُ وُجُوبِ الْإِيمَانِ بِرِسَالَةِ نَبِيِّنَا مُحَمَّدٍ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ إِلَى جَمِيعِ النَّاسِ، وَنَسْخِ الْمِلَلِ بِمِلَّتِهِ " },
{id: "4090", parent: "3961", text: "باب وجوب إيمان أهل الكتاب برسالة الإسلام " },
{id: "4091", parent: "3961", text: "بَابُ نُزُولِ عِيسَى ابْنِ مَرْيَمَ حَاكِمًا بِشَرِيعَةِ نَبِيِّنَا مُحَمَّدٍ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4092", parent: "3961", text: "بَابُ بَيَانِ الزَّمَنِ الَّذِي لَا يُقْبَلُ فِيهِ الْإِيمَانُ " },
{id: "4093", parent: "3961", text: "بَابُ بَدْءِ الْوَحْيِ إِلَى رَسُولِ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4094", parent: "3961", text: "بَابُ الْإِسْرَاءِ بِرَسُولِ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ إِلَى السَّمَاوَاتِ، وَفَرْضِ الصَّلَوَاتِ " },
{id: "4095", parent: "3961", text: "بَابُ ذِكْرِ الْمَسِيحِ ابْنِ مَرْيَمَ، وَالْمَسِيحِ الدَّجَّالِ " },
{id: "4096", parent: "3961", text: "بَابٌ فِي ذِكْرِ سِدْرَةِ الْمُنْتَهَى " },
{id: "4097", parent: "3961", text: "باب {وَلَقَدْ رَآهُ نَزْلَةً أُخْرَى} " },
{id: "4098", parent: "3961", text: "بَابُ مَعْنَى قَوْلِ اللهُ عَزَّ وَجَلَّ: {وَلَقَدْ رَآهُ نَزْلَةً أُخْرَى} [النجم: 13]، وَهَلْ رَأَى النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ رَبَّهُ لَيْلَةَ الْإِسْرَاءِ " },
{id: "4099", parent: "3961", text: "بَابٌ فِي قَوْلِهِ عَلَيْهِ السَّلَامُ: «نُورٌ أَنَّى أَرَاهُ»، وَفِي قَوْلِهِ: «رَأَيْتُ نُورًا» " },
{id: "4100", parent: "3961", text: "بَابٌ فِي قَوْلِهِ عَلَيْهِ السَّلَامُ: إِنَّ اللهُ لَا يَنَامُ، وَفِي قَوْلِهِ: حِجَابُهُ النُّورُ لَوْ كَشَفَهُ لَأَحْرَقَ سُبُحَاتُ وَجْهِهِ مَا انْتَهَى إِلَيْهِ بَصَرُهُ مِنْ خَلْقِهِ " },
{id: "4101", parent: "3961", text: "بَابُ إِثْبَاتِ رُؤْيَةِ الْمُؤْمِنِينَ فِي الْآخِرَةِ رَبَّهُمْ سُبْحَانَهُ وَتَعَالَى " },
{id: "4102", parent: "3961", text: "بَابُ مَعْرِفَةِ طَرِيقِ الرُّؤْيَةِ " },
{id: "4103", parent: "3961", text: "بَابُ إِثْبَاتِ الشَّفَاعَةِ وَإِخْرَاجِ الْمُوَحِّدِينَ مِنَ النَّارِ " },
{id: "4104", parent: "3961", text: "بَابُ آخَرِ أَهْلِ النَّارِ خُرُوجًا " },
{id: "4105", parent: "3961", text: "بَابُ أَدْنَى أَهْلِ الْجَنَّةِ مَنْزِلَةً فِيهَا " },
{id: "4106", parent: "3961", text: "بَابٌ فِي قَوْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «أَنَا أَوَّلُ النَّاسِ يَشْفَعُ فِي الْجَنَّةِ وَأَنَا أَكْثَرُ الْأَنْبِيَاءِ تَبَعًا» " },
{id: "4107", parent: "3961", text: "بَابُ اخْتِبَاءِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ دَعْوَةَ الشَّفَاعَةِ لِأُمَّتِهِ " },
{id: "4108", parent: "3961", text: "بَابُ دُعَاءِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ لِأُمَّتِهِ، وَبُكَائِهِ شَفَقَةً عَلَيْهِمْ " },
{id: "4109", parent: "3961", text: "بَابُ بَيَانِ أَنَّ مِنْ مَاتَ عَلَى الْكُفْرِ فَهُوَ فِي النَّارِ، وَلَا تَنَالُهُ شَفَاعَةٌ، وَلَا تَنْفَعُهُ قَرَابَةُ الْمُقَرَّبِينَ " },
{id: "4110", parent: "3961", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {وَأَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ} [الشعراء: 214] " },
{id: "4111", parent: "3961", text: "بَابُ شَفَاعَةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ لِأَبِي طَالِبٍ وَالتَّخْفِيفِ عَنْهُ بِسَبَبِهِ " },
{id: "4112", parent: "3961", text: "بَابُ أَهْوَنِ أَهْلِ النَّارِ عَذَابًا " },
{id: "4113", parent: "3961", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ مِنْ مَاتَ عَلَى الْكُفْرِ لَا يَنْفَعُهُ عَمَلٌ " },
{id: "4114", parent: "3961", text: "بَابُ مُوَالَاةِ الْمُؤْمِنِينَ وَمُقَاطَعَةِ غَيْرِهِمْ وَالْبَرَاءَةِ مِنْهُمْ " },
{id: "4115", parent: "3961", text: "بَابُ الدَّلِيلِ عَلَى دُخُولِ طَوَائِفَ مِنَ الْمُسْلِمِينَ الْجَنَّةَ بِغَيْرِ حِسَابٍ وَلَا عَذَابٍ " },
{id: "4116  ", parent: "3961", text: "بَابُ كَوْنِ هَذِهِ الْأُمَّةِ نِصْفَ أَهْلِ الْجَنَّةِ " },
{id: "4117  ", parent: "3961", text: "بَابُ قَوْلِهِ يَقُولُ اللهُ لِآدَمَ أَخْرِجْ بَعْثَ النَّارِ مِنْ كُلِّ أَلْفٍ تِسْعَمِائَةٍ وَتِسَعَةٍ وَتِسْعِينَ " },
{id: "3962", parent: "كِتَابُ الطَّهَارَةِ", text: "" },
{id: "4118  ", parent: "3962", text: "بَابُ فَضْلِ الْوُضُوءِ " },
{id: "4119  ", parent: "3962", text: "بَابُ وُجُوبِ الطَّهَارَةِ لِلصَّلَاةِ حَدَّثَنَا سَعِيدُ بْنُ مَنْصُورٍ، وَقُتَيْبَةُ بْنُ سَعِيدٍ، وَأَبُو كَامِلٍ الْجَحْدَرِيُّ، - وَاللَّفْظُ لِسَعِيدٍ -، قَالُوا: حَدَّثَنَا أَبُو عَوَانَةَ، عَنْ سِمَاكِ بْنِ حَرْبٍ، عَنْ مُصْعَبِ بْنِ سَعْدٍ، قَالَ: دَخَلَ عَبْدُ اللهِ بْنُ عُمَرَ عَلَى ابْنِ عَامِرٍ يَعُودُهُ وَهُوَ مَرِيضٌ فَقَالَ: أَلَا تَدْعُو اللهَ لِي يَا ابْنَ عُمَرَ؟ قَالَ: إِنِّي سَمِعْتُ رَسُولَ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ يَقُولُ: «لَا تُقْبَلُ صَلَاةٌ بِغَيْرِ طُهُورٍ وَلَا صَدَقَةٌ مِنْ غُلُولٍ، وَكُنْتَ عَلَى الْبَصْرَةِ». حَدَّثَنَا مُحَمَّدُ بْنُ الْمُثَنَّى، وَابْنُ بَشَّارٍ، قَالَا: حَدَّثَنَا مُحَمَّدُ بْنُ جَعْفَرٍ، حَدَّثَنَا شُعْبَةُ ح، وَحَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، حَدَّثَنَا حُسَيْنُ بْنُ عَلِيٍّ، عَنْ زَائِدَةَ - قَالَ: أَبُو بَكْرٍ - وَوَكِيعٌ، عَنْ إِسْرَائِيلَ كُلُّهُمْ عَنْ سِمَاكِ بْنِ حَرْبٍ بِهَذَا الْإِسْنَادِ عَنِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بِمِثْلِهِ " },
{id: "4120  ", parent: "3962", text: "بَابُ صِفَةِ الْوُضُوءِ وَكَمَالِهِ " },
{id: "4121  ", parent: "3962", text: "بَابُ فَضْلِ الْوُضُوءِ وَالصَّلَاةِ عَقِبَهُ " },
{id: "4122  ", parent: "3962", text: "بَابُ الْصَّلَوَاتِ الْخَمْسِ وَالْجُمُعُةِ إِلَى الْجُمُعَةِ، وَرَمَضَانِ إِلَى رَمَضَانَ مُكفِّرَاتٌ لِمَا بَيْنَهُنَّ مَا اجْتُنِبَتِ الْكَبَائِرُ " },
{id: "4123  ", parent: "3962", text: "بَابُ الذِّكْرِ الْمُسْتَحَبِّ عَقِبَ الْوُضُوءِ " },
{id: "4124  ", parent: "3962", text: "بَابٌ فِي وُضُوءِ النَّبِيِّ صلَّى الله عَلَيْهِ وَسَلَّمَ " },
{id: "4125  ", parent: "3962", text: "بَابُ الْإِيتَارِ فِي الَاسْتِنْثَارِ وَالَاسْتِجْمَارِ " },
{id: "4126  ", parent: "3962", text: "بَابُ وُجُوبِ غَسْلِ الرِّجْلَيْنِ بِكَمَالِهِمَا " },
{id: "4127  ", parent: "3962", text: "بَابُ وُجُوبِ اسْتِيعَابِ جَمِيعِ أَجْزَاءِ مَحَلِّ الطَّهَارَةِ " },
{id: "4128  ", parent: "3962", text: "بَابُ خُرُوجِ الْخَطَايَا مَعَ مَاءِ الْوُضُوءِ " },
{id: "4129  ", parent: "3962", text: "بَابُ اسْتحْبَابِ إِطَالَةِ الْغُرَّةِ وَالتَّحْجِيلِ فِي الْوُضُوءِ " },
{id: "4130  ", parent: "3962", text: "بَابٌ تَبْلُغُ الْحِلْيَةُ حَيْثُ يَبْلُغُ الْوُضُوءُ " },
{id: "4131  ", parent: "3962", text: "بَابُ فَضْلِ إِسْبَاغِ الْوُضُوءِ عَلَى الْمَكَارِهِ " },
{id: "4132  ", parent: "3962", text: "بَابُ السِّوَاكِ " },
{id: "4133  ", parent: "3962", text: "بَابُ خِصَالِ الْفِطْرَةِ " },
{id: "4134  ", parent: "3962", text: "بَابُ الَاسْتِطَابَةِ " },
{id: "4135  ", parent: "3962", text: "باب إذا أتيتم الغائط فلا تستقبلوا القبلة ولا تستدبروها " },
{id: "4136  ", parent: "3962", text: "بَابُ النَّهْيِ عَنِ الَاسْتِنْجَاءِ بِالْيَمِينِ " },
{id: "4137  ", parent: "3962", text: "بَابُ التَّيمُّنِ فِي الطُّهُورِ وَغَيْرِهِ " },
{id: "4138  ", parent: "3962", text: "بَابُ النَّهْيِ عَنِ التَّخلِّي فِي الطُّرُقِ، وَالظِّلَالِ " },
{id: "4139  ", parent: "3962", text: "بَابُ الَاسْتِنْجَاءِ بِالْمَاءِ مِنَ التَّبرُّزِ " },
{id: "4140  ", parent: "3962", text: "بَابُ الْمَسْحِ عَلَى الْخُفَّيْنِ " },
{id: "4141  ", parent: "3962", text: "بَابُ الْمَسْحِ عَلَى النَّاصِيَةِ وَالْعِمَامَةِ " },
{id: "4142  ", parent: "3962", text: "بَابُ التَّوْقِيتِ فِي الْمَسْحِ عَلَى الْخُفَّيْنِ " },
{id: "4143  ", parent: "3962", text: "بَابُ جَوَازِ الصَّلَوَاتِ كُلِّهِا بِوُضُوءٍ وَاحِدٍ " },
{id: "4144  ", parent: "3962", text: "بَابُ كَرَاهَةِ غَمْسِ الْمُتَوَضِّئِ وَغَيْرِهِ يَدَهُ الْمَشْكُوكَ فِي نَجَاسَتِهَا فِي الْإِنَاءِ قَبْلَ غَسْلِهَا ثَلَاثًا " },
{id: "4145  ", parent: "3962", text: "بَابُ حُكْمِ وُلُوغِ الْكَلْبِ " },
{id: "4146  ", parent: "3962", text: "بَابُ النَّهْيِ عَنِ البوْلِ فِي المَاءِ الرَّاكدِ " },
{id: "4147  ", parent: "3962", text: "بَابُ النَّهْيِ عَنِ الَاغْتِسَالِ فِي الْمَاءِ الرَّاكِدِ " },
{id: "4148  ", parent: "3962", text: "بَابُ وُجُوبِ غُسْلِ الْبَوْلِ وَغَيْرِهِ مِنَ النَّجَاسَاتِ إِذَا حَصَلَتْ فِي الْمَسْجِدِ، وَأَنَّ الْأَرْضَ تَطْهُرُ بِالْمَاءِ، مِنْ غَيْرِ حَاجَةٍ إِلَى حَفْرِهَا " },
{id: "4149  ", parent: "3962", text: "بَابُ حُكْمِ بَوْلِ الطِّفْلِ الرَّضِيعِ وَكَيْفيَّةِ غُسْلِهِ " },
{id: "4150  ", parent: "3962", text: "بَابُ حُكْمِ الْمَنِيِّ " },
{id: "4151  ", parent: "3962", text: "بَابُ نَجَاسَةِ الدَّمِ وَكيْفيَّةِ غُسْلِهِ " },
{id: "4152  ", parent: "3962", text: "بَابُ الدَّليلِ عَلَى نَجَاسَةِ الْبَوْلِ وَوُجُوبِ الَاسْتِبْرَاءِ مِنْهُ " },
{id: "3963", parent: "كِتَابُ الْحَيْضِ ", text: "" },
{id: "4153  ", parent: "3963", text: "بَابُ مُبَاشَرَةِ الْحَائِضِ فَوْقَ الْإِزَارِ " },
{id: "4154  ", parent: "3963", text: "بَابُ الَاضْطِجَاعِ مَعَ الْحَائِضِ فِي لِحَافٍ وَاحِدٍ " },
{id: "4155  ", parent: "3963", text: "بَابُ جَوَازِ غُسْلِ الْحَائِضِ رَأْسَ زَوْجِهَا وَتَرْجِيلِهِ وَطَهَارَةِ سُؤْرِهَا وَالَاتِّكَاءِ فِي حِجْرِهَا وَقِرَاءَةِ الْقُرْآنِ فِيهِ " },
{id: "4156  ", parent: "3963", text: "باب الحائض تُناول من المسجد " },
{id: "4157  ", parent: "3963", text: "باب سؤر الحائض " },
{id: "4158  ", parent: "3963", text: "باب اتكاء الرجل في حجر زوجته وهي حائض وقراءة القرآن " },
{id: "4159  ", parent: "3963", text: "باب اصنعوا كل شيء إلا النكاح " },
{id: "4160  ", parent: "3963", text: "بَابُ الْمَذْيِ " },
{id: "4161  ", parent: "3963", text: "بَابُ غَسْلِ الْوَجْهِ وَالْيَدَيْنِ إِذَا اسْتَيْقَظَ مِنَ النَّوْمِ " },
{id: "4162  ", parent: "3963", text: "بَابُ جَوَازِ نَوْمِ الْجُنُبِ وَاسْتِحْبَابُِ الْوُضُوءِ لَهُ، وَغَسْلِ الْفَرْجِ إِذَا أَرَادَ أَنْ يَأْكُلَ أَوْ يَشْرَبَ أَوْ يَنَامَ أَوْ يُجَامِعَ " },
{id: "4163  ", parent: "3963", text: "باب من أتى أهله ثأراد أن يعود " },
{id: "4164  ", parent: "3963", text: "باب الطواف على النساء بغسل واحد " },
{id: "4165  ", parent: "3963", text: "بَابُ وُجُوبِ الْغُسْلِ عَلَى الْمَرْأَةِ بِخُرُوجِ الْمَنِيِّ مِنْهَا " },
{id: "4166  ", parent: "3963", text: "بَابُ بَيَانِ صِفَةِ مَنِيِّ الرَّجُلِ، وَالْمَرْأَةِ وَأَنَّ الْوَلَدَ مَخْلُوقٌ مِنْ مَائِهِمَا " },
{id: "4167  ", parent: "3963", text: "بَابُ صِفةِ غُسْلِ الْجَنَابَةِ " },
{id: "4168  ", parent: "3963", text: "بَابُ الْقَدْرِ الْمُسْتَحَبِّ مِنَ الْمَاءِ فِي غُسْلِ الْجَنَابَةِ، وَغُسْلِ الرَّجُلِ وَالْمَرْأَةِ فِي إِنَاءٍ وَاحِدٍ فِي حَالَةٍ وَاحِدَةٍ، وَغُسْلِ أَحَدِهَمَا بِفَضْلِ الْآخَرِ " },
{id: "4169  ", parent: "3963", text: "باب اغتسال الرجل وزوجته من إناء واحد " },
{id: "4170  ", parent: "3963", text: "باب الاغتسال بفضل المرأة " },
{id: "4171  ", parent: "3963", text: "باب قدر ماء الوضوء والغسل " },
{id: "4172  ", parent: "3963", text: "بَابُ اسْتحْبَابِ إِفَاضَةِ الْمَاءِ عَلَى الرَّأْسِ وَغَيْرِهِ ثَلَاثًا " },
{id: "4173  ", parent: "3963", text: "بَابُ حُكْمِ ضَفَائِرِ الْمُغْتَسِلَةِ " },
{id: "4174  ", parent: "3963", text: "بَابُ اسْتِحْبَابِ اسْتِعْمَالِ الْمُغْتَسِلَةِ مِنَ الْحَيْضِ فِرْصَةً مِنْ مِسْكٍ فِي مَوْضِعِ الدَّمِ " },
{id: "4175  ", parent: "3963", text: "بَابُ الْمُسْتَحَاضَةِ وَغَسْلِهَا وَصَلَاتِهَا " },
{id: "4176  ", parent: "3963", text: "بَابُ وُجُوبِ قَضَاءِ الصَّوْمِ عَلَى الْحَائِضِ دُونَ الصَّلَاةِ " },
{id: "4177  ", parent: "3963", text: "بَابُ تَسَتُّرِ الْمُغْتَسِلِ بِثوْبٍ وَنَحْوِهِ " },
{id: "4178  ", parent: "3963", text: "بَابُ تَحْرِيمِ النَّظَرِ إِلَى الْعَوْرَاتِ " },
{id: "4179  ", parent: "3963", text: "بَابُ جَوَازِ الِاغْتِسَالِ عُرْيَانًا فِي الْخَلْوَةِ " },
{id: "4180  ", parent: "3963", text: "بَابُ الَاعْتِنَاءِ بِحِفْظِ الْعَوْرَةِ " },
{id: "4181  ", parent: "3963", text: "بَابُ مَا يُسْتَتَرُ بِهِ لِقَضَاءِ الْحَاجَةِ " },
{id: "4182  ", parent: "3963", text: "بَابُ إِنَّمَا الْمَاءُ مِنَ الْمَاءِ " },
{id: "4183  ", parent: "3963", text: "بَابُ نَسْخِ الْمَاءُ مِنَ الْمَاءِ وَوُجُوبِ الْغُسْلِ بِالْتِقَاءِ الْخِتَانَيْنِ " },
{id: "4184  ", parent: "3963", text: "بَابُ الْوُضُوءِ مِمَّا مَسَّتِ النَّارُ " },
{id: "4185  ", parent: "3963", text: "بَابُ نسْخِ الْوُضُوءِ مِمَّا مَسَّتِ النَّارُ " },
{id: "4186  ", parent: "3963", text: "باب المضمضة من شرب اللبن " },
{id: "4187  ", parent: "3963", text: "بَابُ الْوُضُوءِ مِنْ لُحُومِ الْإِبِلِ " },
{id: "4188  ", parent: "3963", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ مَنْ تَيَقَّنَ الطَّهَارَةَ، ثُمَّ شَكَّ فِي الْحَدَثِ فَلَهُ أَنْ يُصَلِّيَ بِطَهَارَتِهِ تِلْكَ " },
{id: "4189  ", parent: "3963", text: "بَابُ طَهَارَةِ جُلُودِ الْمَيْتَةِ بِالدِّبَاغِ " },
{id: "4190  ", parent: "3963", text: "باب إذا دُبغ الإهاب فقد طهر " },
{id: "4191  ", parent: "3963", text: "بَابُ التَّيَمُّمِ " },
{id: "4192  ", parent: "3963", text: "باب التيمم في الحضر لرد السلام " },
{id: "4193  ", parent: "3963", text: "باب ترك رد السلام أثناء البول " },
{id: "4194  ", parent: "3963", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ الْمُسْلِمَ لَا يَنْجُسُ حَدَّثَنِي زُهَيْرُ بْنُ حَرْبٍ، حَدَّثَنَا يَحْيَى يَعْنِي ابْنَ سَعِيدٍ قَالَ: حُمَيْدٌ حَدَّثَنَا، ح وَحَدَّثَنَا أَبُو بَكْرِ بْنُ أَبِي شَيْبَةَ، - وَاللَّفْظُ لَهُ - حَدَّثَنَا إِسْمَاعِيلُ ابْنُ عُلَيَّةَ، عَنْ حُمَيْدٍ الطَّوِيلِ، قَالَ: حَدَّثَنَا بَكْرُ بْنُ عَبْدِ اللهِ، عَنْ أَبِي رَافِعٍ، عَنْ أَبِي هُرَيْرَةَ، أَنَّهُ لَقِيَهُ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِي طَرِيقٍ مِنْ طُرُقِ الْمَدِينَةِ، وَهُوَ جُنُبٌ فَانْسَلَّ فَذَهَبَ فَاغْتَسَلَ، فَتَفَقَّدَهُ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فَلَمَّا جَاءَهُ قَالَ: «أَيْنَ كُنْتَ يَا أَبَا هُرَيْرَةَ» قَالَ: يَا رَسُولَ اللهِ، لَقِيتَنِي وَأَنَا جُنُبٌ فَكَرِهْتُ أَنْ أُجَالِسَكَ حَتَّى أَغْتَسِلَ، فَقَالَ رَسُولُ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «سُبْحَانَ اللهِ إِنَّ الْمُؤْمِنَ لَا يَنْجُسُ» " },
{id: "4195  ", parent: "3963", text: "بَابُ ذِكْرِ اللهِ تَعَالَى فِي حَالِ الْجَنَابَةِ وَغَيْرِهَا " },
{id: "4196  ", parent: "3963", text: "بَابُ جَوَازِ أَكْلِ الْمُحْدِثِ الطَّعَامَ، وَأَنَّهُ لَا كَرَاهَةَ فِي ذَلِكَ، وَأَنَّ الْوُضُوءَ لَيْسَ عَلَى الْفَوْرِ " },
{id: "4197  ", parent: "3963", text: "بَابُ مَا يَقُولُ إِذَا أَرَادَ دُخُولَ الْخَلَاءِ " },
{id: "4198  ", parent: "3963", text: "بَابُ الدَّلِيلِ عَلَى أَنَّ نوْمَ الْجَالِسِ لَا يَنْقُضُ الْوُضُوءَ " },
{id: "3964", parent: "كِتَابُ الصَّلَاةِ ", text: "" },
{id: "4199  ", parent: "3964", text: "بَابُ بدْءِ الْأَذَانِ " },
{id: "4200  ", parent: "3964", text: "بَابُ الْأَمْرِ بِشَفْعِ الْأَذَانِ وَإِيتَارِ الْإِقَامَةِ " },
{id: "4201  ", parent: "3964", text: "بَابُ صِفَةِ الْأَذَانِ " },
{id: "4202  ", parent: "3964", text: "بَابُ اسْتحْبَابِ اتِّخاذِ مُؤذِّنَيْنِ لِلْمَسْجدِ الْوَاحِدِ " },
{id: "4203  ", parent: "3964", text: "بَابُ جَوَازِ أَذَانِ الْأَعْمَى إِذَا كَانَ مَعَهُ بَصِيرٌ " },
{id: "4204  ", parent: "3964", text: "بَابُ الْإِمْسَاكِ عَنِ الْإِغَارَةِ عَلَى قَوْمٍ فِي دَارِ الْكُفْرِ، إِذَا سُمِعَ فِيهِمُ الْأَذَانُ " },
{id: "4205  ", parent: "3964", text: "بَابُ الْقَوْلِ مِثْلَ قَوْلِ الْمُؤَذِّنِ لِمَنْ سَمِعَهُ، ثُمَّ يُصَلِّي عَلَى النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ ثُمَّ يَسْأَلُ لهُ الْوَسِيلَةَ " },
{id: "4206  ", parent: "3964", text: "بَابُ فَضْلِ الْأَذَانِ وَهَرَبِ الشَّيْطَانِ عِنْدَ سَمَاعِهِ " },
{id: "4207  ", parent: "3964", text: "باب إدبار الشيطان إذا سمع النداء وعودته للوسوسة " },
{id: "4208  ", parent: "3964", text: "بَابُ اسْتِحْبَابِ رَفْعِ الْيَدَيْنِ حَذْوَ الْمَنْكِبَيْنِ مَعَ تَكْبِيرَةِ الْإِحْرَامِ، وَالرُّكُوعِ، وَفِي الرَّفْعِ مِنَ الرُّكُوعِ، وَأَنَّهُ لَا يَفْعَلُهُ إِذَا رَفَعَ مِنَ السُّجُودِ " },
{id: "4209  ", parent: "3964", text: "بَابُ إِثْبَاتِ التَّكْبِيرِ فِي كُلِّ خَفْضٍ، وَرَفْعٍ فِي الصَّلَاةِ إِلَّا رَفْعَهُ مِنَ الرُّكُوعِ فَيَقُولُ: فِيهِ سَمِعَ اللهُ لِمَنْ حَمِدَهُ " },
{id: "4210  ", parent: "3964", text: "بَابُ وُجُوبِ قِرَاءَةِ الْفَاتِحَةِ فِي كُلِّ رَكْعَةٍ، وَإِنَّهُ إِذَا لَمْ يُحْسِنِ الْفَاتِحَةَ، وَلَا أَمْكَنَهُ تَعَلُّمُهَا قَرَأَ مَا تَيَسَّرَ لَهُ مِنْ غَيْرِهَا " },
{id: "4211  ", parent: "3964", text: "باب ما أسمعه رسول الله صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ من القراءة وما أخفاه " },
{id: "4212  ", parent: "3964", text: "باب اقْرَأْ مَا تَيَسَّرَ مَعَكَ مِنَ الْقُرْآنِ " },
{id: "4213  ", parent: "3964", text: "بَابُ نَهْيِ الْمَأْمُومِ عَنْ جَهْرِهِ بِالْقِرَاءَةِ خَلْفَ إِمَامِهِ " },
{id: "4214  ", parent: "3964", text: "بَابُ حُجَّةِ مَنْ قَالَ لَا يُجْهَرُ بِالْبَسْمَلَةِ " },
{id: "4215  ", parent: "3964", text: "بَابُ حُجَّةِ مَنْ قَالَ: الْبَسْمَلَةُ آيَةٌ مِنْ أَوَّلِ كُلِّ سُورَةٍ سِوَى بَرَاءَةٌ " },
{id: "4216  ", parent: "3964", text: "بَابُ وَضْعِ يَدِهِ الْيُمْنَى عَلَى الْيُسْرَى بَعْدَ تَكْبِيرَةِ الْإِحْرَامِ تَحْتَ صَدْرِهِ فَوْقَ سُرَّتِهِ، وَوَضْعِهِمَا فِي السُّجُودِ عَلَى الْأَرْضِ حَذْوَ مَنْكِبَيْهِ " },
{id: "4217  ", parent: "3964", text: "بَابُ التَّشهُّدِ فِي الصَّلَاةِ " },
{id: "4218  ", parent: "3964", text: "بَابُ الصَّلَاةِ عَلَى النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بعْدَ التَّشهُّدِ " },
{id: "4219  ", parent: "3964", text: "بَابُ التَّسْمِيعِ، وَالتَّحْمِيدِ، وَالتَّأْمِينِ " },
{id: "4220  ", parent: "3964", text: "باب فضل قول المأموم آمين -[307]- " },
{id: "4221  ", parent: "3964", text: "بَابُ ائْتِمَامِ الْمَأْمُومِ بِالْإِمَامِ " },
{id: "4222  ", parent: "3964", text: "بَابُ النَّهْيِ عَنْ مُبَادَرَةِ الْإِمَامِ بِالتَّكْبِيرِ وَغَيْرِهِ " },
{id: "4223  ", parent: "3964", text: "باب صلاة النبي صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ في مرضه وخلفه أبو بكر. " },
{id: "4224  ", parent: "3964", text: "بَابُ اسْتِخْلَافِ الْإِمَامِ إِذَا عَرَضَ لَهُ عُذْرٌ مِنْ مَرَضٍ وَسَفَرٍ، وَغَيْرِهِمَا مَنْ يُصلِّي بِالنَّاسِ، وَأَنَّ مَنْ صَلَّى خَلْفَ إِمَامٍ جَالِسٍ لِعَجْزِهِ عَنِ الْقِيَامِ لَزِمَهُ الْقِيَامُ إِذَا قَدِرَ عَلَيْهِ، وَنَسْخِ الْقُعُودِ خَلْفَ الْقَاعِدِ فِي حَقِّ مَنْ قَدَرَ عَلَى الْقِيَامِ " },
{id: "4225  ", parent: "3964", text: "بَابُ تَقْدِيمِ الْجَمَاعَةِ مَنْ يُصَلِّي بِهِمْ إِذَا تَأَخَّرَ الْإِمَامُ وَلَمْ يَخَافُوا مَفْسَدَةً بِالتَّقْدِيمِ " },
{id: "4226  ", parent: "3964", text: "بَابُ تَسْبِيحِ الرَّجُلِ وَتَصْفِيقِ الْمَرْأَةِ إِذَا نَابَهُمَا شَيْءٌ فِي الصَّلَاةِ " },
{id: "4227  ", parent: "3964", text: "بَابُ الْأَمْرِ بِتَحْسِينِ الصَّلَاةِ وَإِتْمَامِهَا وَالْخُشُوعِ فِيهَا " },
{id: "4228  ", parent: "3964", text: "بَابُ النَّهْي عَنْ سَبْقِ الْإِمَامِ بِرُكُوعٍ أَوْ سُجُودٍ وَنَحْوِهِمَا " },
{id: "4229  ", parent: "3964", text: "بَابُ النَّهْيِ عَنْ رَفْعِ الْبَصَرِ إِلَى السَّمَاءِ فِي الصَّلَاةِ " },
{id: "4230  ", parent: "3964", text: "بَابُ الْأَمْرِ بِالسُّكُونِ فِي الصَّلَاةِ، وَالنَّهْيِ عَنِ الْإِشَارَةِ بِالْيَدِ، وَرَفْعِهَا عِنْدَ السَّلَامِ، وَإتْمَامِ الصُّفُوفِ الْأُوَلِ وَالتَّرَاصِّ فِيهَا وَالْأَمْرِ بِالِاجْتِمَاعِ " },
{id: "4231  ", parent: "3964", text: "بَابُ تَسْوِيَةِ الصُّفُوفِ، وَإِقَامَتِهَا، وَفَضْلِ الْأَوَّلِ فَالْأَوَّلِ مِنْهَا، وَالَازْدِحَامِ عَلَى الصَّفِّ الْأَوَّلِ، وَالْمُسَابَقَةِ إِلَيْهَا، وَتَقْدِيمِ أُولِي الْفَضْلِ، وَتَقْرِيبِهِمْ مِنَ الْإِمَامِ " },
{id: "4232  ", parent: "3964", text: "باب فضل النداء والصف الأول والتكبير وصلاة العتمة والصبح " },
{id: "4233  ", parent: "3964", text: "باب جزاء الذين يتأخرون عن الصفوف الأُول " },
{id: "4234  ", parent: "3964", text: "باب خير الصفوف " },
{id: "4235  ", parent: "3964", text: "بَابُ أمْرِ النِّسَاءِ الْمُصَلِّيَاتِ، وَرَاءَ الرِّجَالِ أَنْ لَا يَرْفَعْنَ رُءُوسَهُنَّ مِنَ السُّجُودِ، حتَّى يَرْفَعَ الرِّجَالُ " },
{id: "4236  ", parent: "3964", text: "بَابُ خُروجِ النِّسَاءِ إِلَى الْمَسَاجِدِ إِذَا لَمْ يَتَرَتَّبْ عَلَيْهِ فِتْنةٌ، وَأَنَّهَا لَا تَخْرُجْ مُطَيَّبَةً " },
{id: "4237  ", parent: "3964", text: "باب إذا شَهِدت المرأة العشاء فلا تمسَّ طيبًا. " },
{id: "4238  ", parent: "3964", text: "باب منع نساء بني إسرائيل المسجد -[329]- " },
{id: "4239  ", parent: "3964", text: "بَابُ التَّوَسُّطِ فِي الْقِرَاءَةِ فِي الصَّلَاةِ الْجَهْرِيَّةِ بَيْنَ الْجَهْرِ وَالْإِسْرَارِ، إِذَا خَافَ مِنَ الْجَهْرِ مَفْسَدَةً " },
{id: "4240  ", parent: "3964", text: "بَابُ الِاسْتِمَاعِ لِلْقِرَاءَةِ " },
{id: "4241  ", parent: "3964", text: "بَابُ الْجَهْرِ بِالْقِرَاءَةِ فِي الصُّبْحِ وَالْقِرَاءَةِ عَلَى الْجِنِّ " },
{id: "4242  ", parent: "3964", text: "بَابُ الْقِرَاءَةِ فِي الظُّهْرِ وَالْعَصْرِ " },
{id: "4243  ", parent: "3964", text: "باب يطول في الركعتين الأوليين -[334]- " },
{id: "4244  ", parent: "3964", text: "بَابُ الْقِرَاءَةِ فِي الصُّبْحِ " },
{id: "4245  ", parent: "3964", text: "باب القراءة في المغرب " },
{id: "4246  ", parent: "3964", text: "بَابُ الْقِرَاءَةِ فِي الْعِشَاءِ " },
{id: "4247  ", parent: "3964", text: "بَابُ أَمْرِ الْأَئِمَّةِ بِتَخْفِيفِ الصَّلَاةِ فِي تَمَامٍ " },
{id: "4248  ", parent: "3964", text: "باب تخفيف الصلاة في تمام. " },
{id: "4249  ", parent: "3964", text: "باب تخيف الصلاة لبكاء الصبي " },
{id: "4250  ", parent: "3964", text: "بَابُ اعْتِدَالِ أَرْكَانِ الصَّلَاةِ وَتَخْفِيفِهَا فِي تَمَامٍ " },
{id: "4251  ", parent: "3964", text: "باب الطمئنينة في الصلاة " },
{id: "4252  ", parent: "3964", text: "بَابُ مُتَابَعَةِ الْإِمَامِ وَالْعَمَلِ بَعْدَهُ " },
{id: "4253  ", parent: "3964", text: "بَابُ مَا يَقُولُ إِذَا رَفَعَ رَأْسَهُ مِنَ الرُّكُوعِ " },
{id: "4254  ", parent: "3964", text: "بَابُ النَّهْيِ عَنْ قِرَاءَةِ الْقُرْآنِ فِي الرُّكُوعِ وَالسُّجُودِ " },
{id: "4255  ", parent: "3964", text: "بَابُ مَا يُقَالُ فِي الرُّكُوعِ وَالسُّجُودِ " },
{id: "4256  ", parent: "3964", text: "بَابُ فَضْلِ السُّجُودِ وَالْحَثِّ عَلَيْهِ " },
{id: "4257  ", parent: "3964", text: "بَابُ أَعْضَاءِ السُّجُودِ، وَالنَّهْيِ عَنْ كَفِّ الشَّعْرِ وَالثَّوْبِ وَعَقْصِ الرَّأْسِ فِي الصَّلَاةِ " },
{id: "4258  ", parent: "3964", text: "بَابُ الِاعْتِدَالِ فِي السُّجُودِ، وَوَضْعِ الْكَفَّيْنِ عَلَى الْأَرْضِ وَرَفْعِ الْمِرْفَقَيْنِ عَنِ الْجَنْبَيْنِ، وَرَفْعِ الْبَطْنِ عَنِ الْفَخْذَيْنِ فِي السُّجُودِ " },
{id: "4259  ", parent: "3964", text: "بَابُ مَا يَجْمَعُ صِفَةَ الصَّلَاةِ وَمَا يُفْتَتَحُ بِهِ وَيُخْتَمُ بِهِ، وَصِفَةَ الرُّكُوعِ وَالِاعْتِدَالِ مِنْهُ، وَالسُّجُودِ وَالِاعْتِدَالِ مِنْهُ، وَالتَّشَهُّدِ بَعْدَ كُلِّ رَكْعَتَيْنِ مِنَ الرُّبَاعِيَّةِ، وَصِفَةَ الْجُلُوسِ بَيْنَ السَّجْدَتَيْنِ، وَفِي التَّشَهُّدِ الْأَوَّلِ " },
{id: "4260  ", parent: "3964", text: "بَابُ سُتْرَةِ الْمُصَلِّي " },
{id: "4261  ", parent: "3964", text: "باب الصلاة إلى الحربة والعَنَزَة " },
{id: "4262  ", parent: "3964", text: "باب الصلاة إلى الراحلة " },
{id: "4263  ", parent: "3964", text: "باب مرور الحمار والكلب -[360]-. " },
{id: "4264  ", parent: "3964", text: "بَابُ مَنْعِ المَارِّ بَيْنَ يَدَيِ الْمُصَلِّي " },
{id: "4265  ", parent: "3964", text: "باب إثم المار بين يدي الْمُصَلِّي " },
{id: "4266  ", parent: "3964", text: "بَابُ دُنُوِّ الْمُصَلِّي مِنَ السُّتْرَةِ " },
{id: "4267  ", parent: "3964", text: "بَابُ قَدَرِ مَا يَسْتُرُ الْمُصَلِّي " },
{id: "4268  ", parent: "3964", text: "بَابُ الِاعْتِرَاضِ بَيْنَ يَدَيِ الْمُصَلِّي " },
{id: "4269  ", parent: "3964", text: "بَابُ الصَّلَاةِ فِي ثَوْبٍ وَاحِدٍ وَصِفَةِ لُبْسِهِ " },
{id: "3965", parent: "كِتَابُ الْمَسَاجِدِ وَمَوَاضِعِ الصَّلَاةَ", text: "" },
{id: "4270  ", parent: "3965", text: "كِتَابُ الْمَسَاجِدِ وَمَوَاضِعِ الصَّلَاةَ " },
{id: "4271  ", parent: "3965", text: "باب جُعلت لي الأرض مسجدًا وطهورًا " },
{id: "4272  ", parent: "3965", text: "بَابُ -[373]- ابْتِنَاءِ مَسْجِدِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4273  ", parent: "3965", text: "بَابُ تَحْوِيلِ الْقِبْلَةِ مِنَ الْقُدْسِ إِلَى الْكَعْبَةِ " },
{id: "4274  ", parent: "3965", text: "بَابُ النَّهْيِ عَنْ بِنَاءِ الْمَسَاجِدِ، عَلَى الْقُبُورِ وَاتِّخَاذِ الصُّوَرِ فِيهَا وَالنَّهْيِ عَنِ اتِّخَاذِ الْقُبُورِ مَسَاجِدَ " },
{id: "4275  ", parent: "3965", text: "بَابُ فَضْلِ بِنَاءِ الْمَسَاجِدِ وَالْحَثِّ عَلَيْهَا " },
{id: "4276  ", parent: "3965", text: "بَابُ النَّدْبِ إِلَى وَضْعِ الْأَيْدِي عَلَى الرُّكَبِ فِي الرُّكُوعِ وَنَسْخِ التَّطْبِيقِ " },
{id: "4277  ", parent: "3965", text: "بَابُ جَوَازِ الْإِقْعَاءِ عَلَى الْعَقِبَيْنِ " },
{id: "4278  ", parent: "3965", text: "بَابُ تَحْرِيمِ الْكَلَامِ فِي الصَّلَاةِ، وَنَسْخِ مَا كَانَ مِنْ إِبَاحَتِهِ " },
{id: "4279  ", parent: "3965", text: "بَابُ جَوَازِ لَعْنِ الشَّيْطَانِ فِي أَثْنَاءِ الصَّلَاةِ، وَالتَّعَوُّذِ مِنْهُ وَجَوَازِ الْعَمَلِ الْقَلِيلِ فِي الصَّلَاةِ " },
{id: "4280  ", parent: "3965", text: "بَابُ جَوَازِ حَمْلَ الصِّبْيَانِ فِي الصَّلَاةِ " },
{id: "4281  ", parent: "3965", text: "بَابُ جَوَازِ الْخُطْوَةِ وَالْخُطْوَتَيْنِ فِي الصَّلَاةِ " },
{id: "4282  ", parent: "3965", text: "بَابُ كَرَاهَةِ الْاخْتِصَارِ فِي الصَّلَاةِ " },
{id: "4283  ", parent: "3965", text: "بَابُ كَرَاهَةِ مَسْحِ الْحَصَى وَتَسْوِيَةِ التُّرَابِ فِي الصَّلَاةِ " },
{id: "4284  ", parent: "3965", text: "بَابُ النَّهْيِ عَنِ الْبُصَاقِ فِي الْمَسْجِدِ فِي الصَّلَاةِ وَغَيْرِهَا " },
{id: "4285  ", parent: "3965", text: "باب البزاق في المسجد خطيئة " },
{id: "4286  ", parent: "3965", text: "بَابُ جَوَازِ الصَّلَاةِ فِي النَّعْلَيْنِ " },
{id: "4287  ", parent: "3965", text: "بَابُ كَرَاهَةِ الصَّلَاةِ فِي ثَوْبٍ لَهُ أَعْلَامٌ " },
{id: "4288  ", parent: "3965", text: "بَابُ كَرَاهَةِ الصَّلَاةِ بِحَضْرَةِ الطَّعَامَ الَّذِي يُرِيدُ أَكْلَهُ فِي الْحَالِ وَكَرَاهَةِ الصَّلَاةِ مَعَ مُدَافَعَةِ الْأَخْبَثَيْنِ " },
{id: "4289  ", parent: "3965", text: "باب لا صلاة بحضرة طعام ولا وهو يدافعه الأَخبثان " },
{id: "4290  ", parent: "3965", text: "بَابُ نَهْيِ مَنْ أَكَلِ ثُومًا أَوْ بَصَلًا أَوْ كُرَّاثًا أَوْ نَحْوَهَا " },
{id: "4291  ", parent: "3965", text: "بَابُ النَّهْيِ عَنْ نَشْدِ الضَّالَّةِ فِي الْمَسْجِدِ وَمَا يَقُولُهُ مَنْ سَمِعَ النَّاشِدَ " },
{id: "4292  ", parent: "3965", text: "بَابُ السَّهْوِ فِي الصَّلَاةِ وَالسُّجُودِ لَهُ " },
{id: "4293  ", parent: "3965", text: "بَابُ سُجُودٍ التِّلَاوَةِ " },
{id: "4294  ", parent: "3965", text: "بَابُ صِفَةِ الْجُلُوسِ فِي الصَّلَاةِ، وَكَيْفِيَّةِ وَضْعِ الْيَدَيْنِ عَلَى الْفَخْذَيْنِ " },
{id: "4295  ", parent: "3965", text: "بَابُ السَّلَامِ لِلتَّحْلِيلِ مِنَ الصَّلَاةِ عِنْدَ فَرَاغِهَا وَكَيْفِيَّتِهِ " },
{id: "4296  ", parent: "3965", text: "بَابُ الذِّكْرِ بَعْدَ الصَّلَاةِ " },
{id: "4297  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ التَّعَوُّذِ مِنْ عَذَابِ الْقَبْرِ " },
{id: "4298  ", parent: "3965", text: "بَابُ مَا يُسْتَعَاذُ مِنْهُ فِي الصَّلَاةِ " },
{id: "4299  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ الذِّكْرِ بَعْدَ الصَّلَاةِ وَبَيَانِ صِفَتِهِ " },
{id: "4300  ", parent: "3965", text: "بَابُ مَا يُقَالُ بَيْنَ تَكْبِيرَةِ الْإِحْرَامِ وَالْقِرَاءَةِ " },
{id: "4301  ", parent: "3965", text: "باب إذا نهض من الركعة الثانية " },
{id: "4302  ", parent: "3965", text: "باب فضل قول الحمد لله حمدًا كثيرًا طيبًا " },
{id: "4303  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ إِتْيَانِ الصَّلَاةِ بِوَقَارٍ وَسَكِينَةٍ، وَالنَّهْيِ عَنْ إِتْيَانِهَا سَعْيًا " },
{id: "4304  ", parent: "3965", text: "بَابُ مَتَى يَقُومُ النَّاسُ لِلصَّلَاةِ " },
{id: "4305  ", parent: "3965", text: "بَابُ مَنْ أَدْرَكَ رَكْعَةً مِنَ الصَّلَاةِ فَقَدْ أَدْرَكَ تِلْكَ الصَّلَاةِ " },
{id: "4306  ", parent: "3965", text: "باب من أدرك ركعة من الصبح " },
{id: "4307  ", parent: "3965", text: "بَابُ أَوْقَاتِ الصَّلَوَاتِ الْخَمْسِ " },
{id: "4308  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ الْإِبْرَادِ بِالظُّهْرِ فِي شِدَّةِ الْحَرِّ لِمَنْ يَمْضِي إِلَى جَمَاعَةٍ، وَيَنَالُهُ الْحَرُّ فِي طَرِيقِهِ " },
{id: "4309  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ تَقْدِيمِ الظُّهْرِ فِي أَوَّلِ الْوَقْتِ فِي غَيْرِ شِدَّةِ الْحَرِّ " },
{id: "4310  ", parent: "3965", text: "باب الشكوى من حر الرمضاء -[433]- " },
{id: "4311  ", parent: "3965", text: "باب السجود على الثوب في شدة الحر " },
{id: "4312  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ التَّبْكِيرِ بِالْعَصْرِ " },
{id: "4313  ", parent: "3965", text: "بَابُ التَّغْلِيظِ فِي تَفْوِيتِ صَلَاةِ الْعَصْرِ " },
{id: "4314  ", parent: "3965", text: "باب الصلاة الوسطى " },
{id: "4315  ", parent: "3965", text: "بَابُ الدَّلِيلِ لِمَنْ قَالَ الصَّلَاةُ الْوُسْطَى هِيَ صَلَاةُ الْعَصْرِ " },
{id: "4316  ", parent: "3965", text: "بَابُ فَضْلِ صَلَاتَيِ الصُّبْحِ وَالْعَصْرِ، وَالْمُحَافَظَةِ عَلَيْهِمَا " },
{id: "4317  ", parent: "3965", text: "بَابُ بَيَانِ أَنَّ أَوَّلَ وَقْتِ الْمَغْرِبِ عِنْدَ غُرُوبِ الشَّمْسِ " },
{id: "4318  ", parent: "3965", text: "بَابُ وَقْتِ الْعِشَاءِ وَتَأْخِيرِهَا " },
{id: "4319  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ التَّبْكِيرِ بِالصُّبْحِ فِي أَوَّلِ وَقْتِهَا، وَهُوَ التَّغْلِيسُ، وَبَيَانِ قَدْرَ الْقِرَاءَةِ فِيهَا " },
{id: "4320  ", parent: "3965", text: "بَابُ كَرَاهِيَةِ تَأْخِيرِ الصَّلَاةِ عَنْ وَقْتِهَا الْمُخْتَارِ، وَمَا يَفْعَلُهُ الْمَأْمُومُ إِذَا أَخَّرَهَا الْإِمَامِ " },
{id: "4321  ", parent: "3965", text: "بَابُ فَضْلِ صَلَاةِ الْجَمَاعَةِ، وَبَيَانِ التَّشْدِيدِ فِي التَّخَلُّفِ عَنْهَا " },
{id: "4322  ", parent: "3965", text: "باب الذين يتخلفون عن صلاة الجماعة والجمعة " },
{id: "4323  ", parent: "3965", text: "بَابُ يَجِبُ إِتْيَانِ الْمَسْجِدِ عَلَى مَنْ سَمِعَ النِّدَاءَ " },
{id: "4324  ", parent: "3965", text: "بَابُ صَلَاةِ الْجَمَاعَةِ مِنْ سُنَنِ الْهُدَى " },
{id: "4325  ", parent: "3965", text: "بَابُ النَّهْيِ عَنِ الْخُرُوجِ مِنَ الْمَسْجِدِ إِذَا أَذَّنَ الْمُؤَذِّنُ " },
{id: "4326  ", parent: "3965", text: "بَابُ فَضْلِ صَلَاةِ الْعِشَاءِ وَالصُّبْحِ فِي جَمَاعَةٍ " },
{id: "4327  ", parent: "3965", text: "بَابُ الرُّخْصَةِ فِي التَّخَلُّفِ عَنِ الْجَمَاعَةِ بِعُذْرٍ " },
{id: "4328  ", parent: "3965", text: "بَابُ جَوَازِ الْجَمَاعَةِ فِي النَّافِلَةِ، وَالصَّلَاةِ عَلَى حَصِيرٍ وَخُمْرَةٍ وَثَوْبٍ، وَغَيْرِهَا مِنَ الطَّاهِرَاتِ " },
{id: "4329  ", parent: "3965", text: "بَابُ فَضْلِ صَلَاةِ الْجَمَاعَةِ وَانْتِظَارِ الصَّلَاةِ " },
{id: "4330  ", parent: "3965", text: "بَابُ فَضْلِ كَثْرَةِ الْخُطَا إِلَى الْمَسَاجِدِ " },
{id: "4331  ", parent: "3965", text: "بَابُ الْمَشْيِ إِلَى الصَّلَاةِ تُمْحَى بِهِ الْخَطَايَا، وَتُرْفَعُ بِهِ الدَّرَجَاتُ " },
{id: "4332  ", parent: "3965", text: "باب فضل الصلوات الخمس " },
{id: "4333  ", parent: "3965", text: "باب من غدا إلى المسجد أو راح. " },
{id: "4334  ", parent: "3965", text: "بَابُ فَضْلِ الْجُلُوسِ فِي مُصَلَّاهُ بَعْدَ الصُّبْحِ، وَفَضْلِ الْمَسَاجِدِ " },
{id: "4335  ", parent: "3965", text: "باب أحب البلاد إلى الله مساجدها " },
{id: "4336  ", parent: "3965", text: "بَابُ مَنْ أَحَقُّ بِالْإِمَامَةِ " },
{id: "4337  ", parent: "3965", text: "بَابُ اسْتِحْبَابِ الْقُنُوتِ فِي جَمِيعِ الصَّلَاةِ إِذَا نَزَلَتْ بِالْمُسْلِمِينَ نَازِلَةٌ " },
{id: "4338  ", parent: "3965", text: "بَابُ قَضَاءِ الصَّلَاةِ الْفَائِتَةِ، وَاسْتِحْبَابِ تَعْجِيلِ قَضَائِهَا " },
{id: "3966", parent: "كِتَابُ صَلَاةِ الْمُسَافِرِينَ وَقَصْرِهَا ", text: "" },
{id: "4339  ", parent: "3966", text: "بَابُ صَلَاةِ الْمُسَافِرِينَ وَقَصْرِهَا " },
{id: "4340  ", parent: "3966", text: "باب إذا صلى المسافر خلف المقيم " },
{id: "4341  ", parent: "3966", text: "باب يقصر إذا خرج من موضعه " },
{id: "4342  ", parent: "3966", text: "باب المسافة التي يقصر فيها الصلاة -[481]- " },
{id: "4343  ", parent: "3966", text: "باب مدة القصر " },
{id: "4344  ", parent: "3966", text: "بَابُ قَصْرِ الصَّلَاةِ بِمِنًى " },
{id: "4345  ", parent: "3966", text: "بَابُ الصَّلَاةِ فِي الرِّحَالِ فِي الْمَطَرِ " },
{id: "4346  ", parent: "3966", text: "بَابُ جَوَازِ صَلَاةِ النَّافِلَةِ عَلَى الدَّابَّةِ فِي السَّفَرِ حَيْثُ تَوَجَّهَتْ " },
{id: "4347  ", parent: "3966", text: "بَابُ جَوَازِ الْجَمْعِ بَيْنَ الصَّلَاتَيْنِ فِي السَّفَرِ " },
{id: "4348  ", parent: "3966", text: "بَابُ الْجَمْعِ بَيْنَ الصَّلَاتَيْنِ فِي الْحَضَرِ " },
{id: "4349  ", parent: "3966", text: "بَابُ جَوَازِ الِانْصِرَافِ مِنَ الصَّلَاةِ عَنِ الْيَمِينِ، وَالشِّمَالِ " },
{id: "4350  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ يَمِينِ الْإِمَامِ " },
{id: "4351  ", parent: "3966", text: "بَابُ كَرَاهَةِ الشُّرُوعِ فِي نَافِلَةٍ بَعْدَ شُرُوعِ الْمُؤَذِّنِ " },
{id: "4352  ", parent: "3966", text: "بَابُ مَا يَقُولُ إِذَا دَخَلَ الْمَسْجِدَ " },
{id: "4353  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ تَحِيَّةِ الْمَسْجِدِ بِرَكْعَتَيْنِ، وَكَرَاهَةِ الْجُلُوسِ قَبْلَ صَلَاتِهِمَا، وَأَنَّهَا مَشْرُوعَةٌ فِي جَمِيعِ الْأَوْقَاتِ " },
{id: "4354  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ الرَّكْعَتَيْنِ فِي الْمَسْجِدِ لِمَنْ قَدِمَ مِنْ سَفَرٍ أَوَّلَ قُدُومِهِ " },
{id: "4355  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ صَلَاةِ الضُّحَى، وَأَنَّ أَقَلَّهَا رَكْعَتَانِ، وَأَكْمَلَهَا ثَمَانِ رَكَعَاتٍ، وَأَوْسَطُهَا أَرْبَعُ رَكَعَاتٍ، أَوْ سِتٍّ، وَالْحَثُّ عَلَى الْمُحَافَظَةِ عَلَيْهَا " },
{id: "4356  ", parent: "3966", text: "باب عدد ركعات الضحى " },
{id: "4357  ", parent: "3966", text: "باب فضل صلاة الصبح " },
{id: "4358  ", parent: "3966", text: "باب الوصية بصلاة الصبح -[499]- " },
{id: "4359  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ رَكْعَتَيْ سُنَّةِ الْفَجْرِ، وَالْحَثِّ عَلَيْهِمَا وَتَخْفِيفِهِمَا، وَالْمُحَافَظَةِ عَلَيْهِمَا، وَبَيَانِ مَا يُسْتَحَبُّ أَنْ يُقْرَأَ فِيهِمَا " },
{id: "4360  ", parent: "3966", text: "باب تعاهد ركعتي الفجر " },
{id: "4361  ", parent: "3966", text: "باب فضل ركعتي الفجر " },
{id: "4362  ", parent: "3966", text: "بَابُ فَضْلِ السُّنَنِ الرَّاتِبَةِ قَبْلَ الْفَرَائِضِ وَبَعْدَهُنَّ، وَبَيَانِ عَدَدِهِنَّ " },
{id: "4363  ", parent: "3966", text: "بَابُ جَوَازِ النَّافِلَةِ قَائِمًا وَقَاعِدًا، وَفِعْلِ بَعْضِ الرَّكْعَةِ قَائِمًا وَبَعْضِهَا قَاعِدًا " },
{id: "4364  ", parent: "3966", text: "بَابُ صَلَاةِ اللَّيْلِ، وَعَدَدِ رَكَعَاتِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِي اللَّيْلِ، وَأَنَّ الْوِتْرَ رَكْعَةٌ، وَأَنَّ الرَّكْعَةَ صَلَاةٌ صَحِيحَةٌ " },
{id: "4365  ", parent: "3966", text: "بَابُ جَامِعِ صَلَاةِ اللَّيْلِ، وَمَنْ نَامَ عَنْهُ أَوْ مَرِضَ " },
{id: "4366  ", parent: "3966", text: "بَابُ صَلَاةِ الْأَوَّابِينَ حِينَ تَرْمَضُ الْفِصَالُ " },
{id: "4367  ", parent: "3966", text: "بَابُ صَلَاةُ اللَّيْلِ مَثْنَى مَثْنَى، وَالْوِتْرُ رَكْعَةٌ مِنْ آخِرِ اللَّيْلِ " },
{id: "4368  ", parent: "3966", text: "بَابُ مَنْ خَافَ أَنْ لَا يَقُومَ مِنْ آخِرِ اللَّيْلِ فَلْيُوتِرْ أَوَّلَهُ " },
{id: "4369  ", parent: "3966", text: "بَابُ أَفْضَلُ الصَّلَاةِ طُولُ الْقُنُوتِ " },
{id: "4370  ", parent: "3966", text: "بَابُ فِي اللَّيْلِ سَاعَةٌ مُسْتَجَابٌ فِيهَا الدُّعَاءُ " },
{id: "4371  ", parent: "3966", text: "بَابُ التَّرْغِيبِ فِي الدُّعَاءِ وَالذِّكْرِ فِي آخِرِ اللَّيْلِ، وَالْإِجَابَةِ فِيهِ " },
{id: "4372  ", parent: "3966", text: "بَابُ التَّرْغِيبِ فِي قِيَامِ رَمَضَانَ، وَهُوَ التَّرَاوِيحُ " },
{id: "4373  ", parent: "3966", text: "بَابُ الدُّعَاءِ فِي صَلَاةِ اللَّيْلِ وَقِيَامِهِ " },
{id: "4374  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ تَطْوِيلِ الْقِرَاءَةِ فِي صَلَاةِ اللَّيْلِ " },
{id: "4375  ", parent: "3966", text: "بَابُ مَا رُوِيَ فِيمَنْ نَامَ اللَّيْلَ أَجَمْعَ حَتَّى أَصَبْحَ " },
{id: "4376  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ صَلَاةِ النَّافِلَةِ فِي بَيْتِهِ، وَجَوَازِهَا فِي الْمَسْجِدِ " },
{id: "4377  ", parent: "3966", text: "بَابُ فَضِيلَةِ الْعَمَلِ الدَّائِمِ مِنْ قِيَامِ اللَّيْلِ وَغَيْرِهِ " },
{id: "4378  ", parent: "3966", text: "بَابُ أَمْرِ مَنْ نَعَسَ فِي صَلَاتِهِ، أَوِ اسْتَعْجَمَ عَلَيْهِ الْقُرْآنُ، أَوِ الذِّكْرُ بِأَنْ يَرْقُدَ، أَوْ يَقْعُدَ حَتَّى يَذْهَبَ عَنْهُ ذَلِكَ " },
{id: "4379  ", parent: "3966", text: "باب فضائل القرآن وما يتعلق به " },
{id: "4380  ", parent: "3966", text: "بَابُ الْأَمْرِ بِتَعَهُّدِ الْقُرْآنِ، وَكَرَاهَةِ قَوْلِ نَسِيتُ آيَةَ كَذَا، وَجَوَازِ قَوْلِ أُنْسِيتُهَا " },
{id: "4381  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ تَحْسِينِ الصَّوْتِ بِالْقُرْآنِ " },
{id: "4382  ", parent: "3966", text: "بَابُ ذِكْرِ قِرَاءَةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ سُورَةَ الْفَتْحِ يَوْمَ فَتْحِ مَكَّةَ " },
{id: "4383  ", parent: "3966", text: "بَابُ نُزُولِ السَّكِينَةِ لِقِرَاءَةِ الْقُرْآنِ " },
{id: "4384  ", parent: "3966", text: "بَابُ فَضِيلَةِ حَافِظِ الْقُرْآنِ " },
{id: "4385  ", parent: "3966", text: "بَابُ فَضْلِ الْمَاهِرِ فِي الْقُرْآنِ، وَالَّذِي يَتَتَعْتَعُ فِيهِ " },
{id: "4386  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ قِرَاءَةِ الْقُرْآنِ عَلَى أَهْلِ الْفَضْلِ، وَالْحُذَّاقِ فِيهِ، وَإِنْ كَانَ الْقَارِئُ أَفْضَلَ مِنَ الْمَقْرُوءِ عَلَيْهِ " },
{id: "4387  ", parent: "3966", text: "بَابُ فَضْلِ اسْتِمَاعِ الْقُرْآنِ، وَطَلَبِ الْقِرَاءَةِ مِنْ حَافِظِهِ لِلِاسْتِمَاعِ وَالْبُكَاءِ عِنْدَ الْقِرَاءَةِ وَالتَّدَبُّرِ " },
{id: "4388  ", parent: "3966", text: "بَابُ فَضْلِ قِرَاءَةِ الْقُرْآنِ فِي الصَّلَاةِ وَتَعَلُّمِهِ " },
{id: "4389  ", parent: "3966", text: "بَابُ فَضْلِ قِرَاءَةِ الْقُرْآنِ، وَسُورَةِ الْبَقَرَةِ " },
{id: "4390  ", parent: "3966", text: "بَابُ فَضْلِ الْفَاتِحَةِ، وَخَوَاتِيمِ سُورَةِ الْبَقَرَةِ، وَالْحَثِّ عَلَى قِرَاءَةِ الْآيَتَيْنِ مِنْ آخِرِ الْبَقَرَةِ " },
{id: "4391  ", parent: "3966", text: "بَابُ فَضْلِ سُورَةِ الْكَهْفِ، وَآيَةِ الْكُرْسِيِّ " },
{id: "4392  ", parent: "3966", text: "بَابُ فَضْلِ قِرَاءَةِ قُلْ هُوَ اللهُ أَحَدٌ " },
{id: "4393  ", parent: "3966", text: "بَابُ فَضْلِ قِرَاءَةِ الْمُعَوِّذَتَيْنِ " },
{id: "4394  ", parent: "3966", text: "بَابُ فَضْلِ مَنْ يَقُومُ بِالْقُرْآنِ، وَيُعَلِّمُهُ، وَفَضْلِ مَنْ تَعَلَّمَ حِكْمَةً مِنْ فِقْهٍ، أَوْ غَيْرِهِ فَعَمِلَ بِهَا وَعَلَّمَهَا " },
{id: "4395  ", parent: "3966", text: "بَابُ بَيَانِ أَنَّ الْقُرْآنَ عَلَى سَبْعَةِ أَحْرُفٍ وَبَيَانِ مَعْنَاهُ " },
{id: "4396  ", parent: "3966", text: "بَابُ تَرْتِيلِ الْقِرَاءَةِ، وَاجْتِنَابِ الْهَذِّ، وَهُوَ الْإِفْرَاطُ فِي السُّرْعَةِ، وَإِبَاحَةِ سُورَتَيْنِ فَأَكْثَرَ فِي رَكْعَةٍ " },
{id: "4397  ", parent: "3966", text: "بَابُ مَا يَتَعَلَّقُ بِالْقِرَاءَاتِ " },
{id: "4398  ", parent: "3966", text: "بَابُ الْأَوْقَاتِ الَّتِي نُهِيَ عَنِ الصَّلَاةِ فِيهَا " },
{id: "4399  ", parent: "3966", text: "بَابُ إِسْلَامِ عَمْرِو بْنِ عَبَسَةَ " },
{id: "4400  ", parent: "3966", text: "بَابُ لَا تَتَحَرَّوْا بِصَلَاتِكُمْ طُلُوعَ الشَّمْسِ وَلَا غُرُوبَهَا " },
{id: "4401  ", parent: "3966", text: "بَابُ مَعْرِفَةِ الرَّكْعَتَيْنِ اللَّتَيْنِ كَانَ يُصَلِّيهِمَا النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بَعْدَ الْعَصْرِ " },
{id: "4402  ", parent: "3966", text: "بَابُ اسْتِحْبَابِ رَكْعَتَيْنِ قَبْلَ صَلَاةِ الْمَغْرِبِ " },
{id: "4403  ", parent: "3966", text: "بَابُ بَيْنَ كُلِّ أَذَانَيْنِ صَلَاةٌ " },
{id: "4404  ", parent: "3966", text: "بَابُ صَلَاةِ الْخَوْفِ " },
{id: "3967", parent: "كِتَابُ الْجُمُعَةِ", text: "" },
{id: "4405  ", parent: "3967", text: "كِتَابُ الْجُمُعَةِ " },
{id: "4406  ", parent: "3967", text: "بَابُ وُجُوبِ غُسْلِ الْجُمُعَةِ عَلَى كُلِّ بَالِغٍ مِنَ الرِّجَالِ، وَبَيَانِ مَا أُمِرُوا بِهِ " },
{id: "4407  ", parent: "3967", text: "بَابُ الطِّيبِ وَالسِّوَاكِ يَوْمَ الْجُمُعَةِ " },
{id: "4408  ", parent: "3967", text: "بَابٌ فِي الْإِنْصَاتِ يَوْمَ الْجُمُعَةِ فِي الْخُطْبَةِ " },
{id: "4409  ", parent: "3967", text: "بَابٌ فِي السَّاعَةِ الَّتِي فِي يَوْمِ الْجُمُعَةِ " },
{id: "4410  ", parent: "3967", text: "بَابُ فَضْلِ يَوْمِ الْجُمُعَةِ " },
{id: "4411  ", parent: "3967", text: "بَابُ هِدَايَةِ هَذِهِ الْأُمَّةِ لِيَوْمِ الْجُمُعَةِ " },
{id: "4412  ", parent: "3967", text: "بَابُ فَضْلِ التَّهْجِيرِ يَوْمَ الْجُمُعَةِ " },
{id: "4413  ", parent: "3967", text: "بَابُ فَضْلِ مَنِ اسْتَمَعَ وَأَنْصَتَ فِي الْخُطْبَةِ " },
{id: "4414  ", parent: "3967", text: "بَابُ صَلَاةِ الْجُمُعَةِ حِينَ تَزُولُ الشَّمْسُ " },
{id: "4415  ", parent: "3967", text: "بَابُ ذِكْرِ الْخُطْبَتَيْنِ قَبْلَ الصَّلَاةِ وَمَا فِيهِمَا مِنَ الْجَلْسَةِ " },
{id: "4416  ", parent: "3967", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْوًا انْفَضُّوا إِلَيْهَا وَتَرَكُوكَ قَائِمًا} [الجمعة: 11] " },
{id: "4417  ", parent: "3967", text: "بَابُ التَّغْلِيظِ فِي تَرْكِ الْجُمُعَةِ " },
{id: "4418  ", parent: "3967", text: "بَابُ تَخْفِيفِ الصَّلَاةِ وَالْخُطْبَةِ " },
{id: "4419  ", parent: "3967", text: "بَابُ التَّحِيَّةُ وَالْإِمَامُ يَخْطُبُ " },
{id: "4420  ", parent: "3967", text: "بَابُ حَدِيثِ التَّعْلِيمِ فِي الْخُطْبَةِ " },
{id: "4421  ", parent: "3967", text: "بَابُ مَا يُقْرَأُ فِي صَلَاةِ الْجُمُعَةِ " },
{id: "4422  ", parent: "3967", text: "بَابُ مَا يُقْرَأُ فِي يَوْمِ الْجُمُعَةِ " },
{id: "4423  ", parent: "3967", text: "بَابُ الصَّلَاةِ بَعْدَ الْجُمُعَةِ " },
{id: "3968", parent: "كِتَابُ صَلَاةِ الْعِيدَيْنِ", text: "" },
{id: "4424  ", parent: "3968", text: "كِتَابُ صَلَاةِ الْعِيدَيْنِ " },
{id: "4425  ", parent: "3968", text: "بَابُ ذِكْرِ إِبَاحَةِ خُرُوجِ النِّسَاءِ فِي الْعِيدَيْنِ إِلَى الْمُصَلَّى وَشُهُودِ الْخُطْبَةِ، مُفَارِقَاتٌ لِلرِّجَالِ " },
{id: "4426  ", parent: "3968", text: "بَابُ تَرْكِ الصَّلَاةِ قَبْلَ الْعِيدِ وَبَعْدَهَا فِي الْمُصَلَّى " },
{id: "4427  ", parent: "3968", text: "بَابُ مَا يُقْرَأُ بِهِ فِي صَلَاةِ الْعِيدَيْنِ " },
{id: "4428  ", parent: "3968", text: "بَابُ الرُّخْصَةِ فِي اللَّعِبِ الَّذِي لَا مَعْصِيَةَ فِيهِ فِي أَيَّامِ الْعِيدِ " },
{id: "3969", parent: "كِتَابُ صَلَاةِ الِاسْتِسْقَاءِ", text: "" },
{id: "4429  ", parent: "3969", text: "كِتَابُ صَلَاةِ الِاسْتِسْقَاءِ " },
{id: "4430  ", parent: "3969", text: "بَابُ رَفْعِ الْيَدَيْنِ بِالدُّعَاءِ فِي الِاسْتِسْقَاءِ " },
{id: "4431  ", parent: "3969", text: "بَابُ الدُّعَاءِ فِي الِاسْتِسْقَاءِ " },
{id: "4432  ", parent: "3969", text: "بَابُ التَّعَوُّذِ عِنْدَ رُؤْيَةِ الرِّيحِ وَالْغَيْمِ، وَالْفَرَحِ بِالْمَطَرِ " },
{id: "4433  ", parent: "3969", text: "بَابٌ فِي رِيحِ الصَّبَا وَالدَّبُورِ " },
{id: "3970", parent: "كِتَابُ الْكُسُوفِ ", text: "" },
{id: "4434  ", parent: "3970", text: "بَابُ صَلَاةِ الْكُسُوفِ " },
{id: "4435  ", parent: "3970", text: "بَابُ ذِكْرِ عَذَابِ الْقَبْرِ فِي صَلَاةِ الْخُسُوفِ " },
{id: "4436  ", parent: "3970", text: "بَابُ مَا عُرِضَ عَلَى النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِي صَلَاةِ الْكُسُوفِ مِنْ أَمْرِ الْجَنَّةِ وَالنَّارِ " },
{id: "4437  ", parent: "3970", text: "بَابُ ذِكْرِ مَنْ قَالَ: إِنَّهُ رَكَعَ ثَمَانِ رَكَعَاتٍ فِي أَرْبَعِ سَجَدَاتٍ " },
{id: "4438  ", parent: "3970", text: "بَابُ ذِكْرِ النِّدَاءِ بِصَلَاةِ الْكُسُوفِ الصَّلَاةُ جَامِعَةٌ " },
{id: "3971", parent: "كِتَابُ الْجَنَائِزِ ", text: "" },
{id: "4439  ", parent: "3971", text: "بَابُ تَلْقِينِ الْمَوْتَى لَا إِلَهَ إِلَّا اللهُ " },
{id: "4440  ", parent: "3971", text: "بَابُ مَا يُقَالُ عِنْدَ الْمُصِيبَةِ " },
{id: "4441  ", parent: "3971", text: "بَابُ مَا يُقَالُ عِنْدَ الْمَرِيضِ وَالْمَيِّتِ " },
{id: "4442  ", parent: "3971", text: "بَابٌ فِي إِغْمَاضِ الْمَيِّتِ وَالدُّعَاءِ لَهُ إِذَا حُضِرَ " },
{id: "4443  ", parent: "3971", text: "بَابٌ فِي شُخُوصِ بَصَرِ الْمَيِّتِ يَتْبَعُ نَفْسَهُ " },
{id: "4444  ", parent: "3971", text: "بَابُ الْبُكَاءِ عَلَى الْمَيِّتِ " },
{id: "4445  ", parent: "3971", text: "بَابٌ فِي عِيَادَةِ الْمَرْضَى " },
{id: "4446  ", parent: "3971", text: "بَابٌ فِي الصَّبْرِ عَلَى الْمُصِيبَةِ عِنْدَ أَوَّلِ الصَّدْمَةِ " },
{id: "4447  ", parent: "3971", text: "بَابُ الْمَيِّتِ يُعَذَّبُ بِبُكَاءِ أَهْلِهِ عَلَيْهِ " },
{id: "4448  ", parent: "3971", text: "بَابُ التَّشْدِيدِ فِي النِّيَاحَةِ " },
{id: "4449  ", parent: "3971", text: "بَابُ نَهْيِ النِّسَاءِ عَنِ اتِّبَاعِ الْجَنَائِزِ " },
{id: "4450  ", parent: "3971", text: "بَابٌ فِي غَسْلِ الْمَيِّتِ " },
{id: "4451  ", parent: "3971", text: "بَابٌ فِي كَفَنِ الْمَيِّتِ " },
{id: "4452  ", parent: "3971", text: "بَابُ تَسْجِيَةِ الْمَيِّتِ " },
{id: "4453  ", parent: "3971", text: "بَابٌ فِي تَحْسِينِ كَفَنِ الْمَيِّتِ " },
{id: "4454  ", parent: "3971", text: "بَابُ الْإِسْرَاعِ بِالْجَنَازَةِ " },
{id: "4455  ", parent: "3971", text: "بَابُ فَضْلِ الصَّلَاةِ عَلَى الْجَنَازَةِ وَاتِّبَاعِهَا " },
{id: "4456  ", parent: "3971", text: "بَابُ مَنْ صَلَّى عَلَيْهِ مِائَةٌ شُفِّعُوا فِيهِ " },
{id: "4457  ", parent: "3971", text: "بَابُ مَنْ صَلَّى عَلَيْهِ أَرْبَعُونَ شُفِّعُوا فِيهِ " },
{id: "4458  ", parent: "3971", text: "بَابُ فِيمَنْ يُثْنَى عَلَيْهِ خَيْرٌ أَوْ شَرٌّ مِنَ الْمَوْتَى " },
{id: "4459  ", parent: "3971", text: "بَابُ مَا جَاءَ فِي مُسْتَرِيحٍ وَمُسْتَرَاحٍ مِنْهُ " },
{id: "4460  ", parent: "3971", text: "بَابٌ فِي التَّكْبِيرِ عَلَى الْجَنَازَةِ " },
{id: "4461  ", parent: "3971", text: "بَابُ الصَّلَاةِ عَلَى الْقَبْرِ " },
{id: "4462  ", parent: "3971", text: "بَابُ الْقِيَامِ لِلْجَنَازَةِ " },
{id: "4463  ", parent: "3971", text: "بَابُ نَسْخِ الْقِيَامِ لِلْجَنَازَةِ " },
{id: "4464  ", parent: "3971", text: "بَابُ الدُّعَاءِ لِلْمَيِّتِ فِي الصَّلَاةِ " },
{id: "4465  ", parent: "3971", text: "بَابُ أَيْنَ يَقُومُ الْإِمَامُ مِنَ الْمَيِّتِ لِلصَّلَاةِ عَلَيْهِ " },
{id: "4466  ", parent: "3971", text: "بَابُ رُكُوبِ الْمُصَلِّي عَلَى الْجَنَازَةِ إِذَا انْصَرَفَ " },
{id: "4467  ", parent: "3971", text: "بَابٌ فِي اللَّحْدِ وَنَصْبِ اللَّبِنَ عَلَى الْمَيِّتِ " },
{id: "4468  ", parent: "3971", text: "بَابُ جَعْلِ الْقَطِيفَةِ فِي الْقَبْرِ " },
{id: "4469  ", parent: "3971", text: "بَابُ الْأَمْرِ بِتَسْوِيَةِ الْقَبْرِ " },
{id: "4470  ", parent: "3971", text: "بَابُ النَّهْيِ عَنْ تَجْصِيصِ الْقَبْرِ وَالْبِنَاءِ عَلَيْهِ " },
{id: "4471  ", parent: "3971", text: "بَابُ النَّهْيِ عَنِ الْجُلُوسِ عَلَى الْقَبْرِ وَالصَّلَاةِ عَلَيْهِ " },
{id: "4472  ", parent: "3971", text: "بَابُ الصَّلَاةِ عَلَى الْجَنَازَةِ فِي الْمَسْجِدِ " },
{id: "4473  ", parent: "3971", text: "بَابُ مَا يُقَالُ عِنْدَ دُخُولِ الْقُبُورِ وَالدُّعَاءِ لِأَهْلِهَا " },
{id: "4474  ", parent: "3971", text: "بَابُ اسْتِئْذَانِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ رَبَّهُ عَزَّ وَجَلَّ فِي زِيَارَةِ قَبْرِ أُمِّهِ " },
{id: "4475  ", parent: "3971", text: "بَابُ تَرْكِ الصَّلَاةِ عَلَى الْقَاتِلِ نَفْسَهُ " },
{id: "3972", parent: "كِتَابُ الزَّكَاةِ ", text: "" },
{id: "4476  ", parent: "3972", text: "كِتَابُ الزَّكَاةِ " },
{id: "4477  ", parent: "3972", text: "بَابُ مَا فِيهِ الْعُشْرُ أَوْ نِصْفُ الْعُشْرِ " },
{id: "4478  ", parent: "3972", text: "بَابُ لَا زَكَاةَ عَلَى الْمُسْلِمِ فِي عَبْدِهِ وَفَرَسِهِ " },
{id: "4479  ", parent: "3972", text: "بَابٌ فِي تَقْدِيمِ الزَّكَاةِ وَمَنْعِهَا " },
{id: "4480  ", parent: "3972", text: "بَابُ زَكَاةِ الْفِطْرِ عَلَى الْمُسْلِمِينَ مِنَ التَّمْرِ وَالشَّعِيرِ " },
{id: "4481  ", parent: "3972", text: "بَابُ الْأَمْرِ بِإِخْرَاجِ زَكَاةِ الْفِطْرِ قَبْلَ الصَّلَاةِ " },
{id: "4482  ", parent: "3972", text: "بَابُ إِثْمِ مَانِعِ الزَّكَاةِ " },
{id: "4483  ", parent: "3972", text: "بَابُ إِرْضَاءِ السُّعَاةِ " },
{id: "4484  ", parent: "3972", text: "بَابُ تَغْلِيظِ عُقُوبَةِ مَنْ لَا يُؤَدِّي الزَّكَاةَ " },
{id: "4485  ", parent: "3972", text: "بَابُ التَّرْغِيبِ فِي الصَّدَقَةِ " },
{id: "4486  ", parent: "3972", text: "بَابٌ فِي الْكَنَّازِينَ لِلْأَمْوَالِ وَالتَّغْلِيظِ عَلَيْهِمْ " },
{id: "4487  ", parent: "3972", text: "بَابُ الْحَثِّ عَلَى النَّفَقَةِ وَتَبْشِيرِ الْمُنْفِقِ بِالْخَلَفِ " },
{id: "4488  ", parent: "3972", text: "بَابُ فَضْلِ النَّفَقَةِ عَلَى الْعِيَالِ وَالْمَمْلُوكِ، وَإِثْمِ مَنْ ضَيَّعَهُمْ أَوْ حَبَسَ نَفَقَتَهُمْ عَنْهُمْ " },
{id: "4489  ", parent: "3972", text: "بَابُ الِابْتِدَاءِ فِي النَّفَقَةِ بِالنَّفْسِ ثُمَّ أَهْلِهِ ثُمَّ الْقَرَابَةِ " },
{id: "4490  ", parent: "3972", text: "بَابُ فَضْلِ النَّفَقَةِ وَالصَّدَقَةِ عَلَى الْأَقْرَبِينَ وَالزَّوْجِ وَالْأَوْلَادِ، وَالْوَالِدَيْنِ وَلَوْ كَانُوا مُشْرِكِينَ " },
{id: "4491  ", parent: "3972", text: "بَابُ وُصُولِ ثَوَابِ الصَّدَقَةِ عَنِ الْمَيِّتِ إِلَيْهِ " },
{id: "4492  ", parent: "3972", text: "بَابُ بَيَانِ أَنَّ اسْمَ الصَّدَقَةِ يَقَعُ عَلَى كُلِّ نَوْعٍ مِنَ الْمَعْرُوفِ " },
{id: "4493  ", parent: "3972", text: "بَابٌ فِي الْمُنْفِقِ وَالْمُمْسِكِ " },
{id: "4494  ", parent: "3972", text: "بَابُ التَّرْغِيبِ فِي الصَّدَقَةِ قَبْلَ أَنْ لَا يُوجَدَ مَنْ يَقْبَلُهَا " },
{id: "4495  ", parent: "3972", text: "بَابُ قَبُولِ الصَّدَقَةِ مِنَ الْكَسْبِ الطَّيِّبِ وَتَرْبِيَتِهَا " },
{id: "4496  ", parent: "3972", text: "بَابُ الْحَثِّ عَلَى الصَّدَقَةِ وَلَوْ بِشِقِّ تَمْرَةٍ، أَوْ كَلِمَةٍ طَيِّبَةٍ وَأَنَّهَا حِجَابٌ مِنَ النَّارِ " },
{id: "4497  ", parent: "3972", text: "بَابُ الْحَمْلِ أُجْرَةً يُتَصَدَّقُ بِهَا، وَالنَّهْيِ الشَّدِيدِ عَنْ تَنْقِيصِ الْمُتَصَدِّقِ بِقَلِيلٍ " },
{id: "4498  ", parent: "3972", text: "بَابُ فَضْلِ الْمَنِيحَةِ " },
{id: "4499  ", parent: "3972", text: "بَابُ مَثَلِ الْمُنْفِقِ وَالْبَخِيلِ " },
{id: "4500  ", parent: "3972", text: "بَابُ ثُبُوتِ أَجْرِ الْمُتَصَدِّقِ، وَإِنْ وَقَعَتِ الصَّدَقَةُ فِي يَدِ غَيْرِ أَهْلِهَا " },
{id: "4501  ", parent: "3972", text: "بَابُ أَجْرِ الْخَازِنِ الْأَمِينِ، وَالْمَرْأَةِ إِذَا تَصَدَّقَتْ مِنْ بَيْتِ زَوْجِهَا غَيْرَ مُفْسِدَةٍ بِإِذْنِهِ الصَّرِيحِ أَوِ الْعُرْفِيِّ " },
{id: "4502  ", parent: "3972", text: "بَابُ مَا أَنْفَقَ الْعَبْدُ مِنْ مَالِ مَوْلَاهُ " },
{id: "4503  ", parent: "3972", text: "بَابُ مَنْ جَمَعَ الصَّدَقَةَ، وَأَعْمَالَ الْبِرِّ " },
{id: "4504  ", parent: "3972", text: "بَابُ الْحَثِّ عَلَى الْإِنْفَاقِ وَكَرَاهَةِ الْإِحْصَاءِ " },
{id: "4505  ", parent: "3972", text: "بَابُ الْحَثِّ عَلَى الصَّدَقَةِ، وَلَوْ بِالْقَلِيلِ وَلَا تَمْتَنِعُ مِنَ الْقَلِيلِ لِاحْتِقَارِهِ " },
{id: "4506  ", parent: "3972", text: "بَابُ فَضْلِ إِخْفَاءِ الصَّدَقَةِ " },
{id: "4507  ", parent: "3972", text: "بَابُ بَيَانِ أَنَّ أَفْضَلَ الصَّدَقَةِ صَدَقَةُ الصَّحِيحِ الشَّحِيحِ " },
{id: "4508  ", parent: "3972", text: "بَابُ بَيَانِ أَنَّ الْيَدَ الْعُلْيَا خَيْرٌ مِنَ الْيَدِ السُّفْلَى، وَأَنَّ الْيَدَ الْعُلْيَا هِيَ الْمُنْفِقَةُ وَأَنَّ السُّفْلَى هِيَ الْآخِذَةُ " },
{id: "4509  ", parent: "3972", text: "بَابُ النَّهْيِ عَنِ الْمَسْأَلَةِ " },
{id: "4510  ", parent: "3972", text: "بَابُ الْمِسْكِينِ الَّذِي لَا يَجِدُ غِنًى، وَلَا يُفْطَنُ لَهُ فَيُتَصَدَّقُ عَلَيْهِ " },
{id: "4511  ", parent: "3972", text: "بَابُ كَرَاهَةِ الْمَسْأَلَةِ لِلنَّاسِ " },
{id: "4512  ", parent: "3972", text: "بَابُ مَنْ تَحِلُّ لَهُ الْمَسْأَلَةُ " },
{id: "4513  ", parent: "3972", text: "بَابُ إِبَاحَةِ الْأَخْذِ لِمَنْ أُعْطِيَ مِنْ غَيْرِ مَسْأَلَةٍ وَلَا إِشْرَافٍ " },
{id: "4514  ", parent: "3972", text: "بَابُ كَرَاهَةِ الْحِرْصِ عَلَى الدُّنْيَا " },
{id: "4515  ", parent: "3972", text: "بَابُ لَوْ أَنَّ لِابْنِ آدَمَ وَادِيَيْنِ لَابْتَغَى ثَالِثًا " },
{id: "4516  ", parent: "3972", text: "بَابُ لَيْسَ الْغِنَى عَنْ كَثْرَةِ الْعَرَضِ " },
{id: "4517  ", parent: "3972", text: "بَابُ تَخَوُّفِ مَا يَخْرُجُ مِنْ زَهْرَةِ الدُّنْيَا " },
{id: "4518  ", parent: "3972", text: "بَابُ فَضْلِ التَّعَفُّفِ وَالصَّبْرِ " },
{id: "4519  ", parent: "3972", text: "بَابٌ فِي الْكَفَافِ وَالْقَنَاعَةِ " },
{id: "4520  ", parent: "3972", text: "بَابُ إِعْطَاءِ مَنْ سَأَلَ بِفُحْشٍ وَغِلْظَةٍ " },
{id: "4521  ", parent: "3972", text: "بَابُ إِعْطَاءِ مَنْ يُخَافُ عَلَى إِيمَانِهِ " },
{id: "4522  ", parent: "3972", text: "بَابُ إِعْطَاءِ الْمُؤَلَّفَةِ قُلُوبُهُمْ عَلَى الْإِسْلَامِ وَتَصَبُّرِ مَنْ قَوِيَ إِيمَانُهُ " },
{id: "4523  ", parent: "3972", text: "بَابُ ذِكْرِ الْخَوَارِجِ وَصِفَاتِهِمْ " },
{id: "4524  ", parent: "3972", text: "بَابُ التَّحْرِيضِ عَلَى قَتْلِ الْخَوَارِجِ " },
{id: "4525  ", parent: "3972", text: "بَابُ الْخَوَارِجُ شَرُّ الْخَلْقِ وَالْخَلِيقَةِ " },
{id: "4526  ", parent: "3972", text: "بَابُ تَحْرِيمِ الزَّكَاةِ عَلَى رَسُولِ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَعَلَى آلِهِ وَهُمْ بَنُو هَاشِمٍ وَبَنُو الْمُطَّلِبِ دُونَ غَيْرِهِمْ " },
{id: "4527  ", parent: "3972", text: "بَابُ تَرْكِ اسْتِعْمَالِ آلِ النَّبِيِّ عَلَى الصَّدَقَةِ " },
{id: "4528  ", parent: "3972", text: "بَابُ إِبَاحَةِ الْهَدِيَّةِ لِلنَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَلِبَنِي هَاشِمٍ وَبَنِي الْمُطَّلِبِ، وَإِنْ كَانَ الْمُهْدِي مَلَكَهَا بِطَرِيقِ الصَّدَقَةِ، وَبَيَانِ أَنَّ الصَّدَقَةَ، إِذَا قَبَضَهَا الْمُتَصَدَّقُ عَلَيْهِ زَالَ عَنْهَا وَصْفُ الصَّدَقَةِ وَحَلَّتْ لِكُلِّ أَحَدٍ مِمَّنْ كَانَتِ الصَّدَقَةُ مُحَرَّمَةً عَلَيْهِ " },
{id: "4529  ", parent: "3972", text: "بَابُ قَبُولِ النَّبِيِّ الْهَدِيَّةَ وَرَدِّهِ الصَّدَقَةَ " },
{id: "4530  ", parent: "3972", text: "بَابُ الدُّعَاءِ لِمَنْ أَتَى بِصَدَقَتِهِ " },
{id: "4531  ", parent: "3972", text: "بَابُ إِرْضَاءِ السَّاعِي مَا لَمْ يَطْلُبْ حَرَامًا " },
{id: "3973", parent: "كِتَابُ الصِّيَامِ ", text: "" },
{id: "4532  ", parent: "3973", text: "بَابُ فَضْلِ شَهْرِ رَمَضَانَ " },
{id: "4533  ", parent: "3973", text: "بَابُ وُجُوبِ صَوْمِ رَمَضَانَ لِرُؤْيَةِ الْهِلَالِ، وَالْفِطْرِ لِرُؤْيَةِ الْهِلَالِ، وَأَنَّهُ إِذَا غُمَّ فِي أَوَّلِهِ أَوْ آخِرِهِ أُكْمِلَتْ عِدَّةُ الشَّهْرِ ثَلَاثِينَ يَوْمًا " },
{id: "4534  ", parent: "3973", text: "بَابُ لَا تَقَدَّمُوا رَمَضَانَ بِصَوْمِ يَوْمٍ وَلَا يَوْمَيْنِ " },
{id: "4535  ", parent: "3973", text: "بَابُ الشَّهْرُ يَكُونُ تِسْعًا وَعِشْرِينَ " },
{id: "4536  ", parent: "3973", text: "بَابُ بَيَانِ أَنَّ لِكُلِّ بَلَدٍ رُؤْيَتَهُمْ وَأَنَّهُمْ إِذَا رَأَوُا الْهِلَالَ بِبَلَدٍ لَا يَثْبُتُ حُكْمُهُ لِمَا بَعُدَ عَنْهُمْ " },
{id: "4537  ", parent: "3973", text: "بَابُ بَيَانِ أَنَّهُ لَا اعْتِبَارَ بِكُبْرِ الْهِلَالِ وَصِغَرِهِ، وَأَنَّ اللهَ تَعَالَى أَمَدَّهُ لِلرُّؤْيَةِ فَإِنْ غُمَّ فَلْيُكْمَلْ ثَلَاثُونَ " },
{id: "4538  ", parent: "3973", text: "بَابُ بَيَانِ مَعْنَى قَوْلِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ «شَهْرَا عِيدٍ لَا يَنْقُصَانِ» " },
{id: "4539  ", parent: "3973", text: "بَابُ بَيَانِ أَنَّ الدُّخُولَ فِي الصَّوْمِ يَحْصُلُ بِطُلُوعِ الْفَجْرِ، وَأَنَّ لَهُ الْأَكْلَ وَغَيْرَهُ حَتَّى يَطْلُعَ الْفَجْرُ، وَبَيَانِ صِفَةِ الْفَجْرِ الَّذِي تَتَعَلَّقُ بِهِ الْأَحْكَامُ مِنَ الدُّخُولِ فِي الصَّوْمِ، وَدُخُولِ وَقْتِ صَلَاةِ الصُّبْحِ وَغَيْرِ ذَلِكَ " },
{id: "4540  ", parent: "3973", text: "بَابُ فَضْلِ السُّحُورِ وَتَأْكِيدِ اسْتِحْبَابِهِ، وَاسْتِحْبَابِ تَأْخِيرِهِ وَتَعْجِيلِ الْفِطْرِ " },
{id: "4541  ", parent: "3973", text: "بَابُ بَيَانِ وَقْتِ انْقِضَاءِ الصَّوْمِ وَخُرُوجِ النَّهَارِ " },
{id: "4542  ", parent: "3973", text: "بَابُ النَّهْيِ عَنِ الْوِصَالِ فِي الصَّوْمِ " },
{id: "4543  ", parent: "3973", text: "بَابُ بَيَانِ أَنَّ الْقُبْلَةَ فِي الصَّوْمِ لَيْسَتْ مُحَرَّمَةً عَلَى مَنْ لَمْ تُحَرِّكْ شَهْوَتَهُ " },
{id: "4544  ", parent: "3973", text: "بَابُ صِحَّةِ صَوْمِ مَنْ طَلَعَ عَلَيْهِ الْفَجْرُ وَهُوَ جُنُبٌ " },
{id: "4545  ", parent: "3973", text: "بَابُ تَغْلِيظِ تَحْرِيمِ الْجِمَاعِ فِي نَهَارِ رَمَضَانَ عَلَى الصَّائِمِ، وَوُجُوبِ الْكَفَّارَةِ الْكُبْرَى فِيهِ وَبَيَانِهَا، وَأَنَّهَا تَجِبُ عَلَى الْمُوسِرِ وَالْمُعْسِرِ وَتَثْبُتُ فِي ذِمَّةِ الْمُعْسِرِ حَتَّى يَسْتَطِيعَ " },
{id: "4546  ", parent: "3973", text: "بَابُ جَوَازِ الصَّوْمِ وَالْفِطْرِ فِي شَهْرِ رَمَضَانَ لِلْمُسَافِرِ فِي غَيْرِ مَعْصِيَةٍ إِذَا كَانَ سَفَرُهُ مَرْحَلَتَيْنِ فَأَكْثَرَ، وَأَنَّ الْأَفْضَلَ لِمَنْ أَطَاقَهُ بِلَا ضَرَرٍ أَنْ يَصُومَ، وَلِمَنْ يَشُقُّ عَلَيْهِ أَنْ يُفْطِرَ " },
{id: "4547  ", parent: "3973", text: "بَابُ أَجْرِ الْمُفْطِرِ فِي السَّفَرِ إِذَا تَوَلَّى الْعَمَلَ " },
{id: "4548  ", parent: "3973", text: "بَابُ التَّخْيِيرِ فِي الصَّوْمِ وَالْفِطْرِ فِي السَّفَرِ " },
{id: "4549  ", parent: "3973", text: "بَابُ اسْتِحْبَابِ الْفِطْرِ لِلْحَاجِّ بِعَرَفَاتٍ يَوْمَ عَرَفَةَ " },
{id: "4550  ", parent: "3973", text: "بَابُ صَوْمِ يَوْمِ عَاشُورَاءَ " },
{id: "4551  ", parent: "3973", text: "بَابُ أَيُّ يَوْمٍ يُصَامُ فِي عَاشُورَاءَ " },
{id: "4552  ", parent: "3973", text: "بَابُ مَنْ أَكَلَ فِي عَاشُورَاءَ فَلْيَكُفَّ بَقِيَّةَ يَوْمِهِ " },
{id: "4553  ", parent: "3973", text: "بَابُ النَّهْيِ عَنْ صَوْمِ يَوْمِ الْفِطْرِ وَيَوْمِ الْأَضْحَى " },
{id: "4554  ", parent: "3973", text: "بَابُ تَحْرِيمِ صَوْمِ أَيَّامِ التَّشْرِيقِ " },
{id: "4555  ", parent: "3973", text: "بَابُ كَرَاهَةِ صِيَامِ يَوْمِ الْجُمُعَةِ مُنْفَرِدًا " },
{id: "4556  ", parent: "3973", text: "بَابُ بَيَانِ نَسْخِ قَوْله تَعَالَى {وَعَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ} [البقرة: 184] بِقَوْلِهِ: {فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ} [البقرة: 185] " },
{id: "4557  ", parent: "3973", text: "بَابُ قَضَاءِ رَمَضَانَ فِي شَعْبَانَ " },
{id: "4558  ", parent: "3973", text: "بَابُ قَضَاءِ الصِّيَامِ عَنِ الْمَيِّتِ " },
{id: "4559  ", parent: "3973", text: "بَابُ الصَّائِمِ يُدْعَى لِطَعَامٍ فَلْيَقُلْ: إِنِّي صَائِمٌ " },
{id: "4560  ", parent: "3973", text: "بَابُ حِفْظِ اللِّسَانِ لِلصَّائِمِ " },
{id: "4561  ", parent: "3973", text: "بَابُ فَضْلِ الصِّيَامِ " },
{id: "4562  ", parent: "3973", text: "بَابُ فَضْلِ الصِّيَامِ فِي سَبِيلِ اللهِ لِمَنْ يُطِيقُهُ، بِلَا ضَرَرٍ وَلَا تَفْوِيتِ حَقٍّ " },
{id: "4563  ", parent: "3973", text: "بَابُ جَوَازِ صَوْمِ النَّافِلَةِ بِنِيَّةٍ مِنَ النَّهَارِ قَبْلَ الزَّوَالِ، وَجَوَازِ فِطْرِ الصَّائِمِ نَفْلًا مِنْ غَيْرِ عُذْرٍ " },
{id: "4564  ", parent: "3973", text: "بَابُ أَكْلُ النَّاسِي وَشُرْبُهُ وَجِمَاعُهُ لَا يُفْطِرُ " },
{id: "4565  ", parent: "3973", text: "بَابُ صِيَامِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِي غَيْرِ رَمَضَانَ، وَاسْتِحْبَابِ أَنْ لَا يُخْلِيَ شَهْرًا عَنْ صَوْمٍ " },
{id: "4566  ", parent: "3973", text: "بَابُ النَّهْيِ عَنْ صَوْمِ الدَّهْرِ لِمَنْ تَضَرَّرَ بِهِ أَوْ فَوَّتَ بِهِ حَقًّا أَوْ لَمْ يُفْطِرِ الْعِيدَيْنِ وَالتَّشْرِيقَ، وَبَيَانِ تَفْضِيلِ صَوْمِ يَوْمٍ، وَإِفْطَارِ يَوْمٍ " },
{id: "4567  ", parent: "3973", text: "بَابُ اسْتِحْبَابِ صِيَامِ ثَلَاثَةِ أَيَّامٍ مِنْ كُلِّ شَهْرٍ وَصَوْمِ يَوْمِ عَرَفَةَ وَعَاشُورَاءَ وَالِاثْنَيْنِ وَالْخَمِيسِ " },
{id: "4568  ", parent: "3973", text: "بَابُ صَوْمِ سُرَرِ شَعْبَانَ " },
{id: "4569  ", parent: "3973", text: "بَابُ فَضْلِ صَوْمِ الْمُحَرَّمِ " },
{id: "4570  ", parent: "3973", text: "بَابُ اسْتِحْبَابِ صَوْمِ سِتَّةِ أَيَّامٍ مِنْ شَوَّالٍ إِتْبَاعًا لِرَمَضَانَ " },
{id: "4571  ", parent: "3973", text: "بَابُ فَضْلِ لَيْلَةِ الْقَدْرِ، وَالْحَثِّ عَلَى طَلَبِهَا، وَبَيَانِ مَحَلِّهَا وَأَرْجَى أَوْقَاتِ طَلَبِهَا " },
{id: "3974", parent: "كِتَابُ الِاعْتِكَافِ ", text: "" },
{id: "4572  ", parent: "3974", text: "بَابُ اعْتِكَافِ الْعَشْرِ الْأَوَاخِرِ مِنْ رَمَضَانَ " },
{id: "4573  ", parent: "3974", text: "بَابُ مَتَى يَدْخُلُ مَنْ أَرَادَ الِاعْتِكَافَ فِي مُعْتَكَفِهِ " },
{id: "4574  ", parent: "3974", text: "بَابُ الِاجْتِهَادِ فِي الْعَشْرِ الْأَوَاخِرِ مِنْ شَهْرِ رَمَضَانَ " },
{id: "4575  ", parent: "3974", text: "بَابُ صَوْمِ عَشْرِ ذِي الْحِجَّةِ " },
{id: "3975", parent: "كِتَابُ الْحَجِّ ", text: "" },
{id: "4576  ", parent: "3975", text: "بَابُ مَا يُبَاحُ لِلْمُحْرِمِ بِحَجٍّ أَوْ عُمْرَةٍ، وَمَا لَا يُبَاحُ وَبَيَانِ تَحْرِيمِ الطِّيبِ عَلَيْهِ حَدَّثَنَا يَحْيَى بْنُ يَحْيَى، قَالَ: قَرَأْتُ عَلَى مَالِكٍ، عَنْ نَافِعٍ، عَنِ ابْنِ عُمَرَ رَضِيَ اللهُ عَنْهُمَا، أَنَّ رَجُلًا سَأَلَ رَسُولَ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: مَا يَلْبَسُ الْمُحْرِمُ مِنَ الثِّيَابِ؟ فَقَالَ رَسُولُ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «لَا تَلْبَسُوا الْقُمُصَ، وَلَا الْعَمَائِمَ، وَلَا السَّرَاوِيلَاتِ، وَلَا الْبَرَانِسَ، وَلَا الْخِفَافَ، إِلَّا أَحَدٌ لَا يَجِدُ النَّعْلَيْنِ، فَلْيَلْبَسِ الْخُفَّيْنِ، وَلْيَقْطَعْهُمَا أَسْفَلَ مِنَ الْكَعْبَيْنِ، وَلَا تَلْبَسُوا مِنَ الثِّيَابِ شَيْئًا مَسَّهُ الزَّعْفَرَانُ وَلَا الْوَرْسُ» " },
{id: "4577  ", parent: "3975", text: "بَابُ مَوَاقِيتِ الْحَجِّ وَالْعُمْرَةِ " },
{id: "4578  ", parent: "3975", text: "بَابُ التَّلْبِيَةِ وَصِفَتِهَا وَوَقْتِهَا " },
{id: "4579  ", parent: "3975", text: "بَابُ أَمْرِ أَهْلِ الْمَدِينَةِ بِالْإِحْرَامِ مِنْ عِنْدِ مَسْجِدِ ذِي الْحُلَيْفَةِ " },
{id: "4580  ", parent: "3975", text: "بَابُ الْإِهْلَالِ مِنْ حَيْثُ تَنْبَعِثُ الرَّاحِلَةُ " },
{id: "4581  ", parent: "3975", text: "بَابُ الصَّلَاةِ فِي مَسْجِدِ ذِي الحُلَيْفَةِ " },
{id: "4582  ", parent: "3975", text: "بَابُ الطِّيبِ لِلْمُحْرِمِ عِنْدَ الْإِحْرَامِ " },
{id: "4583  ", parent: "3975", text: "بَابُ تَحْرِيمِ الصَّيْدِ لِلْمُحْرِمِ " },
{id: "4584  ", parent: "3975", text: "بَابُ مَا يَنْدُبُ لِلْمُحْرِمِ وَغَيْرِهِ قَتْلَهُ مِنَ الدَّوَابِّ فِي الْحِلِّ وَالْحَرَمِ " },
{id: "4585  ", parent: "3975", text: "بَابُ جَوَازِ حَلْقِ الرَّأْسِ لِلْمُحْرِمِ إِذَا كَانَ بِهِ أَذًى، وَوُجُوبِ الْفِدْيَةِ لِحَلْقِهِ، وَبَيَانِ قَدْرِهَا " },
{id: "4586  ", parent: "3975", text: "بَابُ جَوَازِ الْحِجَامَةِ لِلْمُحْرِمِ " },
{id: "4587  ", parent: "3975", text: "بَابُ جَوَازِ مُدَاوَاةِ الْمُحْرِمِ عَيْنَيْهِ " },
{id: "4588  ", parent: "3975", text: "بَابُ جَوَازِ غَسْلِ الْمُحْرِمِ بَدَنَهُ وَرَأْسَهُ " },
{id: "4589  ", parent: "3975", text: "بَابُ مَا يُفْعَلُ بِالْمُحْرِمِ إِذَا مَاتَ " },
{id: "4590  ", parent: "3975", text: "بَابُ جَوَازِ اشْتِرَاطِ الْمُحْرِمِ التَّحَلُّلَ بِعُذْرِ الْمَرَضِ وَنَحْوِهِ " },
{id: "4591  ", parent: "3975", text: "بَابُ إِحْرَامِ النُّفَسَاءِ وَاسْتِحْبَابِ اغْتِسَالِهَا لِلْإِحْرَامِ، وَكَذَا الْحَائِضُ " },
{id: "4592  ", parent: "3975", text: "بَابُ بَيَانِ وُجُوهِ الْإِحْرَامِ، وَأَنَّهُ يَجُوزُ إِفْرَادُ الْحَجِّ وَالتَّمَتُّعِ وَالْقِرَانِ، وَجَوَازِ إِدْخَالِ الْحَجِّ عَلَى الْعُمْرَةِ، وَمَتَى يَحِلُّ الْقَارِنُ مِنْ نُسُكِهِ " },
{id: "4593  ", parent: "3975", text: "بَابٌ فِي الْمُتْعَةِ بِالْحَجِّ وَالْعُمْرَةِ " },
{id: "4594  ", parent: "3975", text: "بَابُ حَجَّةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4595  ", parent: "3975", text: "بَابُ مَا جَاءَ أَنَّ عَرَفَةَ كُلَّهَا مَوْقِفٌ " },
{id: "4596  ", parent: "3975", text: "بَابٌ فِي الْوُقُوفِ وَقَوْلُهُ تَعَالَى: {ثُمَّ أَفِيضُوا مِنْ حَيْثُ أَفَاضَ النَّاسُ} [البقرة: 199] " },
{id: "4597  ", parent: "3975", text: "بَابٌ فِي نَسْخِ التَّحَلُّلِ مِنَ الْإِحْرَامِ وَالْأَمْرِ بِالتَّمَامِ " },
{id: "4598  ", parent: "3975", text: "بَابُ جَوَازِ التَّمَتُّعِ " },
{id: "4599  ", parent: "3975", text: "بَابُ وُجُوبِ الدَّمِ عَلَى الْمُتَمَتِّعِ، وَأَنَّهُ إِذَا عَدَمَهُ لَزِمَهُ صَوْمُ ثَلَاثَةِ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ إِذَا رَجَعَ إِلَى أَهْلِهِ " },
{id: "4600  ", parent: "3975", text: "بَابُ بَيَانِ أَنَّ الْقَارِنَ لَا يَتَحَلَّلُ إِلَّا فِي وَقْتِ تَحَلُّلِ الْحَاجِّ الْمُفْرِدِ " },
{id: "4601  ", parent: "3975", text: "بَابُ بَيَانِ جَوَازِ التَّحَلُّلِ بِالْإِحْصَارِ وَجَوَازِ الْقِرَانِ " },
{id: "4602  ", parent: "3975", text: "بَابٌ فِي الْإِفْرَادِ وَالْقِرَانِ بِالْحَجِّ وَالْعُمْرَةِ " },
{id: "4603  ", parent: "3975", text: "بَابُ مَا يَلْزَمُ مَنْ أَحْرَمَ بِالْحَجِّ، ثُمَّ قَدِمَ مَكَّةَ مِنَ الطَّوَافِ وَالسَّعْيِ " },
{id: "4604  ", parent: "3975", text: "بَابُ مَا يَلْزَمُ، مَنْ طَافَ بِالْبَيْتِ وَسَعَى، مِنَ الْبَقَاءِ عَلَى الْإِحْرَامِ وَتَرْكِ التَّحَلُّلِ " },
{id: "4605  ", parent: "3975", text: "بَابٌ فِي مُتْعَةِ الْحَجِّ " },
{id: "4606  ", parent: "3975", text: "بَابُ جَوَازِ الْعُمْرَةِ فِي أَشْهُرِ الْحَجِّ " },
{id: "4607  ", parent: "3975", text: "بَابُ تَقْلِيدِ الْهَدْيِ وَإِشْعَارِهِ عِنْدَ الْإِحْرَامِ " },
{id: "4608  ", parent: "3975", text: "بَابُ التَّقْصِيرِ فِي الْعُمْرَةِ " },
{id: "4609  ", parent: "3975", text: "بَابُ إِهْلَالِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَهَدْيِهِ " },
{id: "4610  ", parent: "3975", text: "بَابُ بَيَانِ عَدَدِ عُمَرِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَزَمَانِهِنَّ " },
{id: "4611  ", parent: "3975", text: "بَابُ فَضْلِ الْعُمْرَةِ فِي رَمَضَانَ " },
{id: "4612  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ دُخُولِ مَكَّةَ مِنَ الثَّنِيَّةِ الْعُلْيَا وَالْخُرُوجِ مِنْهَا مِنَ الثَّنِيَّةِ السُّفْلَى وَدُخُولِ بَلَدِهِ مِنْ طَرِيقٍ غَيْرَ الَّتِي خَرَجَ مِنْهَا " },
{id: "4613  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ الْمَبِيتِ بِذِي طُوًى عِنْدَ إِرَادَةِ دُخُولِ مَكَّةَ، وَالِاغْتِسَالِ لِدُخُولِهَا وَدُخُولِهَا نَهَارًا " },
{id: "4614  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ الرَّمَلِ فِي الطَّوَافِ وَالْعُمْرَةِ، وَفِي الطَّوَافِ الْأَوَّلِ فِي الْحَجِّ " },
{id: "4615  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ اسْتِلَامِ الرُّكْنَيْنِ الْيَمَانِيَيْنِ فِي الطَّوَافِ دُونَ الرُّكْنَيْنِ الْآخَرَيْنِ " },
{id: "4616  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ تَقْبِيلِ الْحَجَرِ الْأَسْوَدِ فِي الطَّوَافِ " },
{id: "4617  ", parent: "3975", text: "بَابُ جَوَازِ الطَّوَافِ عَلَى بَعِيرٍ وَغَيْرِهِ، وَاسْتِلَامِ الْحَجَرِ بِمِحْجَنٍ وَنَحْوِهِ لِلرَّاكِبِ " },
{id: "4618  ", parent: "3975", text: "بَابُ بَيَانِ أَنَّ السَّعْيَ بَيْنَ الصَّفَا وَالْمَرْوَةِ رُكْنٌ لَا يَصِحُّ الْحَجُّ إِلَّا بِهِ " },
{id: "4619  ", parent: "3975", text: "بَابُ بَيَانِ أَنَّ السَّعْيَ لَا يُكَرَّرُ " },
{id: "4620  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ إِدَامَةِ الْحَاجِّ التَّلْبِيَةَ حَتَّى يَشْرَعَ فِي رَمْيِ جَمْرَةِ الْعَقَبَةِ يَوْمَ النَّحْرِ " },
{id: "4621  ", parent: "3975", text: "بَابُ التَّلْبِيَةِ وَالتَّكْبِيرِ فِي الذَّهَابِ مِنْ مِنًى إِلَى عَرَفَاتٍ فِي يَوْمِ عَرَفَةَ " },
{id: "4622  ", parent: "3975", text: "بَابُ الْإِفَاضَةِ مِنْ عَرَفَاتٍ إِلَى الْمُزْدَلِفَةِ وَاسْتِحْبَابِ صَلَاتَيِ الْمَغْرِبِ وَالْعِشَاءِ جَمِيعًا بِالْمُزْدَلِفَةِ فِي هَذِهِ اللَّيْلَةِ " },
{id: "4623  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ زِيَادَةِ التَّغْلِيسِ بِصَلَاةِ الصُّبْحِ يَوْمَ النَّحْرِ بِالْمُزْدَلِفَةِ، وَالْمُبَالَغَةِ فِيهِ بَعْدَ تَحَقُّقِ طُلُوعِ الْفَجْرِ " },
{id: "4624  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ تَقْدِيمِ دَفْعِ الضَّعَفَةِ مِنَ النِّسَاءِ وَغَيْرِهِنَّ مِنْ مُزْدَلِفَةَ إِلَى مِنًى فِي أَوَاخِرِ اللَّيْلِ قَبْلَ زَحْمَةِ النَّاسِ، وَاسْتِحْبَابِ الْمُكْثِ لِغَيْرِهِمْ حَتَّى يُصَلُّوا الصُّبْحَ بِمُزْدَلِفَةَ " },
{id: "4625  ", parent: "3975", text: "بَابُ رَمْيِ جَمْرَةِ الْعَقَبَةِ مِنْ بَطْنِ الْوَادِي وَتَكُونُ مَكَّةُ عَنْ يَسَارِهِ وَيُكَبِّرُ مَعَ كُلِّ حَصَاةٍ " },
{id: "4626  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ رَمْيِ جَمْرَةِ الْعَقَبَةِ يَوْمَ النَّحْرِ رَاكِبًا، وَبَيَانِ قَوْلِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ «لِتَأْخُذُوا مَنَاسِكَكُمْ» " },
{id: "4627  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ كَوْنِ حَصَى الْجِمَارِ بِقَدْرِ حَصَى الْخَذْفِ " },
{id: "4628  ", parent: "3975", text: "بَابُ بَيَانِ وَقْتِ اسْتِحْبَابِ الرَّمْيِ " },
{id: "4629  ", parent: "3975", text: "بَابُ بَيَانِ أَنَّ حَصَى الْجِمَارِ سَبْعٌ " },
{id: "4630  ", parent: "3975", text: "بَابُ تَفْضِيلِ الْحَلْقِ عَلَى التَّقْصِيرِ وَجَوَازِ التَّقْصِيرِ " },
{id: "4631  ", parent: "3975", text: "بَابُ بَيَانِ أَنَّ السُّنَّةَ يَوْمَ النَّحْرِ أَنْ يَرْمِيَ، ثُمَّ يَنْحَرَ، ثُمَّ يَحْلِقَ وَالِابْتِدَاءِ فِي الْحَلْقِ بِالْجَانِبِ الْأَيْمَنِ مِنْ رَأْسِ الْمَحْلُوقِ " },
{id: "4632  ", parent: "3975", text: "بَابُ مَنْ حَلَقَ قَبْلَ النَّحْرِ، أَوْ نَحَرَ قَبْلَ الرَّمْيِ " },
{id: "4633  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ طَوَافِ الْإِفَاضَةِ يَوْمَ النَّحْرِ " },
{id: "4634  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ النُّزُولِ بِالْمُحَصَّبِ يَوْمَ النَّفْرِ وَالصَّلَاةِ بِهِ " },
{id: "4635  ", parent: "3975", text: "بَابُ وُجُوبِ الْمَبِيتِ بِمِنًى لَيَالِي أَيَّامِ التَّشْرِيقِ، وَالتَّرْخِيصِ فِي تَرْكِهِ لِأَهْلِ السِّقَايَةِ " },
{id: "4636  ", parent: "3975", text: "بَابٌ فِي الصَّدَقَةِ بِلُحُومِ الْهَدْيِ وَجُلُودِهَا وَجِلَالِهَا " },
{id: "4637  ", parent: "3975", text: "بَابُ الِاشْتِرَاكِ فِي الْهَدْيِ وَإِجْزَاءِ الْبَقَرَةِ وَالْبَدَنَةِ كُلٍّ مِنْهُمَا عَنْ سَبْعَةٍ " },
{id: "4638  ", parent: "3975", text: "بَابُ نَحْرِ الْبُدْنِ قِيَامًا مُقَيَّدَةً " },
{id: "4639  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ بَعْثِ الْهَدْيِ إِلَى الْحَرَمِ لِمَنْ لَا يُرِيدُ الذَّهَابَ بِنَفْسِهِ وَاسْتِحْبَابِ تَقْلِيدِهِ وَفَتْلِ الْقَلَائِدِ وَأَنَّ بَاعِثَهُ لَا يَصِيرُ مُحْرِمًا وَلَا يَحْرُمُ عَلَيْهِ شَيْءٌ بِذَلِكَ " },
{id: "4640  ", parent: "3975", text: "بَابُ جَوَازِ رُكُوبِ الْبَدَنَةِ الْمُهْدَاةِ لِمَنِ احْتَاجَ إِلَيْهَا " },
{id: "4641  ", parent: "3975", text: "بَابُ مَا يَفْعَلُ بِالْهَدْيِ إِذَا عَطِبَ فِي الطَّرِيقِ " },
{id: "4642  ", parent: "3975", text: "بَابُ وُجُوبِ طَوَافِ الْوَدَاعِ وَسُقُوطِهِ عَنِ الْحَائِضِ " },
{id: "4643  ", parent: "3975", text: "بَابُ اسْتِحْبَابِ دُخُولِ الْكَعْبَةِ لِلْحَاجِّ وَغَيْرِهِ، وَالصَّلَاةِ فِيهَا، وَالدُّعَاءِ فِي نَوَاحِيهَا كُلِّهَا " },
{id: "4644  ", parent: "3975", text: "بَابُ نَقْضِ الْكَعْبَةِ وَبِنَائِهَا " },
{id: "4645  ", parent: "3975", text: "بَابُ جَدْرِ الْكَعْبَةِ وَبَابِهَا " },
{id: "4646  ", parent: "3975", text: "بَابُ الْحَجِّ عَنِ الْعَاجِزِ لِزَمَانَةٍ وَهَرَمٍ وَنَحْوِهِمَا، أَوْ لِلْمَوْتِ " },
{id: "4647  ", parent: "3975", text: "بَابُ صِحَّةِ حَجِّ الصَّبِيِّ وَأَجْرِ مَنْ حَجَّ بِهِ " },
{id: "4648  ", parent: "3975", text: "بَابُ فَرْضِ الْحَجِّ مَرَّةً فِي الْعُمُرِ " },
{id: "4649  ", parent: "3975", text: "بَابُ سَفَرِ الْمَرْأَةِ مَعَ مَحْرَمٍ إِلَى حَجٍّ وَغَيْرِهِ " },
{id: "4650  ", parent: "3975", text: "بَابُ مَا يَقُولُ إِذَا رَكِبَ إِلَى سَفَرِ الْحَجِّ وَغَيْرِهِ " },
{id: "4651  ", parent: "3975", text: "بَابُ مَا يَقُولُ إِذَا قَفَلَ مِنْ سَفَرِ الْحَجِّ وَغَيْرِهِ " },
{id: "4652  ", parent: "3975", text: "بَابُ التَّعْرِيسِ بِذِي الْحُلَيْفَةِ، وَالصَّلَاةِ بِهَا إِذَا صَدَرَ مِنَ الْحَجِّ أَوِ الْعُمْرَةِ " },
{id: "4653  ", parent: "3975", text: "بَابُ لَا يَحُجُّ الْبَيْتَ مُشْرِكٌ، وَلَا يَطُوفُ بِالْبَيْتِ عُرْيَانٌ، وَبَيَانُ يَوْمِ الْحَجِّ الْأَكْبَرِ " },
{id: "4654  ", parent: "3975", text: "بَابٌ فِي فَضْلِ الْحَجِّ وَالْعُمْرَةِ، وَيَوْمِ عَرَفَةَ " },
{id: "4655  ", parent: "3975", text: "بَابُ النُّزُولِ بِمَكَّةَ لِلْحَاجِ، وَتَوْرِيثِ دُورِهَا " },
{id: "4656  ", parent: "3975", text: "بَابُ جَوَازِ الْإِقَامَةِ بِمَكَّةَ لِلْمُهَاجِرِ مِنْهَا بَعْدَ فَرَاغِ الْحَجِّ وَالْعُمْرَةِ، ثَلَاثَةَ أَيَّامٍ بِلَا زِيَادَةٍ " },
{id: "4657  ", parent: "3975", text: "بَابُ تَحْرِيمِ مَكَّةَ وَصَيْدِهَا وَخَلَاهَا وَشَجَرِهَا وَلُقَطَتِهَا، إِلَّا لِمُنْشِدٍ عَلَى الدَّوَامِ " },
{id: "4658  ", parent: "3975", text: "بَابُ النَّهْيِ عَنْ حَمْلِ السِّلَاحِ بِمَكَّةَ بِلَا حَاجَةٍ " },
{id: "4659  ", parent: "3975", text: "بَابُ جَوَازِ دُخُولِ مَكَّةَ بِغَيْرِ إِحْرَامٍ " },
{id: "4660  ", parent: "3975", text: "بَابُ فَضْلِ الْمَدِينَةِ، وَدُعَاءِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِيهَا بِالْبَرَكَةِ، وَبَيَانِ تَحْرِيمِهَا، وَتَحْرِيمِ صَيْدِهَا وَشَجَرِهَا، وَبَيَانِ حُدُودِ حَرَمِهَا " },
{id: "4661  ", parent: "3975", text: "باب الترغيب في سكن المدينة والصبر على لأوائها " },
{id: "4662  ", parent: "3975", text: "بَابُ صِيَانَةِ الْمَدِينَةِ مِنْ دُخُولِ الطَّاعُونِ، وَالدَّجَّالِ إِلَيْهَا " },
{id: "4663  ", parent: "3975", text: "بَابُ الْمَدِينَةِ تَنْفِي شِرَارَهَا " },
{id: "4664  ", parent: "3975", text: "بَابُ مَنْ أَرَادَ أَهْلَ الْمَدِينَةِ بِسُوءٍ أَذَابَهُ اللهُ " },
{id: "4665  ", parent: "3975", text: "بَابُ التَّرْغِيبِ فِي الْمَدِينَةِ عِنْدَ فَتْحِ الْأَمْصَارِ " },
{id: "4666  ", parent: "3975", text: "بَابٌ فِي الْمَدِينَةِ حِينَ يَتْرُكُهَا أَهْلُهَا " },
{id: "4667  ", parent: "3975", text: "بَابُ مَا بَيْنَ الْقَبْرِ وَالْمِنْبَرِ رَوْضَةٌ مِنْ رِيَاضِ الْجَنَّةِ " },
{id: "4668  ", parent: "3975", text: "بَابُ أُحُدٌ جَبَلٌ يُحِبُّنَا وَنُحِبُّهُ " },
{id: "4669  ", parent: "3975", text: "بَابُ فَضْلِ الصَّلَاةِ بِمَسْجِدَيْ مَكَّةَ وَالْمَدِينَةَ " },
{id: "4670  ", parent: "3975", text: "بَابُ لَا تُشَدُّ الرِّحَالُ إِلَّا إِلَى ثَلَاثَةِ مَسَاجِدَ " },
{id: "4671  ", parent: "3975", text: "بَابُ بَيَانِ أَنَّ الْمَسْجِدَ الَّذِي أُسِّسَ عَلَى التَّقْوَى هُوَ مَسْجِدُ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بِالْمَدِينَةِ " },
{id: "4672  ", parent: "3975", text: "بَابُ فَضْلِ مَسْجِدِ قُبَاءٍ، وَفَضْلِ الصَّلَاةِ فِيهِ، وَزِيَارَتِهِ " },
{id: "3976", parent: "كِتَابُ النِّكَاحِ ", text: "" },
{id: "4673  ", parent: "3976", text: "بَابُ اسْتِحْبَابِ النِّكَاحِ لِمَنْ تَاقَتْ نَفْسُهُ إِلَيْهِ، وَوَجَدَ مُؤَنَهُ، وَاشْتِغَالِ مَنْ عَجَزَ عَنِ الْمُؤَنِ بِالصَّوْمِ " },
{id: "4674  ", parent: "3976", text: "بَابُ نَدْبِ مَنْ رَأَى امْرَأَةً فَوَقَعَتْ فِي نَفْسِهِ، إِلَى أَنْ يَأْتِيَ امْرَأَتَهُ أَوْ جَارِيَتَهُ فَيُوَاقِعَهَا " },
{id: "4675  ", parent: "3976", text: "بَابُ نِكَاحِ الْمُتْعَةِ، وَبَيَانِ أَنَّهُ أُبِيحَ، ثُمَّ نُسِخَ، ثُمَّ أُبِيحَ، ثُمَّ نُسِخَ، وَاسْتَقَرَّ تَحْرِيمُهُ إِلَى يَوْمِ الْقِيَامَةِ " },
{id: "4676  ", parent: "3976", text: "بَابُ تَحْرِيمِ الْجَمْعِ بَيْنَ الْمَرْأَةِ وَعَمَّتِهَا أَوْ خَالَتِهَا فِي النِّكَاحِ " },
{id: "4677  ", parent: "3976", text: "بَابُ تَحْرِيمِ نِكَاحِ الْمُحْرِمِ، وَكَرَاهَةِ خِطْبَتِهِ " },
{id: "4678  ", parent: "3976", text: "بَابُ تَحْرِيمِ الْخِطْبَةِ عَلَى خِطْبَةِ أَخِيهِ، حَتَّى يَأْذَنَ أَوْ يَتْرُكَ " },
{id: "4679  ", parent: "3976", text: "بَابُ تَحْرِيمِ نِكَاحِ الشِّغَارِ وَبُطْلَانِهِ " },
{id: "4680  ", parent: "3976", text: "بَابُ الْوَفَاءِ بِالشُّرُوطِ فِي النِّكَاحِ " },
{id: "4681  ", parent: "3976", text: "بَابُ اسْتِئْذَانِ الثَّيِّبِ فِي النِّكَاحِ بِالنُّطْقِ، وَالْبِكْرِ بِالسُّكُوتِ " },
{id: "4682  ", parent: "3976", text: "بَابُ تَزْوِيجِ الْأَبِ الْبِكْرَ الصَّغِيرَةَ " },
{id: "4683  ", parent: "3976", text: "بَابُ اسْتِحْبَابِ التَّزَوُّجِ وَالتَّزْوِيجِ فِي شَوَّالٍ، وَاسْتِحْبَابِ الدُّخُولِ فِيهِ " },
{id: "4684  ", parent: "3976", text: "بَابُ نَدْبِ النَّظَرِ إِلَى وَجْهِ الْمَرْأَةِ وَكَفَّيْهَا لِمَنْ يُرِيدُ تَزَوُّجَهَا " },
{id: "4685  ", parent: "3976", text: "بَابُ الصَّدَاقِ، وَجَوَازِ كَوْنِهِ تَعْلِيمَ قُرْآنٍ، وَخَاتَمَ حَدِيدٍ، وَغَيْرَ ذَلِكَ مِنْ قَلِيلٍ وَكَثِيرٍ، وَاسْتِحْبَابِ كَوْنِهِ خَمْسَمِائَةِ دِرْهَمٍ لِمَنْ لَا يُجْحِفُ بِهِ " },
{id: "4686  ", parent: "3976", text: "بَابُ فَضِيلَةِ إِعْتَاقِهِ أَمَتَهُ، ثُمَّ يَتَزَوَّجُهَا " },
{id: "4687  ", parent: "3976", text: "بَابُ زَوَاجِ زَيْنَبَ بِنْتِ جَحْشٍ، وَنُزُولِ الْحِجَابِ، وَإِثْبَاتِ وَلِيمَةِ الْعُرْسِ " },
{id: "4688  ", parent: "3976", text: "بَابُ الْأَمْرِ بِإِجَابَةِ الدَّاعِي إِلَى دَعْوَةٍ " },
{id: "4689  ", parent: "3976", text: "بَابُ لَا تَحِلُّ الْمُطَلَّقَةُ ثَلَاثًا لِمُطَلِّقِهَا حَتَّى تَنْكِحَ زَوْجًا غَيْرَهُ، وَيَطَأَهَا، ثُمَّ يُفَارِقَهَا وَتَنْقَضِيَ عِدَّتُهَا " },
{id: "4690  ", parent: "3976", text: "بَابُ مَا يُسْتَحَبُّ أَنْ يَقُولَهُ عِنْدَ الْجِمَاعِ " },
{id: "4691  ", parent: "3976", text: "بَابُ جَوَازِ جِمَاعِهِ امْرَأَتَهُ فِي قُبُلِهَا، مِنْ قُدَّامِهَا، وَمِنْ وَرَائِهَا مِنْ غَيْرِ تَعَرُّضٍ لِلدُّبُرِ " },
{id: "4692  ", parent: "3976", text: "بَابُ تَحْرِيمِ امْتِنَاعِهَا مِنْ فِرَاشِ زَوْجِهَا " },
{id: "4693  ", parent: "3976", text: "بَابُ تَحْرِيمِ إِفْشَاءِ سِرِّ الْمَرْأَةِ " },
{id: "4694  ", parent: "3976", text: "بَابُ حُكْمِ الْعَزْلِ " },
{id: "4695  ", parent: "3976", text: "بَابُ تَحْرِيمِ وَطْءِ الْحَامِلِ الْمَسْبِيَّةِ " },
{id: "4696  ", parent: "3976", text: "بَابُ جَوَازِ الْغِيلَةِ، وَهِيَ وَطْءُ الْمُرْضِعِ، وَكَرَاهَةِ الْعَزْلِ " },
{id: "3977", parent: "كِتَابُ الرِّضَاعِ ", text: "" },
{id: "4697  ", parent: "3977", text: "بَابُ يَحْرُمُ مِنَ الرَّضَاعَةِ مَا يَحْرُمُ مِنَ الْوِلَادَةِ " },
{id: "4698  ", parent: "3977", text: "بَابُ تَحْرِيمِ الرَّضَاعَةِ مِنْ مَاءِ الْفَحْلِ " },
{id: "4699  ", parent: "3977", text: "بَابُ تَحْرِيمِ ابْنَةِ الْأَخِ مِنَ الرَّضَاعَةِ " },
{id: "4700  ", parent: "3977", text: "بَابُ تَحْرِيمِ الرَّبِيبَةِ، وَأُخْتِ الْمَرْأَةِ " },
{id: "4701  ", parent: "3977", text: "بَابٌ فِي الْمَصَّةِ وَالْمَصَّتَيْنِ " },
{id: "4702  ", parent: "3977", text: "بَابُ التَّحْرِيمِ بِخَمْسِ رَضَعَاتٍ " },
{id: "4703  ", parent: "3977", text: "بَابُ رِضَاعَةِ الْكَبِيرِ " },
{id: "4704  ", parent: "3977", text: "بَابُ إِنَّمَا الرَّضَاعَةُ مِنَ الْمَجَاعَةِ " },
{id: "4705  ", parent: "3977", text: "بَابُ جَوَازِ وَطْءِ الْمَسْبِيَّةِ بَعْدَ الِاسْتِبْرَاءِ، وَإِنْ كَانَ لَهَا زَوْجٌ انْفَسَخَ نِكَاحُهَا بِالسَّبْيِ " },
{id: "4706  ", parent: "3977", text: "بَابُ الْوَلَدُ لِلْفِرَاشِ، وَتَوَقِّي الشُّبُهَاتِ " },
{id: "4707  ", parent: "3977", text: "بَابُ الْعَمَلِ بِإِلْحَاقِ الْقَائِفِ الْوَلَدَ " },
{id: "4708  ", parent: "3977", text: "بَابُ قَدْرِ مَا تَسْتَحِقُّهُ الْبِكْرُ، وَالثَّيِّبُ مِنْ إِقَامَةِ الزَّوْجِ عِنْدَهَا عُقْبَ الزِّفَافِ " },
{id: "4709  ", parent: "3977", text: "بَابُ الْقَسْمِ بَيْنَ الزَّوْجَاتِ، وَبَيَانِ أَنَّ السُّنَّةَ أَنْ تَكُونَ لِكُلِّ وَاحِدَةٍ لَيْلَةٌ مَعَ يَوْمِهَا " },
{id: "4710  ", parent: "3977", text: "بَابُ جَوَازِ هِبَتِهَا نَوْبَتَهَا لِضُرَّتِهَا " },
{id: "4711  ", parent: "3977", text: "بَابُ اسْتِحْبَابِ نِكَاحِ ذَاتِ الدِّينِ " },
{id: "4712  ", parent: "3977", text: "بَابُ اسْتِحْبَابِ نِكَاحِ الْبِكْرِ " },
{id: "4713  ", parent: "3977", text: "بَابُ خَيْرُ مَتَاعِ الدُّنْيَا الْمَرْأَةُ الصَّالِحَةُ " },
{id: "4714  ", parent: "3977", text: "بَابُ الْوَصِيَّةِ بِالنِّسَاءِ " },
{id: "4715  ", parent: "3977", text: "بَابُ لَوْلَا حَوَّاءُ لَمْ تَخُنْ أُنْثَى زَوْجَهَا الدَّهْرَ " },
{id: "3978", parent: "كِتَابُ الطَّلَاقِ ", text: "" },
{id: "4716  ", parent: "3978", text: "بَابُ تَحْرِيمِ طَلَاقِ الْحَائِضِ بِغَيْرِ رِضَاهَا، وَأَنَّهُ لَوْ خَالَفَ وَقَعَ الطَّلَاقُ، وَيُؤْمَرُ بِرَجْعَتِهَا " },
{id: "4717  ", parent: "3978", text: "بَابُ طَلَاقِ الثَّلَاثِ " },
{id: "4718  ", parent: "3978", text: "بَابُ وُجُوبِ الْكَفَّارَةِ عَلَى مَنْ حَرَّمَ امْرَأَتَهُ، وَلَمْ يَنْوِ الطَّلَاقَ " },
{id: "4719  ", parent: "3978", text: "بَابُ بَيَانِ أَنَّ تَخْيِيرَ امْرَأَتِهِ لَا يَكُونُ طَلَاقًا إِلَّا بِالنِّيَّةِ " },
{id: "4720  ", parent: "3978", text: "بَابٌ فِي الْإِيلَاءِ، وَاعْتِزَالِ النِّسَاءِ، وَتَخْيِيرِهِنَّ وَقَوْلِهِ تَعَالَى: {وَإِنْ تَظَاهَرَا عَلَيْهِ} [التحريم: 4] " },
{id: "4721  ", parent: "3978", text: "بَابُ الْمُطَلَّقَةِ ثَلَاثًا لَا نَفَقَةَ لَهَا " },
{id: "4722  ", parent: "3978", text: "بَابُ جَوَازِ خُرُوجِ الْمُعْتَدَّةِ الْبَائِنِ، وَالْمُتَوَفَّى عَنْهَا زَوْجُهَا فِي النَّهَارِ لِحَاجَتِهَا " },
{id: "4723  ", parent: "3978", text: "بَابُ انْقِضَاءِ عِدَّةِ الْمُتَوَفَّى عَنْهَا زَوْجُهَا، وَغَيْرِهَا بِوَضْعِ الْحَمْلِ " },
{id: "4724  ", parent: "3978", text: "بَابُ وُجُوبِ الْإِحْدَادِ فِي عِدَّةِ الْوَفَاةِ، وَتَحْرِيمِهِ فِي غَيْرِ ذَلِكَ إِلَّا ثَلَاثَةَ أَيَّامٍ " },
{id: "3979", parent: "كِتَابُ اللِّعَانِ", text: "" },
{id: "4725  ", parent: "3979", text: "كِتَابُ اللِّعَانِ " },
{id: "3980", parent: "كِتَابُ الْعِتْقِ", text: "" },
{id: "4726  ", parent: "3980", text: "كِتَابُ الْعِتْقِ " },
{id: "4727  ", parent: "3980", text: "بَابُ -[1140]- ذِكْرِ سِعَايَةِ الْعَبْدِ " },
{id: "4728  ", parent: "3980", text: "بَابُ إِنَّمَا الْوَلَاءُ لِمَنْ أَعْتَقَ " },
{id: "4729  ", parent: "3980", text: "بَابُ النَّهْيِ عَنْ بَيْعِ الْوَلَاءِ، وَهِبَتِهِ " },
{id: "4730  ", parent: "3980", text: "بَابُ تَحْرِيمِ تَوَلِّي الْعَتِيقِ غَيْرَ مَوَالِيهِ " },
{id: "4731  ", parent: "3980", text: "بَابُ فَضْلِ الْعِتْقِ " },
{id: "4732  ", parent: "3980", text: "بَابُ فَضْلِ عِتْقِ الْوَالِدِ " },
{id: "3981", parent: "كِتَابُ الْبُيُوعِ ", text: "" },
{id: "4733  ", parent: "3981", text: "بَابُ إِبْطَالِ بَيْعِ الْمُلَامَسَةِ وَالْمُنَابَذَةِ " },
{id: "4734  ", parent: "3981", text: "بَابُ بُطْلَانِ بَيْعِ الْحَصَاةِ، وَالْبَيْعِ الَّذِي فِيهِ غَرَرٌ " },
{id: "4735  ", parent: "3981", text: "بَابُ تَحْرِيمِ بَيْعِ حَبَلِ الْحَبَلَةِ " },
{id: "4736  ", parent: "3981", text: "بَابُ تَحْرِيمِ بَيْعِ الرَّجُلِ عَلَى بَيْعِ أَخِيهِ، وَسَوْمِهِ عَلَى سَوْمِهِ، وَتَحْرِيمِ النَّجْشِ، وَتَحْرِيمِ التَّصْرِيَةِ " },
{id: "4737  ", parent: "3981", text: "بَابُ تَحْرِيمِ تَلَقِّي الْجَلَبِ " },
{id: "4738  ", parent: "3981", text: "بَابُ تَحْرِيمِ بَيْعِ الْحَاضِرِ لِلْبَادِي " },
{id: "4739  ", parent: "3981", text: "بَابُ حُكْمِ بَيْعِ الْمُصَرَّاةِ " },
{id: "4740  ", parent: "3981", text: "بَابُ بُطْلَانِ بَيْعِ الْمَبِيعِ قَبْلَ الْقَبْضِ " },
{id: "4741  ", parent: "3981", text: "بَابُ تَحْرِيمِ بَيْعِ صُبْرَةِ التَّمْرِ الْمَجْهُولَةِ الْقَدْرِ بِتَمْرٍ " },
{id: "4742  ", parent: "3981", text: "بَابُ ثُبُوتِ خِيَارِ الْمَجْلِسِ لِلْمُتَبَايِعَيْنِ " },
{id: "4743  ", parent: "3981", text: "بَابُ الصِّدْقِ فِي الْبَيْعِ وَالْبَيَانِ " },
{id: "4744  ", parent: "3981", text: "بَابُ مَنْ يُخْدَعُ فِي الْبَيْعِ " },
{id: "4745  ", parent: "3981", text: "بَابُ النَّهْيِ عَنْ بَيْعِ الثِّمَارِ قَبْلَ بُدُوِّ صَلَاحِهَا بِغَيْرِ شَرْطِ الْقَطْعِ " },
{id: "4746  ", parent: "3981", text: "بَابُ تَحْرِيمِ بَيْعِ الرُّطَبِ بِالتَّمْرِ إِلَّا فِي الْعَرَايَا " },
{id: "4747  ", parent: "3981", text: "بَابُ مَنْ بَاعَ نَخْلًا عَلَيْهَا ثَمَرٌ " },
{id: "4748  ", parent: "3981", text: "بَابُ النَّهْيِ عَنِ الْمُحَاقَلَةِ وَالْمُزَابَنَةِ، وَعَنِ الْمُخَابَرَةِ، وَبَيْعِ الثَّمَرَةِ قَبْلَ بُدُوِّ صَلَاحِهَا، وَعَنْ بَيْعِ الْمُعَاوَمَةِ وَهُوَ بَيْعُ السِّنِينَ " },
{id: "4749  ", parent: "3981", text: "بَابُ كِرَاءِ الْأَرْضِ " },
{id: "4750  ", parent: "3981", text: "بَابُ كِرَاءِ الْأَرْضِ بِالطَّعَامِ " },
{id: "4751  ", parent: "3981", text: "بَابُ كِرَاءِ الْأَرْضِ بِالذَّهَبِ وَالْوَرِقِ " },
{id: "4752  ", parent: "3981", text: "بَابٌ فِي الْمُزَارَعَةِ وَالْمُؤَاجَرَةِ " },
{id: "4753  ", parent: "3981", text: "بَابُ الْأَرْضِ تُمْنَحُ " },
{id: "3982", parent: "كِتَابُ الْمُسَاقَاةِ ", text: "" },
{id: "4754  ", parent: "3982", text: "بَابُ الْمُسَاقَاةِ، وَالْمُعَامَلَةِ بِجُزْءٍ مِنَ الثَّمَرِ وَالزَّرْعِ " },
{id: "4755  ", parent: "3982", text: "بَابُ فَضْلِ الْغَرْسِ وَالزَّرْعِ " },
{id: "4756  ", parent: "3982", text: "بَابُ وَضْعِ الْجَوَائِحِ " },
{id: "4757  ", parent: "3982", text: "بَابُ اسْتِحْبَابِ الْوَضْعِ مِنَ الدَّيْنِ " },
{id: "4758  ", parent: "3982", text: "بَابُ مَنْ أَدْرَكَ مَا بَاعَهُ عِنْدَ الْمُشْتَرِي وَقَدْ أَفْلَسَ فَلَهُ الرُّجُوعُ فِيهِ " },
{id: "4759  ", parent: "3982", text: "بَابُ فَضْلِ إِنْظَارِ الْمُعْسِرِ " },
{id: "4760  ", parent: "3982", text: "بَابُ تَحْرِيمِ مَطْلِ الْغَنِيِّ، وَصِحَّةِ الْحَوَالَةِ، وَاسْتِحْبَابِ قَبُولِهَا إِذَا أُحِيلَ عَلَى مَلِيٍّ " },
{id: "4761  ", parent: "3982", text: "بَابُ تَحْرِيمِ بَيْعِ فَضْلِ الْمَاءِ الَّذِي يَكُونُ بِالْفَلَاةِ وَيُحْتَاجُ إِلَيْهِ لِرَعْيِ الْكَلَأِ، وَتَحْرِيمِ مَنْعِ بَذْلِهِ، وَتَحْرِيمِ بَيْعِ ضِرَابِ الْفَحْلِ " },
{id: "4762  ", parent: "3982", text: "بَابُ تَحْرِيمِ ثَمَنِ الْكَلْبِ، وَحُلْوَانِ الْكَاهِنِ، وَمَهْرِ الْبَغِيِّ، وَالنَّهْيِ عَنْ بَيْعِ السِّنَّوْرِ " },
{id: "4763  ", parent: "3982", text: "بَابُ الْأَمْرِ بِقَتْلِ الْكِلَابِ، وَبَيَانِ نَسْخِهِ، وَبَيَانِ تَحْرِيمِ اقْتِنَائِهَا إِلَّا لِصَيْدٍ، أَوْ زَرْعٍ، أَوْ مَاشِيَةٍ وَنَحْوِ ذَلِكَ " },
{id: "4764  ", parent: "3982", text: "بَابُ حِلِّ أُجْرَةِ الْحِجَامَةِ " },
{id: "4765  ", parent: "3982", text: "بَابُ تَحْرِيمِ بَيْعِ الْخَمْرِ " },
{id: "4766  ", parent: "3982", text: "بَابُ تَحْرِيمِ بَيْعِ الْخَمْرِ، وَالْمَيْتَةِ، وَالْخِنْزِيرِ، وَالْأَصْنَامِ " },
{id: "4767  ", parent: "3982", text: "بَابُ الرِّبَا " },
{id: "4768  ", parent: "3982", text: "بَابُ الصَّرْفِ وَبَيْعِ الذَّهَبِ بِالْوَرِقِ نَقْدًا " },
{id: "4769  ", parent: "3982", text: "بَابُ النَّهْيِ عَنْ بَيْعِ الْوَرِقِ بِالذَّهَبِ دَيْنًا " },
{id: "4770  ", parent: "3982", text: "بَابُ بَيْعِ الْقِلَادَةِ فِيهَا خَرَزٌ وَذَهَبٌ " },
{id: "4771  ", parent: "3982", text: "بَابُ بَيْعِ الطَّعَامِ مِثْلًا بِمِثْلٍ " },
{id: "4772  ", parent: "3982", text: "بَابُ لَعْنِ آكِلِ الرِّبَا وَمُؤْكِلِهِ " },
{id: "4773  ", parent: "3982", text: "بَابُ أَخْذِ الْحَلَالِ وَتَرْكِ الشُّبُهَاتِ " },
{id: "4774  ", parent: "3982", text: "بَابُ بَيْعِ الْبَعِيرِ وَاسْتِثْنَاءِ رُكُوبِهِ " },
{id: "4775  ", parent: "3982", text: "بَابُ مَنِ اسْتَسْلَفَ شَيْئًا فَقَضَى خَيْرًا مِنْهُ، وَخَيْرُكُمْ أَحْسَنُكُمْ قَضَاءً " },
{id: "4776  ", parent: "3982", text: "بَابُ جَوَازِ بَيْعِ الْحَيَوَانِ بِالْحَيَوَانِ مِنْ جِنْسِهِ مُتَفَاضِلًا " },
{id: "4777  ", parent: "3982", text: "بَابُ الرَّهْنِ وَجَوَازِهِ فِي الْحَضَرِ كَالسَّفَرِ " },
{id: "4778  ", parent: "3982", text: "بَابُ السَّلَمِ " },
{id: "4779  ", parent: "3982", text: "بَابُ تَحْرِيمِ الِاحْتِكَارِ فِي الْأَقْوَاتِ " },
{id: "4780  ", parent: "3982", text: "بَابُ النَّهْيِ عَنِ الْحَلِفِ فِي الْبَيْعِ " },
{id: "4781  ", parent: "3982", text: "بَابُ الشُّفْعَةِ " },
{id: "4782  ", parent: "3982", text: "بَابُ غَرْزِ الْخَشَبِ فِي جِدَارِ الْجَارِ " },
{id: "4783  ", parent: "3982", text: "بَابُ تَحْرِيمِ الظُّلْمِ وَغَصْبِ الْأَرْضِ وَغَيْرِهَا " },
{id: "4784  ", parent: "3982", text: "بَابُ قَدْرِ الطَّرِيقِ إِذَا اخْتَلَفُوا فِيهِ " },
{id: "3983", parent: "كِتَابُ الْفَرَائِضِ", text: "" },
{id: "4785  ", parent: "3983", text: "كِتَابُ الْفَرَائِضِ " },
{id: "4786  ", parent: "3983", text: "بَابُ أَلْحِقُوا الْفَرَائِضَ بِأَهْلِهَا، فَمَا بَقِيَ فَلِأَوْلَى رَجُلٍ ذَكَرٍ " },
{id: "4787  ", parent: "3983", text: "بَابُ مِيرَاثِ الْكَلَالَةِ " },
{id: "4788  ", parent: "3983", text: "بَابُ آخِرُ آيَةٍ أُنْزِلَتْ آيَةُ الْكَلَالَةِ " },
{id: "4789  ", parent: "3983", text: "بَابُ مَنْ تَرَكَ مَالًا فَلِوَرَثَتِهِ " },
{id: "3984", parent: "كِتَابُ الْهِبَاتِ ", text: "" },
{id: "4790  ", parent: "3984", text: "كِتَابُ الْهِبَاتِ بَابُ كَرَاهَةِ شِرَاءِ الْإِنْسَانِ مَا تَصَدَّقَ بِهِ مِمَّنْ تُصُدِّقَ عَلَيْهِ " },
{id: "4791  ", parent: "3984", text: "بَابُ تَحْرِيمِ الرُّجُوعِ فِي الصَّدَقَةِ وَالْهِبَةِ بَعْدَ الْقَبْضِ إِلَّا مَا وَهَبَهُ لِوَلَدِهِ وَإِنْ سَفَلَ " },
{id: "4792  ", parent: "3984", text: "بَابُ كَرَاهَةِ تَفْضِيلِ بَعْضِ الْأَوْلَادِ فِي الْهِبَةِ " },
{id: "4793  ", parent: "3984", text: "بَابُ الْعُمْرَى " },
{id: "3985", parent: "كِتَابُ الْوَصِيَّةِ ", text: "" },
{id: "4794  ", parent: "3985", text: "كِتَابُ الْوَصِيَّةِ " },
{id: "4795  ", parent: "3985", text: "بَابُ الْوَصِيَّةِ بِالثُّلُثِ " },
{id: "4796  ", parent: "3985", text: "بَابُ وُصُولِ ثَوَابِ الصَّدَقَاتِ إِلَى الْمَيِّتِ " },
{id: "4797  ", parent: "3985", text: "بَابُ مَا يَلْحَقُ الْإِنْسَانَ مِنَ الثَّوَابِ بَعْدَ وَفَاتِهِ " },
{id: "4798  ", parent: "3985", text: "بَابُ الْوَقْفِ " },
{id: "4799  ", parent: "3985", text: "بَابُ تَرْكِ الْوَصِيَّةِ لِمَنْ لَيْسَ لَهُ شَيْءٌ يُوصِي فِيهِ " },
{id: "3986", parent: "كِتَابُ النَّذْرِ ", text: "" },
{id: "4800  ", parent: "3986", text: "بَابُ الْأَمْرِ بِقَضَاءِ النَّذْرِ " },
{id: "4801  ", parent: "3986", text: "بَابُ النَّهْيِ عَنِ النَّذْرِ وَأَنَّهُ لَا يَرُدُّ شَيْئًا " },
{id: "4802  ", parent: "3986", text: "بَابُ لَا وَفَاءَ لِنَذْرٍ فِي مَعْصِيَةِ اللهِ، وَلَا فِيمَا لَا يَمْلِكُ الْعَبْدُ " },
{id: "4803  ", parent: "3986", text: "بَابُ مَنْ نَذَرَ أَنْ يَمْشِيَ إِلَى الْكَعْبَةِ " },
{id: "4804  ", parent: "3986", text: "بَابٌ فِي كَفَّارَةِ النَّذْرِ " },
{id: "3987", parent: "كِتَابُ الْأَيْمَانِ ", text: "" },
{id: "4805  ", parent: "3987", text: "بَابُ النَّهْيِ عَنِ الْحَلِفِ بِغَيْرِ اللهِ تَعَالَى " },
{id: "4806  ", parent: "3987", text: "بَابُ مَنْ حَلَفَ بِاللَّاتِ وَالْعُزَّى، فَلْيَقُلْ: لَا إِلَهَ إِلَّا اللهُ " },
{id: "4807  ", parent: "3987", text: "بَابُ نَدْبِ مَنْ حَلَفَ يَمِينًا فَرَأَى غَيْرَهَا خَيْرًا مِنْهَا، أَنْ يَأْتِيَ الَّذِي هُوَ خَيْرٌ، وَيُكَفِّرُ عَنْ يَمِينِهِ " },
{id: "4808  ", parent: "3987", text: "بَابُ يَمِينِ الْحَالِفِ عَلَى نِيَّةِ الْمُسْتَحْلِفِ " },
{id: "4809  ", parent: "3987", text: "بَابُ الِاسْتِثْنَاءِ " },
{id: "4810  ", parent: "3987", text: "بَابُ النَّهْيِ عَنِ الْإِصْرَارِ عَلَى الْيَمِينِ، فِيمَا يَتَأَذَّى بِهِ أَهْلُ الْحَالِفِ، مِمَّا لَيْسَ بِحَرَامٍ " },
{id: "4811  ", parent: "3987", text: "بَابُ نَذْرِ الْكَافِرِ وَمَا يَفْعَلُ فِيهِ إِذَا أَسْلَمَ " },
{id: "4812  ", parent: "3987", text: "بَابُ صُحْبَةِ الْمَمَالِيكِ، وَكَفَّارَةِ مَنْ لَطَمَ عَبْدَهُ " },
{id: "4813  ", parent: "3987", text: "بَابُ التَّغْلِيظِ عَلَى مَنْ قَذَفَ مَمْلُوكَهُ بِالزِّنَا " },
{id: "4814  ", parent: "3987", text: "بَابُ إِطْعَامِ الْمَمْلُوكِ مِمَّا يَأْكُلُ، وَإِلْبَاسُهُ مِمَّا يَلْبَسُ، وَلَا يُكَلِّفْهُ مَا يَغْلِبُهُ " },
{id: "4815  ", parent: "3987", text: "بَابُ ثَوَابِ الْعَبْدِ وَأَجْرِهِ إِذَا نَصَحَ لِسَيِّدِهِ وَأَحْسَنَ عِبَادَةَ اللهِ " },
{id: "4816  ", parent: "3987", text: "بَابُ مَنْ أَعْتَقَ شِرْكًا لَهُ فِي عَبْدٍ " },
{id: "4817  ", parent: "3987", text: "بَابُ جَوَازِ بَيْعِ الْمُدَبِّرِ " },
{id: "3988", parent: "كِتَابُ الْقَسَامَةِ وَالْمُحَارِبِينَ وَالْقِصَاصِ وَالدِّيَاتِ ", text: "" },
{id: "4818  ", parent: "3988", text: "بَابُ الْقَسَامَةِ " },
{id: "4819  ", parent: "3988", text: "بَابُ حُكْمِ الْمُحَارِبِينَ وَالْمُرْتَدِينَ " },
{id: "4820  ", parent: "3988", text: "بَابُ ثُبُوتِ الْقِصَاصِ فِي الْقَتْلِ بِالْحَجَرِ وَغَيْرِهِ مِنَ الْمُحَدَّدَاتِ، وَالْمُثَقَّلَاتِ، وَقَتْلِ الرَّجُلِ بِالْمَرْأَةِ " },
{id: "4821  ", parent: "3988", text: "بَابُ الصَّائِلِ عَلَى نَفْسِ الْإِنْسَانِ أَوْ عُضْوِهِ، إِذَا دَفَعَهُ الْمَصُولِ عَلَيْهِ، فَأَتْلَفَ نَفْسَهُ أَوْ عُضْوَهُ، لَا ضَمَانَ عَلَيْهِ " },
{id: "4822  ", parent: "3988", text: "بَابُ إِثْبَاتِ الْقِصَاصِ فِي الْأَسْنَانِ، وَمَا فِي مَعْنَاهَا " },
{id: "4823  ", parent: "3988", text: "بَابُ مَا يُبَاحُ بِهِ دَمُ الْمُسْلِمِ " },
{id: "4824  ", parent: "3988", text: "بَابُ بَيَانِ إِثْمِ مَنْ سَنَّ الْقَتْلَ " },
{id: "4825  ", parent: "3988", text: "بَابُ الْمُجَازَاةِ بِالدِّمَاءِ فِي الْآخِرَةِ، وَأَنَّهَا أَوَّلُ مَا يُقْضَى فِيهِ بَيْنَ النَّاسِ يَوْمَ الْقِيَامَةِ " },
{id: "4826  ", parent: "3988", text: "بَابُ تَغْلِيظِ تَحْرِيمِ الدِّمَاءِ وَالْأَعْرَاضِ وَالْأَمْوَالِ " },
{id: "4827  ", parent: "3988", text: "بَابُ صِحَّةِ الْإِقْرَارِ بِالْقَتْلِ، وَتَمْكِينِ وَلِيِّ الْقَتِيلِ مِنَ الْقِصَاصِ، وَاسْتِحْبَابِ طَلَبِ الْعَفْوِ مِنْهُ " },
{id: "4828  ", parent: "3988", text: "بَابُ دِيَةِ الْجَنِينِ، وَوُجُوبِ الدِّيَةِ فِي قَتْلِ الْخَطَإِ، وَشِبْهِ الْعَمْدِ عَلَى عَاقِلَةِ الْجَانِي " },
{id: "3989", parent: "كِتَابُ الْحُدُودِ ", text: "" },
{id: "4829  ", parent: "3989", text: "بَابُ حَدِّ السَّرِقَةِ وَنِصَابِهَا " },
{id: "4830  ", parent: "3989", text: "بَابُ قَطْعِ السَّارِقِ الشَّرِيفِ وَغَيْرِهِ، وَالنَّهْيِ عَنِ الشَّفَاعَةِ فِي الْحُدُودِ " },
{id: "4831  ", parent: "3989", text: "بَابُ حَدِّ الزِّنَى " },
{id: "4832  ", parent: "3989", text: "بَابُ رَجْمِ الثَّيِّبِ فِي الزِّنَى " },
{id: "4833  ", parent: "3989", text: "بَابُ مَنِ اعْتَرَفَ عَلَى نَفْسِهِ بِالزِّنَى " },
{id: "4834  ", parent: "3989", text: "بَابُ رَجْمِ الْيَهُودِ أَهْلِ الذِّمَّةِ فِي الزِّنَى " },
{id: "4835  ", parent: "3989", text: "بَابُ تَأْخِيرِ الْحَدِّ عَنِ النُّفَسَاءِ " },
{id: "4836  ", parent: "3989", text: "بَابُ حَدِّ الْخَمْرِ " },
{id: "4837  ", parent: "3989", text: "بَابُ قَدْرِ أَسْوَاطِ التَّعْزِيرِ " },
{id: "4838  ", parent: "3989", text: "بَابُ الْحُدُودُ كَفَّارَاتٌ لِأَهْلِهَا " },
{id: "4839  ", parent: "3989", text: "بَابُ جُرْحُ الْعَجْمَاءِ، وَالْمَعْدِنِ، وَالْبِئْرِ جُبَارٌ " },
{id: "3990", parent: "كِتَابُ الْأَقْضِيَةِ ", text: "" },
{id: "4840  ", parent: "3990", text: "بَابُ الْيَمِينِ عَلَى الْمُدَّعَى عَلَيْهِ " },
{id: "4841  ", parent: "3990", text: "بَابُ الْقَضَاءِ بِالْيَمِينِ وَالشَّاهِدِ " },
{id: "4842  ", parent: "3990", text: "بَابُ الْحُكْمِ بِالظَّاهِرِ، وَاللَّحْنِ بِالْحُجَّةِ " },
{id: "4843  ", parent: "3990", text: "بَابُ قَضِيَّةِ هِنْدٍ " },
{id: "4844  ", parent: "3990", text: "بَابُ النَّهْيِ عَنْ كَثْرَةِ الْمَسَائِلِ مِنْ غَيْرِ حَاجَةٍ، وَالنَّهْيِ عَنْ مَنْعٍ وَهَاتِ، وَهُوَ الِامْتِنَاعُ مِنْ أَدَاءِ حَقٍّ لَزِمَهُ، أَوْ طَلَبِ مَا لَا يَسْتَحِقُّهُ " },
{id: "4845  ", parent: "3990", text: "بَابُ بَيَانِ أَجْرِ الْحَاكِمِ إِذَا اجْتَهَدَ فَأَصَابَ، أَوْ أَخْطَأَ " },
{id: "4846  ", parent: "3990", text: "بَابُ كَرَاهَةِ قَضَاءِ الْقَاضِي وَهُوَ غَضْبَانُ " },
{id: "4847  ", parent: "3990", text: "بَابُ نَقْضِ الْأَحْكَامِ الْبَاطِلَةِ، وَرَدِّ مُحْدَثَاتِ الْأُمُورِ " },
{id: "4848  ", parent: "3990", text: "بَابُ بَيَانِ خَيْرِ الشُّهُودِ " },
{id: "4849  ", parent: "3990", text: "بَابُ بَيَانِ اخْتِلَافِ الْمُجْتَهِدِينَ " },
{id: "4850  ", parent: "3990", text: "بَابُ اسْتِحْبَابِ إِصْلَاحِ الْحَاكِمِ بَيْنَ الْخَصْمَيْنِ " },
{id: "3991", parent: "كِتَابُ اللُّقَطَةِ ", text: "" },
{id: "4851  ", parent: "3991", text: "كِتَابُ اللُّقَطَةِ " },
{id: "4852  ", parent: "3991", text: "بَابٌ -[1351]- فِي لُقَطَةِ الْحَاجِّ " },
{id: "4853  ", parent: "3991", text: "بَابُ تَحْرِيمِ حَلْبِ الْمَاشِيَةِ بِغَيْرِ إِذْنِ مَالِكِهَا " },
{id: "4854  ", parent: "3991", text: "بَابُ الضِّيَافَةِ وَنَحْوِهَا " },
{id: "4855  ", parent: "3991", text: "بَابُ اسْتِحْبَابِ الْمُؤَاسَاةِ بِفُضُولِ الْمَالِ " },
{id: "4856  ", parent: "3991", text: "بَابُ اسْتِحْبَابِ خَلْطِ الْأَزْوَادِ إِذَا قَلَّتْ، وَالْمُؤَاسَاةِ فِيهَا " },
{id: "3992", parent: "كِتَابُ الْجِهَادِ وَالسِّيَرِ ", text: "" },
{id: "4857  ", parent: "3992", text: "بَابُ جَوَازِ الْإِغَارَةِ عَلَى الْكُفَّارِ الَّذِينَ بَلَغَتْهُمْ دَعْوَةُ الْإِسْلَامِ، مِنْ غَيْرِ تَقَدُّمِ الْإِعْلَامِ بِالْإِغَارَةِ " },
{id: "4858  ", parent: "3992", text: "بَابُ تَأْمِيرِ الْإِمَامِ الْأُمَرَاءَ عَلَى الْبُعُوثِ، وَوَصِيَّتِهِ إِيَّاهُمْ بِآدَابِ الْغَزْوِ وَغَيْرِهَا " },
{id: "4859  ", parent: "3992", text: "بَابٌ فِي الْأَمْرِ بِالتَّيْسِيرِ، وَتَرْكِ التَّنْفِيرِ " },
{id: "4860  ", parent: "3992", text: "بَابُ تَحْرِيمِ الْغَدْرِ " },
{id: "4861  ", parent: "3992", text: "بَابُ جَوَازِ الْخِدَاعِ فِي الْحَرْبِ " },
{id: "4862  ", parent: "3992", text: "بَابُ كَرَاهَةِ تَمَنِّي لِقَاءِ الْعَدُوِّ، وَالْأَمْرِ بِالصَّبْرِ عِنْدَ اللِّقَاءِ " },
{id: "4863  ", parent: "3992", text: "بَابُ اسْتِحْبَابِ الدُّعَاءِ بِالنَّصْرِ عِنْدَ لِقَاءِ الْعَدُوِّ " },
{id: "4864  ", parent: "3992", text: "بَابُ تَحْرِيمِ قَتْلِ النِّسَاءِ وَالصِّبْيَانِ فِي الْحَرْبِ " },
{id: "4865  ", parent: "3992", text: "بَابُ جَوَازِ قَتْلِ النِّسَاءِ وَالصِّبْيَانِ فِي الْبَيَاتِ مِنْ غَيْرِ تَعَمُّدٍ " },
{id: "4866  ", parent: "3992", text: "بَابُ جَوَازِ قَطْعِ أَشْجَارِ الْكُفَّارِ وَتَحْرِيقِهَا " },
{id: "4867  ", parent: "3992", text: "بَابُ تَحْلِيلِ الْغَنَائِمِ لِهَذِهِ الْأُمَّةِ خَاصَّةً " },
{id: "4868  ", parent: "3992", text: "بَابُ الْأَنْفَالِ " },
{id: "4869  ", parent: "3992", text: "بَابُ اسْتِحْقَاقِ الْقَاتِلِ سَلَبَ الْقَتِيلِ " },
{id: "4870  ", parent: "3992", text: "بَابُ التَّنْفِيلِ، وَفِدَاءِ الْمُسْلِمِينَ بِالْأَسَارَى " },
{id: "4871  ", parent: "3992", text: "بَابُ حُكْمِ الْفَيْءِ " },
{id: "4872  ", parent: "3992", text: "بَابُ قَوْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «لَا نُورَثُ مَا تَرَكْنَا فَهُوَ صَدَقَةٌ» " },
{id: "4873  ", parent: "3992", text: "بَابُ كَيْفِيَّةِ قِسْمَةِ الْغَنِيمَةِ بَيْنَ الْحَاضِرِينَ " },
{id: "4874  ", parent: "3992", text: "بَابُ الْإِمْدَادِ بِالْمَلَائِكَةِ فِي غَزْوَةِ بَدْرٍ، وَإِبَاحَةِ الْغَنَائِمِ " },
{id: "4875  ", parent: "3992", text: "بَابُ رَبْطِ الْأَسِيرِ وَحَبْسِهِ، وَجَوَازِ الْمَنِّ عَلَيْهِ " },
{id: "4876  ", parent: "3992", text: "بَابُ إِجْلَاءِ الْيَهُودِ مِنَ الْحِجَازِ " },
{id: "4877  ", parent: "3992", text: "بَابُ إِخْرَاجِ الْيَهُودِ، وَالنَّصَارَى مِنْ جَزِيرَةِ الْعَرَبِ " },
{id: "4878  ", parent: "3992", text: "بَابُ جَوَازِ قِتَالِ مَنْ نَقَضَ الْعَهْدَ، وَجَوَازِ إِنْزَالِ أَهْلِ الْحِصْنِ عَلَى حُكْمِ حَاكِمٍ عَدْلٍ أَهْلٍ لِلْحُكْمِ " },
{id: "4879  ", parent: "3992", text: "بَابُ الْمُبَادَرَةِ بِالْغَزْوِ، وَتَقْدِيمِ أَهَمِّ الْأَمْرَيْنِ الْمُتَعَارِضَيْنِ " },
{id: "4880  ", parent: "3992", text: "بَابُ رَدِّ الْمُهَاجِرِينَ إِلَى الْأَنْصَارِ مَنَائِحَهُمْ مِنَ الشَّجَرِ وَالثَّمَرِ حِينَ اسْتَغْنَوْا عَنْهَا بِالْفُتُوحِ " },
{id: "4881  ", parent: "3992", text: "بَابُ جَوَازِ الْأَكْلِ مِنْ طَعَامِ الْغَنِيمَةِ فِي دَارِ الْحَرْبِ " },
{id: "4882  ", parent: "3992", text: "بَابُ كِتَابِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ إِلَى هِرَقْلَ يَدْعُوهُ إِلَى الْإِسْلَامِ " },
{id: "4883  ", parent: "3992", text: "بَابُ كُتُبِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ إِلَى مُلُوكِ الْكُفَّارِ يَدْعُوهُمْ إِلَى اللهِ عَزَّ وَجَلَّ " },
{id: "4884  ", parent: "3992", text: "بَابٌ فِي غَزْوَةِ حُنَيْنٍ " },
{id: "4885  ", parent: "3992", text: "بَابُ غَزْوَةِ الطَّائِفِ " },
{id: "4886  ", parent: "3992", text: "بَابُ غَزْوَةِ بَدْرٍ " },
{id: "4887  ", parent: "3992", text: "بَابُ فَتْحِ مَكَّةَ " },
{id: "4888  ", parent: "3992", text: "بَابُ إِزَالَةِ الْأَصْنَامِ مِنْ حَوْلِ الْكَعْبَةِ " },
{id: "4889  ", parent: "3992", text: "بَابُ لَا يُقْتَلُ قُرَشِيٌّ صَبْرًا بَعْدَ الْفَتْحِ " },
{id: "4890  ", parent: "3992", text: "بَابُ صُلْحِ الْحُدَيْبِيَةِ فِي الْحُدَيْبِيَةِ " },
{id: "4891  ", parent: "3992", text: "بَابُ الْوَفَاءِ بِالْعَهْدِ " },
{id: "4892  ", parent: "3992", text: "بَابُ غَزْوَةِ الْأَحْزَابِ " },
{id: "4893  ", parent: "3992", text: "بَابُ غَزْوَةِ أُحُدٍ " },
{id: "4894  ", parent: "3992", text: "بَابُ اشْتِدَادِ غَضَبِ اللهِ عَلَى مَنْ قَتَلَهُ رَسُولُ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4895  ", parent: "3992", text: "بَابُ مَا لَقِيَ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ مِنْ أَذَى الْمُشْرِكِينَ وَالْمُنَافِقِينَ " },
{id: "4896  ", parent: "3992", text: "بَابٌ فِي دُعَاءِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ إِلَى اللهِ، وَصَبْرِهِ عَلَى أَذَى الْمُنَافِقِينَ " },
{id: "4897  ", parent: "3992", text: "بَابُ قَتْلِ أَبِي جَهْلٍ " },
{id: "4898  ", parent: "3992", text: "بَابُ قَتْلِ كَعْبِ بْنِ الْأَشْرَفِ طَاغُوتِ الْيَهُودِ " },
{id: "4899  ", parent: "3992", text: "بَابُ غَزْوَةِ خَيْبَرَ " },
{id: "4900  ", parent: "3992", text: "بَابُ غَزْوَةِ الْأَحْزَابِ وَهِيَ الْخَنْدَقُ " },
{id: "4901  ", parent: "3992", text: "بَابُ غَزْوَةِ ذِي قَرَدٍ وَغَيْرِهَا " },
{id: "4902  ", parent: "3992", text: "بَابُ قَوْلِ اللهِ تَعَالَى: {وَهُوَ الَّذِي كَفَّ أَيْدِيَهُمْ عَنْكُمْ} [الفتح: 24] الْآيَةَ " },
{id: "4903  ", parent: "3992", text: "بَابُ غَزْوَةِ النِّسَاءِ مَعَ الرِّجَالِ " },
{id: "4904  ", parent: "3992", text: "بَابُ النِّسَاءِ الْغَازِيَاتِ يُرْضَخُ لَهُنَّ وَلَا يُسْهَمُ، وَالنَّهْيِ عَنْ قَتْلِ صِبْيَانِ أَهْلِ الْحَرْبِ " },
{id: "4905  ", parent: "3992", text: "بَابُ عَدَدِ غَزَوَاتِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4906  ", parent: "3992", text: "بَابُ غَزْوَةِ ذَاتِ الرِّقَاعِ " },
{id: "4907  ", parent: "3992", text: "بَابُ كَرَاهَةِ الِاسْتِعَانَةِ فِي الْغَزْوِ بِكَافِرٍ " },
{id: "3993", parent: "كِتَابُ الْإِمَارَةِ ", text: "" },
{id: "4908  ", parent: "3993", text: "بَابُ النَّاسُ تَبَعٌ لِقُرَيْشٍ، وَالْخِلَافَةُ فِي قُرَيْشٍ " },
{id: "4909  ", parent: "3993", text: "بَابُ الِاسْتِخْلَافِ وَتَرْكِهِ " },
{id: "4910  ", parent: "3993", text: "بَابُ النَّهْيِ عَنْ طَلَبِ الْإِمَارَةِ وَالْحِرْصِ عَلَيْهَا " },
{id: "4911  ", parent: "3993", text: "بَابُ كَرَاهَةِ الْإِمَارَةِ بِغَيْرِ ضَرُورَةٍ " },
{id: "4912  ", parent: "3993", text: "بَابُ فَضِيلَةِ الْإِمَامِ الْعَادِلِ، وَعُقُوبَةِ الْجَائِرِ، وَالْحَثِّ عَلَى الرِّفْقِ بِالرَّعِيَّةِ، وَالنَّهْيِ عَنْ إِدْخَالِ الْمَشَقَّةِ عَلَيْهِمْ " },
{id: "4913  ", parent: "3993", text: "بَابُ غِلَظِ تَحْرِيمِ الْغُلُولِ " },
{id: "4914  ", parent: "3993", text: "بَابُ تَحْرِيمِ هَدَايَا الْعُمَّالِ " },
{id: "4915  ", parent: "3993", text: "بَابُ وُجُوبِ طَاعَةِ الْأُمَرَاءِ فِي غَيْرِ مَعْصِيَةٍ، وَتَحْرِيمِهَا فِي الْمَعْصِيَةِ " },
{id: "4916  ", parent: "3993", text: "بَابٌ فِي الْإِمَامِ إِذَا أَمَرَ بِتَقْوَى اللهِ وَعَدَلَ كَانَ لَهُ أَجْرٌ " },
{id: "4917  ", parent: "3993", text: "بَابُ الْأَمْرِ بالْوَفَاءِ بِبَيْعَةِ الْخُلَفَاءِ، الْأَوَّلِ فَالْأَوَّلِ " },
{id: "4918  ", parent: "3993", text: "بَابُ الْأَمْرِ بِالصَّبْرِ عِنْدَ ظُلْمِ الْوُلَاةِ وَاسْتِئْثَارِهِمْ " },
{id: "4919  ", parent: "3993", text: "بَابٌ فِي طَاعَةِ الْأُمَرَاءِ وَإِنْ مَنَعُوا الْحُقُوقَ " },
{id: "4920  ", parent: "3993", text: "بَابُ الْأَمْرِ بِلُزُومِ الْجَمَاعَةِ عِنْدَ ظُهُورِ الْفِتَنِ وتحذير الدعاة إلى الكفر " },
{id: "4921  ", parent: "3993", text: "بَابُ حُكْمِ مَنْ فَرَّقَ أَمْرَ الْمُسْلِمِينَ وَهُوَ مُجْتَمِعٌ " },
{id: "4922  ", parent: "3993", text: "بَابُ إِذَا بُويِعَ لِخَلِيفَتَيْنِ " },
{id: "4923  ", parent: "3993", text: "بَابُ وُجُوبِ الْإِنْكَارِ عَلَى الْأُمَرَاءِ فِيمَا يُخَالِفُ الشَّرْعَ، وَتَرْكِ قِتَالِهِمْ مَا صَلَّوْا، وَنَحْوِ ذَلِكَ " },
{id: "4924  ", parent: "3993", text: "بَابُ خِيَارِ الْأَئِمَّةِ وَشِرَارِهِمْ " },
{id: "4925  ", parent: "3993", text: "بَابُ اسْتِحْبَابِ مُبَايَعَةِ الْإِمَامِ الْجَيْشَ عِنْدَ إِرَادَةِ الْقِتَالِ، وَبَيَانِ بَيْعَةِ الرِّضْوَانِ تَحْتَ الشَّجَرَةِ " },
{id: "4926  ", parent: "3993", text: "بَابُ تَحْرِيمِ رُجُوعِ الْمُهَاجِرِ إِلَى اسْتِيطَانِ وَطَنِهِ " },
{id: "4927  ", parent: "3993", text: "بَابُ الْمُبَايَعَةِ بَعْدَ فَتْحِ مَكَّةَ عَلَى الْإِسْلَامِ وَالْجِهَادِ وَالْخَيْرِ، وَبَيَانِ مَعْنَى لَا هِجْرَةَ بَعْدَ الْفَتْحِ " },
{id: "4928  ", parent: "3993", text: "بَابُ كَيْفِيَّةِ بَيْعَةِ النِّسَاءِ " },
{id: "4929  ", parent: "3993", text: "بَابُ الْبَيْعَةِ عَلَى السَّمْعِ وَالطَّاعَةِ فِيمَا اسْتَطَاعَ " },
{id: "4930  ", parent: "3993", text: "بَابُ بَيَانِ سِنِّ الْبُلُوغِ " },
{id: "4931  ", parent: "3993", text: "بَابُ النَّهْيِ أَنْ يُسَافَرَ بِالْمُصْحَفِ إِلَى أَرْضِ الْكُفَّارِ إِذَا خِيفَ وُقُوعُهُ بِأَيْدِيهِمْ " },
{id: "4932  ", parent: "3993", text: "بَابُ الْمُسَابَقَةِ بَيْنَ الْخَيْلِ وَتَضْمِيرِهَا " },
{id: "4933  ", parent: "3993", text: "بَابُ الْخَيْلُ فِي نَوَاصِيهَا الْخَيْرُ إِلَى يَوْمِ الْقِيَامَةِ " },
{id: "4934  ", parent: "3993", text: "بَابُ مَا يُكْرَهُ مِنْ صِفَاتِ الْخَيْلِ " },
{id: "4935  ", parent: "3993", text: "بَابُ فَضْلِ الْجِهَادِ وَالْخُرُوجِ فِي سَبِيلِ اللهِ " },
{id: "4936  ", parent: "3993", text: "بَابُ فَضْلِ الشَّهَادَةِ فِي سَبِيلِ اللهِ تَعَالَى " },
{id: "4937  ", parent: "3993", text: "بَابُ فَضْلِ الْغَدْوَةِ وَالرَّوْحَةِ فِي سَبِيلِ اللهِ " },
{id: "4938  ", parent: "3993", text: "بَابُ بَيَانِ مَا أَعَدَّهُ اللهُ تَعَالَى لِلْمُجَاهِدِ فِي الْجَنَّةِ مِنَ الدَّرَجَاتِ " },
{id: "4939  ", parent: "3993", text: "بَابُ مَنْ قُتِلَ فِي سَبِيلِ اللهِ كُفِّرَتْ خَطَايَاهُ إِلَّا الدَّيْنَ " },
{id: "4940  ", parent: "3993", text: "بَابُ بَيَانِ أَنَّ أَرْوَاحَ الشُّهَدَاءِ فِي الْجَنَّةِ، وَأَنَّهُمْ أَحْيَاءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَ " },
{id: "4941  ", parent: "3993", text: "بَابُ فَضْلِ الْجِهَادِ وَالرِّبَاطِ " },
{id: "4942  ", parent: "3993", text: "بَابُ بَيَانِ الرَّجُلَيْنِ يَقْتُلُ أَحَدُهُمَا الْآخَرَ يَدْخُلَانِ الْجَنَّةَ " },
{id: "4943  ", parent: "3993", text: "بَابُ مَنْ قَتَلَ كَافِرًا ثُمَّ أَسْلَمَ " },
{id: "4944  ", parent: "3993", text: "بَابُ فَضْلِ الصَّدَقَةِ فِي سَبِيلِ اللهِ وَتَضْعِيفِهَا " },
{id: "4945  ", parent: "3993", text: "بَابُ فَضْلِ إِعَانَةِ الْغَازِي فِي سَبِيلِ اللهِ بِمَرْكُوبٍ وَغَيْرِهِ، وَخِلَافَتِهِ فِي أَهْلِهِ بِخَيْرٍ " },
{id: "4946  ", parent: "3993", text: "بَابُ حُرْمَةِ نِسَاءِ الْمُجَاهِدِينَ، وَإِثْمِ مَنْ خَانَهُمْ فِيهِنَّ " },
{id: "4947  ", parent: "3993", text: "بَابُ سُقُوطِ فَرْضِ الْجِهَادِ عَنِ الْمَعْذُورِينَ " },
{id: "4948  ", parent: "3993", text: "بَابُ ثُبُوتِ الْجَنَّةِ لِلشَّهِيدِ " },
{id: "4949  ", parent: "3993", text: "بَابُ مَنْ قَاتَلَ لِتَكُونَ كَلِمَةُ اللهِ هِيَ الْعُلْيَا فَهُوَ فِي سَبِيلِ اللهِ " },
{id: "4950  ", parent: "3993", text: "بَابُ مَنْ قَاتَلَ لِلرِّيَاءِ وَالسُّمْعَةِ اسْتَحَقَّ النَّارَ " },
{id: "4951  ", parent: "3993", text: "بَابُ بَيَانِ قَدْرِ ثَوَابِ مَنْ غَزَا فَغَنِمَ، وَمَنْ لَمْ يَغْنَمْ " },
{id: "4952  ", parent: "3993", text: "بَابُ قَوْلِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «إِنَّمَا الْأَعْمَالُ بِالنِّيَّةِ»، وَأَنَّهُ يَدْخُلُ فِيهِ الْغَزْوُ وَغَيْرُهُ مِنَ الْأَعْمَالِ " },
{id: "4953  ", parent: "3993", text: "بَابُ اسْتِحْبَابِ طَلَبِ الشَّهَادَةِ فِي سَبِيلِ اللهِ تَعَالَى " },
{id: "4954  ", parent: "3993", text: "بَابُ ذَمِّ مَنْ مَاتَ، وَلَمْ يَغْزُ، وَلَمْ يُحَدِّثْ نَفْسَهُ بِالْغَزْوِ " },
{id: "4955  ", parent: "3993", text: "بَابُ ثَوَابِ مَنْ حَبَسَهُ عَنِ الْغَزْوِ مَرَضٌ أَوْ عُذْرٌ آخَرُ " },
{id: "4956  ", parent: "3993", text: "بَابُ فَضْلِ الْغَزْوِ فِي الْبَحْرِ " },
{id: "4957  ", parent: "3993", text: "بَابُ فَضْلِ الرِّبَاطِ فِي سَبِيلِ اللهِ عَزَّ وَجَلَّ " },
{id: "4958  ", parent: "3993", text: "بَابُ بَيَانِ الشُّهَدَاءِ " },
{id: "4959  ", parent: "3993", text: "بَابُ فَضْلِ الرَّمْيِ وَالْحَثِّ عَلَيْهِ، وَذَمِّ مَنْ عَلِمَهُ ثُمَّ نَسِيَهُ " },
{id: "4960  ", parent: "3993", text: "بَابُ قَوْلِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «لَا تَزَالُ طَائِفَةٌ مِنْ أُمَّتِي ظَاهِرِينَ عَلَى الْحَقِّ لَا يَضُرُّهُمْ مَنْ خَالَفَهُمْ» " },
{id: "4961  ", parent: "3993", text: "بَابُ مُرَاعَاةِ مَصْلَحَةِ الدَّوَابِّ فِي السَّيْرِ، وَالنَّهْيِ عَنِ التَّعْرِيسِ فِي الطَّرِيقِ " },
{id: "4962  ", parent: "3993", text: "بَابُ السَّفَرُ قِطْعَةٌ مِنَ الْعَذَابِ، وَاسْتِحْبَابِ تَعْجِيلِ الْمُسَافِرِ إِلَى أَهْلِهِ بَعْدَ قَضَاءِ شُغْلِهِ " },
{id: "4963  ", parent: "3993", text: "بَابُ كَرَاهَةِ الطُّرُوقِ، وَهُوَ الدُّخُولُ لَيْلًا، لِمَنْ وَرَدَ مِنْ سَفَرٍ " },
{id: "3994", parent: "كِتَابُ الصَّيْدِ وَالذَّبَائِحِ وَمَا يُؤْكَلُ مِنَ الْحَيَوَانِ ", text: "" },
{id: "4964  ", parent: "3994", text: "بَابُ الصَّيْدِ بِالْكِلَابِ الْمُعَلَّمَةِ " },
{id: "4965  ", parent: "3994", text: "بَابُ إِذَا غَابَ عَنْهُ الصَّيْدُ ثُمَّ وَجَدَهُ " },
{id: "4966  ", parent: "3994", text: "بَابُ تَحْرِيمِ أَكْلِ كُلِّ ذِي نَابٍ مِنَ السِّبَاعِ، وَكُلِّ ذِي مِخْلَبٍ مِنَ الطَّيْرِ " },
{id: "4967  ", parent: "3994", text: "بَابُ إِبَاحَةِ مَيْتَاتِ الْبَحْرِ " },
{id: "4968  ", parent: "3994", text: "بَابُ تَحْرِيمِ أَكْلِ لَحْمِ الْحُمُرِ الْإِنْسِيَّةِ " },
{id: "4969  ", parent: "3994", text: "بَابٌ فِي أَكْلِ لُحُومِ الْخَيْلِ " },
{id: "4970  ", parent: "3994", text: "بَابُ إِبَاحَةِ الضَّبِّ " },
{id: "4971  ", parent: "3994", text: "بَابُ إِبَاحَةِ الْجَرَادِ " },
{id: "4972  ", parent: "3994", text: "بَابُ إِبَاحَةِ الْأَرْنَبِ " },
{id: "4973  ", parent: "3994", text: "بَابُ إِبَاحَةِ مَا يُسْتَعَانُ بِهِ عَلَى الِاصْطِيَادِ وَالْعَدُوِّ، وَكَرَاهَةِ الْخَذْفِ " },
{id: "4974  ", parent: "3994", text: "بَابُ الْأَمْرِ بِإِحْسَانِ الذَّبْحِ وَالْقَتْلِ، وَتَحْدِيدِ الشَّفْرَةِ " },
{id: "4975  ", parent: "3994", text: "بَابُ النَّهْيِ عَنْ صَبْرِ الْبَهَائِمِ " },
{id: "3995", parent: "كِتَابُ الْأَضَاحِيِّ ", text: "" },
{id: "4976  ", parent: "3995", text: "بَابُ وَقْتِهَا " },
{id: "4977  ", parent: "3995", text: "بَابُ سِنِّ الْأُضْحِيَّةِ " },
{id: "4978  ", parent: "3995", text: "بَابُ اسْتِحْبَابِ الضَّحِيَّةِ، وَذَبْحِهَا مُبَاشَرَةً بِلَا تَوْكِيلٍ، وَالتَّسْمِيَةِ وَالتَّكْبِيرِ " },
{id: "4979  ", parent: "3995", text: "بَابُ جَوَازِ الذَّبْحِ بِكُلِّ مَا أَنْهَرَ الدَّمَ، إِلَّا السِّنَّ، وَالظُّفُرَ، وَسَائِرَ الْعِظَامِ " },
{id: "4980  ", parent: "3995", text: "بَابُ بَيَانِ مَا كَانَ مِنَ النَّهْيِ عَنْ أَكْلِ لُحُومِ الْأَضَاحِيِّ بَعْدَ ثَلَاثٍ فِي أَوَّلِ الْإِسْلَامِ، وَبَيَانِ نَسْخِهِ وَإِبَاحَتِهِ إِلَى مَتَى شَاءَ " },
{id: "4981  ", parent: "3995", text: "بَابُ الْفَرَعِ وَالْعَتِيرَةِ " },
{id: "4982  ", parent: "3995", text: "بَابُ نَهْيِ مَنْ دَخَلَ عَلَيْهِ عَشْرُ ذِي الْحِجَّةِ وَهُوَ مُرِيدُ التَّضْحِيَةِ أَنْ يَأْخُذَ مِنْ شَعْرِهِ، أَوْ أَظْفَارِهِ شَيْئًا " },
{id: "4983  ", parent: "3995", text: "بَابُ تَحْرِيمِ الذَّبْحِ لِغَيْرِ اللهِ تَعَالَى وَلَعْنِ فَاعِلِهِ " },
{id: "3996", parent: "كِتَابُ الْأَشْرِبَةِ ", text: "" },
{id: "4984  ", parent: "3996", text: "بَابُ تَحْرِيمِ الْخَمْرِ، وَبَيَانِ أَنَّهَا تَكُونُ مِنْ عَصِيرِ الْعِنَبِ، وَمِنَ التَّمْرِ وَالْبُسْرِ وَالزَّبِيبِ، وَغَيْرِهَا مِمَّا يُسْكِرُ " },
{id: "4985  ", parent: "3996", text: "بَابُ تَحْرِيمِ تَخْلِيلِ الْخَمْرِ " },
{id: "4986  ", parent: "3996", text: "بَابُ تَحْرِيمِ التَّدَاوِي بِالْخَمْرِ " },
{id: "4987  ", parent: "3996", text: "بَابُ بَيَانِ أَنَّ جَمِيعَ مَا يُنْبَذُ مِمَّا يُتَّخَذُ مِنَ النَّخْلِ وَالْعِنَبِ يُسَمَّى خَمْرًا " },
{id: "4988  ", parent: "3996", text: "بَابُ كَرَاهَةِ انْتِبَاذِ التَّمْرِ وَالزَّبِيبِ مَخْلُوطَيْنِ " },
{id: "4989  ", parent: "3996", text: "بَابُ النَّهْيِ عَنِ الِانْتِبَاذِ فِي الْمُزَفَّتِ وَالدُّبَّاءِ وَالْحَنْتَمِ وَالنَّقِيرِ، وَبَيَانِ أَنَّهُ مَنْسُوخٌ، وَأَنَّهُ الْيَوْمَ حَلَالٌ مَا لَمْ يَصِرْ مُسْكِرًا " },
{id: "4990  ", parent: "3996", text: "بَابُ بَيَانِ أَنَّ كُلَّ مُسْكِرٍ خَمْرٌ وَأَنَّ كُلَّ خَمْرٍ حَرَامٌ " },
{id: "4991  ", parent: "3996", text: "بَابُ عُقُوبَةِ مَنْ شَرِبَ الْخَمْرَ إِذَا لَمْ يَتُبْ مِنْهَا بِمَنْعِهِ إِيَّاهَا فِي الْآخِرَةِ " },
{id: "4992  ", parent: "3996", text: "بَابُ إِبَاحَةِ النَّبِيذِ الَّذِي لَمْ يَشْتَدَّ وَلَمْ يَصِرْ مُسْكِرًا " },
{id: "4993  ", parent: "3996", text: "بَابُ جَوَازِ شُرْبِ اللَّبَنِ " },
{id: "4994  ", parent: "3996", text: "بَابٌ فِي شُرْبِ النَّبِيذِ وَتَخْمِيرِ الْإِنَاءِ " },
{id: "4995  ", parent: "3996", text: "بَابُ الْأَمْرِ بِتَغْطِيَةِ الْإِنَاءِ وَإِيكَاءِ السِّقَاءِ، وَإِغْلَاقِ الْأَبْوَابِ، وَذِكْرِ اسْمِ اللهِ عَلَيْهَا، وَإِطْفَاءِ السِّرَاجِ وَالنَّارِ عِنْدَ النَّوْمِ، وَكَفِّ الصِّبْيَانِ وَالْمَوَاشِي بَعْدَ الْمَغْرِبِ " },
{id: "4996  ", parent: "3996", text: "بَابُ آدَابِ الطَّعَامِ وَالشَّرَابِ وَأَحْكَامِهِمَا " },
{id: "4997  ", parent: "3996", text: "بَابُ كَرَاهِيَةِ الشُّرْبِ قَائِمًا " },
{id: "4998  ", parent: "3996", text: "بَابٌ فِي الشُّرْبِ مِنْ زَمْزَمَ قَائِمًا " },
{id: "4999  ", parent: "3996", text: "بَابُ كَرَاهَةِ التَّنَفُّسِ فِي نَفْسِ الْإِنَاءِ، وَاسْتِحْبَابِ التَّنَفُّسِ ثَلَاثًا خَارِجَ الْإِنَاءِ " },
{id: "5000  ", parent: "3996", text: "بَابُ اسْتِحْبَابِ إِدَارَةِ الْمَاءِ وَاللَّبَنِ وَنَحْوِهِمَا عَنْ يَمِينِ الْمُبْتَدِئِ " },
{id: "5001  ", parent: "3996", text: "بَابُ اسْتِحْبَابِ لَعْقِ الْأَصَابِعِ وَالْقَصْعَةِ، وَأَكْلِ اللُّقْمَةِ السَّاقِطَةِ بَعْدَ مَسْحِ مَا يُصِيبُهَا مِنْ أَذًى، وَكَرَاهَةِ مَسْحِ الْيَدِ قَبْلَ لَعْقِهَا " },
{id: "5002  ", parent: "3996", text: "بَابُ مَا يَفْعَلُ الضَّيْفُ إِذَا تَبِعَهُ غَيْرُ مَنْ دَعَاهُ صَاحِبُ الطَّعَامِ، وَاسْتِحْبَابِ إِذْنِ صَاحِبِ الطَّعَامِ لِلتَّابِعِ " },
{id: "5003  ", parent: "3996", text: "بَابُ جَوَازِ اسْتِتْبَاعِهِ غَيْرَهُ إِلَى دَارِ مَنْ يَثِقُ بِرِضَاهُ بِذَلِكَ، وَبِتَحَقُّقِهِ تَحَقُّقًا تَامًّا، وَاسْتِحْبَابِ الِاجْتِمَاعِ عَلَى الطَّعَامِ " },
{id: "5004  ", parent: "3996", text: "بَابُ جَوَازِ أَكْلِ الْمَرَقِ، وَاسْتِحْبَابِ أَكْلِ الْيَقْطِينِ، وَإِيثَارِ أَهْلِ الْمَائِدَةِ بَعْضِهِمْ بَعْضًا وَإِنْ كَانُوا ضِيفَانًا إِذَا لَمْ يَكْرَهْ ذَلِكَ صَاحِبُ الطَّعَامِ " },
{id: "5005  ", parent: "3996", text: "بَابُ اسْتِحْبَابِ وَضْعِ النَّوَى خَارِجَ التَّمْرِ، وَاسْتِحْبَابِ دُعَاءِ الضَّيْفِ لِأَهْلِ الطَّعَامِ، وَطَلَبِ الدُّعَاءِ مِنَ الضَّيْفِ الصَّالِحِ وَإِجَابَتِهِ لِذَلِكَ " },
{id: "5006  ", parent: "3996", text: "بَابُ أَكْلِ الْقِثَّاءِ بِالرُّطَبِ " },
{id: "5007  ", parent: "3996", text: "بَابُ اسْتِحْبَابِ تَوَاضُعِ الْآكِلِ، وَصِفَةِ قُعُودِهِ " },
{id: "5008  ", parent: "3996", text: "بَابُ نَهْيِ الْآكِلِ مَعَ جَمَاعَةٍ عَنْ قِرَانِ تَمْرَتَيْنِ وَنَحْوِهِمَا فِي لُقْمَةٍ إِلَّا بِإِذْنِ أَصْحَابِهِ " },
{id: "5009  ", parent: "3996", text: "بَابٌ فِي ادِّخَارِ التَّمْرِ وَنَحْوِهِ مِنَ الْأَقْوَاتِ لِلْعِيَالِ " },
{id: "5010  ", parent: "3996", text: "بَابُ فَضْلِ تَمْرِ الْمَدِينَةِ " },
{id: "5011  ", parent: "3996", text: "بَابُ فَضْلِ الْكَمْأَةِ، وَمُدَاوَاةِ الْعَيْنِ بِهَا " },
{id: "5012  ", parent: "3996", text: "بَابُ فَضِيلَةِ الْأَسْوَدِ مِنَ الْكَبَاثِ " },
{id: "5013  ", parent: "3996", text: "بَابُ فَضِيلَةِ الْخَلِّ وَالتَّأَدُّمِ بِهِ " },
{id: "5014  ", parent: "3996", text: "بَابُ إِبَاحَةِ أَكْلِ الثُّومِ، وَأَنَّهُ يَنْبَغِي لِمَنْ أَرَادَ خِطَابَ الْكِبَارِ تَرْكُهُ، وَكَذَا مَا فِي مَعْنَاهُ " },
{id: "5015  ", parent: "3996", text: "بَابُ إِكْرَامِ الضَّيْفِ وَفَضْلِ إِيثَارِهِ " },
{id: "5016 ", parent: "3996", text: "بَابُ فَضِيلَةِ الْمُوَاسَاةِ فِي الطَّعَامِ الْقَلِيلِ، وَأَنَّ طَعَامَ الِاثْنَيْنِ يَكْفِي الثَّلَاثَةَ وَنَحْوِ ذَلِكَ " },
{id: "5017 ", parent: "3996", text: "بَابُ الْمُؤْمِنُ يَأْكُلُ فِي مِعًى وَاحِدٍ، وَالْكَافِرُ يَأْكُلُ فِي سَبْعَةِ أَمْعَاءٍ " },
{id: "5018 ", parent: "3996", text: "بَابُ لَا يَعِيبُ الطَّعَامَ " },
{id: "3997", parent: "كِتَابُ اللِّبَاسِ وَالزِّينَةِ ", text: "" },
{id: "5019 ", parent: "3997", text: "بَابُ تَحْرِيمِ اسْتِعْمَالِ أَوَانِي الذَّهَبِ وَالْفِضَّةِ فِي الشُّرْبِ وَغَيْرِهِ عَلَى الرِّجَالِ وَالنِّسَاءِ " },
{id: "5020 ", parent: "3997", text: "بَابُ تَحْرِيمِ اسْتِعْمَالِ إِنَاءِ الذَّهَبِ وَالْفِضَّةِ عَلَى الرِّجَالِ وَالنِّسَاءِ، وَخَاتَمِ الذَّهَبِ وَالْحَرِيرِ عَلَى الرَّجُلِ، وَإِبَاحَتِهِ لِلنِّسَاءِ، وَإِبَاحَةِ الْعَلَمِ وَنَحْوِهِ لِلرَّجُلِ مَا لَمْ يَزِدْ عَلَى أَرْبَعِ أَصَابِعَ " },
{id: "5021 ", parent: "3997", text: "بَابُ إِبَاحَةِ لُبْسِ الْحَرِيرِ لِلرَّجُلِ إِذَا كَانَ بِهِ حِكَّةٌ أَوْ نَحْوُهَا " },
{id: "5022 ", parent: "3997", text: "بَابُ النَّهْيِ عَنْ لُبْسِ الرَّجُلِ الثَّوْبَ الْمُعَصْفَرَ " },
{id: "5023 ", parent: "3997", text: "بَابُ فَضْلِ لِبَاسِ ثِيَابِ الْحِبَرَةِ " },
{id: "5024 ", parent: "3997", text: "بَابُ التَّوَاضُعِ فِي اللِّبَاسِ، وَالِاقْتِصَارِ عَلَى الْغَلِيظِ مِنْهُ وَالْيَسِيرِ فِي اللِّبَاسِ وَالْفِرَاشِ وَغَيْرِهِمَا، وَجَوَازِ لُبْسِ الثَّوْبِ الشَّعَرِ، وَمَا فِيهِ أَعْلَامٌ " },
{id: "5025 ", parent: "3997", text: "بَابُ جَوَازِ اتِّخَاذِ الْأَنْمَاطِ " },
{id: "5026 ", parent: "3997", text: "بَابُ كَرَاهَةِ مَا زَادَ عَلَى الْحَاجَةِ مِنَ الْفِرَاشِ وَاللِّبَاسِ " },
{id: "5027 ", parent: "3997", text: "بَابُ تَحْرِيمِ جَرِّ الثَّوْبِ خُيَلَاءَ، وَبَيَانِ حَدِّ مَا يَجُوزُ إِرْخَاؤُهُ إِلَيْهِ وَمَا يُسْتَحَبُّ " },
{id: "5028 ", parent: "3997", text: "بَابُ تَحْرِيمِ التَّبَخْتُرِ فِي الْمَشْيِ مَعَ إِعْجَابِهِ بِثِيَابِهِ " },
{id: "5029 ", parent: "3997", text: "بَابُ طَرْحِ خَاتَمِ الذَّهَبِ " },
{id: "5030 ", parent: "3997", text: "بَابُ لُبْسِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ خَاتَمًا مِنْ وَرِقٍ نَقْشُهُ مُحَمَّدٌ رَسُولُ اللهِ، وَلُبْسِ الْخُلَفَاءِ لَهُ مِنْ بَعْدِهِ " },
{id: "5031 ", parent: "3997", text: "بَابٌ فِي اتِّخَاذِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ خَاتَمًا لَمَّا أَرَادَ أَنْ يَكْتُبَ إِلَى الْعَجَمِ " },
{id: "5032 ", parent: "3997", text: "بَابٌ فِي طَرْحِ الْخَوَاتِمِ " },
{id: "5033 ", parent: "3997", text: "بَابٌ فِي خَاتَمِ الْوَرِقِ فَصُّهُ حَبَشِيٌّ " },
{id: "5034 ", parent: "3997", text: "بَابٌ فِي لُبْسِ الْخَاتَمِ فِي الْخِنْصِرِ مِنَ الْيَدِ " },
{id: "5035 ", parent: "3997", text: "بَابٌ فِي النَّهْيِ عَنِ التَّخَتُّمِ فِي الْوُسْطَى وَالَّتِي تَلِيهَا " },
{id: "5036 ", parent: "3997", text: "بَابُ مَا جَاءَ فِي الانْتِعَالِ وَالاسْتِكْثَارِ مِنَ النِّعَالِ " },
{id: "5037 ", parent: "3997", text: "بَابُ إِذَا انْتَعَلَ فَلْيَبْدَأْ بِالْيَمِينِ وَإِذَا خَلَعَ فَلْيَبْدأْ بِالشِّمَالِ " },
{id: "5038 ", parent: "3997", text: "بَابُ اشْتِمَالِ الصَّمَّاءِ وَالِاحْتِبَاءِ فِي ثَوْبٍ وَاحِدٍ " },
{id: "5039 ", parent: "3997", text: "بَابٌ فِي مَنْعِ الِاسْتِلْقَاءِ عَلَى الظَّهْرِ وَوَضْعِ إِحْدَى الرِّجْلَيْنِ عَلَى الْأُخْرَى " },
{id: "5040 ", parent: "3997", text: "بَابٌ فِي إِبَاحَةِ الِاسْتِلْقَاءِ وَوَضْعِ إِحْدَى الرِّجْلَيْنِ عَلَى الْأُخْرَى " },
{id: "5041 ", parent: "3997", text: "بَابُ النَهْيِ عَنِ التَّزَعْفُرِ لِلرِّجَالِ " },
{id: "5042 ", parent: "3997", text: "بَابٌ فِي صَبْغِ الشَّعْرِ وَتَغْيِيرِ الشَّيْبِ " },
{id: "5043 ", parent: "3997", text: "بَابٌ فِي مُخَالَفَةِ الْيَهُودِ فِي الصَّبْغِ " },
{id: "5044 ", parent: "3997", text: "بَابُ لَا تَدْخُلُ الْمَلَائِكَةُ بَيْتًا فِيهِ كَلْبٌ وَلَا صُورَةٌ " },
{id: "5045 ", parent: "3997", text: "بَابُ كَرَاهَةِ الْكَلْبِ وَالْجَرَسِ فِي السَّفَرِ " },
{id: "5046 ", parent: "3997", text: "بَابُ كَرَاهَةِ قِلَادَةِ الْوَتَرِ فِي رَقَبَةِ الْبَعِيرِ " },
{id: "5047 ", parent: "3997", text: "بَابُ النَّهْيِ عَنْ ضَرْبِ الْحَيَوَانِ فِي وَجْهِهِ وَوَسْمِهِ فِيهِ " },
{id: "5048 ", parent: "3997", text: "بَابُ جَوَازِ وَسْمِ الْحَيَوَانِ غَيْرِ الْآدَمِيِّ فِي غَيْرِ الْوَجْهِ، وَنَدْبِهِ فِي نَعَمِ الزَّكَاةِ وَالْجِزْيَةِ " },
{id: "5049 ", parent: "3997", text: "بَابُ كَرَاهَةِ الْقَزَعِ " },
{id: "5050 ", parent: "3997", text: "بَابُ النَّهْيِ عَنِ الْجُلُوسِ فِي الطُّرُقَاتِ وَإِعْطَاءِ الطَّرِيقِ حَقَّهُ " },
{id: "5051 ", parent: "3997", text: "بَابُ تَحْرِيمِ فِعْلِ الْوَاصِلَةِ وَالْمُسْتَوْصِلَةِ وَالْوَاشِمَةِ وَالْمُسْتَوْشِمَةِ وَالنَّامِصَةِ وَالْمُتَنَمِّصَةِ وَالْمُتَفَلِّجَاتِ وَالْمُغَيِّرَاتِ خَلْقِ اللهِ " },
{id: "5052 ", parent: "3997", text: "بَابُ النِّسَاءِ الْكَاسِيَاتِ الْعَارِيَاتِ الْمَائِلَاتِ الْمُمِيلَاتِ " },
{id: "5053 ", parent: "3997", text: "بَابُ النَّهْيِ عَنِ التَّزْوِيرِ فِي اللِّبَاسِ وَغَيْرِهِ وَالتَّشَبُّعِ بِمَا لَمْ يُعْطَ " },
{id: "3998", parent: "كِتَابُ الْآدَابِ ", text: "" },
{id: "5054 ", parent: "3998", text: "بَابُ النَّهْيِ عَنِ التَّكَنِّي بِأَبِي الْقَاسِمِ وَبَيَانِ مَا يُسْتَحَبُّ مِنَ الْأَسْمَاءِ " },
{id: "5055 ", parent: "3998", text: "بَابُ كَرَاهَةِ التَّسْمِيَةِ بِالْأَسْمَاءِ الْقَبِيحَةِ وَبِنَافِعٍ وَنَحْوِهِ " },
{id: "5056 ", parent: "3998", text: "بَابُ اسْتِحْبَابِ تَغْيِيرِ الِاسْمِ الْقَبِيحِ إِلَى حَسَنٍ، وَتَغْيِيرِ اسْمِ بَرَّةَ إِلَى زَيْنَبَ وَجُوَيْرِيَةَ وَنَحْوِهِمَا " },
{id: "5057 ", parent: "3998", text: "بَابُ تَحْرِيمِ التَّسَمِّي بِمَلِكِ الْأَمْلَاكِ، وَبِمَلِكِ الْمُلُوكِ " },
{id: "5058 ", parent: "3998", text: "بَابُ اسْتِحْبَابِ تَحْنِيكِ الْمَوْلُودِ عِنْدَ وِلَادَتِهِ وَحَمْلِهِ إِلَى صَالِحٍ يُحَنِّكُهُ، وَجَوَازِ تَسْمِيَتِهِ يَوْمَ وِلَادَتِهِ، وَاسْتِحْبَابِ التَّسْمِيَةِ بِعَبْدِ اللهِ وَإِبْرَاهِيمَ وَسَائِرِ أَسْمَاءِ الْأَنْبِيَاءِ عَلَيْهِمِ السَّلَامُ " },
{id: "5059 ", parent: "3998", text: "بَابُ جَوَازِ قَوْلِهِ لِغَيْرِ ابْنِهِ: يَا بُنَيَّ وَاسْتِحْبَابِهِ لِلْمُلَاطَفَةِ " },
{id: "5060 ", parent: "3998", text: "بَابُ الِاسْتِئْذَانِ " },
{id: "5061 ", parent: "3998", text: "بَابُ كَرَاهَةِ قَوْلِ الْمُسْتَأْذِنِ أَنَا إِذَا قِيلَ مَنْ هَذَا " },
{id: "5062 ", parent: "3998", text: "بَابُ تَحْرِيمِ النَّظَرِ فِي بَيْتِ غَيْرِهِ " },
{id: "5063 ", parent: "3998", text: "بَابُ نَظَرِ الْفُجَاءَةِ " },
{id: "3999", parent: "كِتَابُ السَّلَامِ ", text: "" },
{id: "5064 ", parent: "3999", text: "بَابُ يُسَلِّمُ الرَّاكِبُ عَلَى الْمَاشِي وَالْقَلِيلُ عَلَى الْكَثِيرِ " },
{id: "5065 ", parent: "3999", text: "بَابُ مِنْ حَقِّ الْجُلُوسِ عَلَى الطَّرِيقِ رَدُّ السَّلَامِ " },
{id: "5066 ", parent: "3999", text: "بَابُ مِنْ حَقِّ الْمُسْلِمِ لِلْمُسْلِمِ رَدُّ السَّلَامِ " },
{id: "5067 ", parent: "3999", text: "بَابُ النَّهْيِ عَنِ ابْتِدَاءِ أَهْلِ الْكِتَابِ بِالسَّلَامِ وَكَيْفَ يُرَدُّ عَلَيْهِمْ " },
{id: "5068 ", parent: "3999", text: "بَابُ اسْتِحْبَابِ السَّلَامِ عَلَى الصِّبْيَانِ " },
{id: "5069 ", parent: "3999", text: "بَابُ جَوَازِ جَعْلِ الْإِذْنِ رَفْعُ حِجَابٍ أَوْ نَحْوِهِ مِنَ الْعَلَامَاتِ " },
{id: "5070 ", parent: "3999", text: "بَابُ إِبَاحَةِ الْخُرُوجِ لِلنِّسَاءِ لِقَضَاءِ حَاجَةِ الْإِنْسَانِ " },
{id: "5071 ", parent: "3999", text: "بَابُ تَحْرِيمِ الْخَلْوَةِ بِالْأَجْنَبِيَّةِ وَالدُّخُولِ عَلَيْهَا " },
{id: "5072 ", parent: "3999", text: "بَابُ بَيَانِ أَنَّهُ يُسْتَحَبُّ لِمَنْ رُئِيَ خَالِيًا بِامْرَأَةٍ وَكَانَتْ زَوْجَتَهُ أَوْ مَحْرَمًا لَهُ أَنْ يَقُولَ هَذِهِ فُلَانَةُ لِيَدْفَعَ ظَنَّ السُّوءِ بِهِ " },
{id: "5073 ", parent: "3999", text: "بَابُ مَنْ أَتَى مَجْلِسًا فَوَجَدَ فُرْجَةً فَجَلَسَ فِيهَا وَإِلَّا وَرَاءَهُمْ " },
{id: "5074 ", parent: "3999", text: "بَابُ تَحْرِيمِ إِقَامَةِ الْإِنْسَانِ مِنْ مَوْضِعِهِ الْمُبَاحِ الَّذِي سَبَقَ إِلَيْهِ " },
{id: "5075 ", parent: "3999", text: "بَابُ إِذَا قَامَ مِنْ مَجْلِسِهِ، ثُمَّ عَادَ فَهُوَ أَحَقُّ بِهِ " },
{id: "5076 ", parent: "3999", text: "بَابُ مَنْعِ الْمُخَنَّثِ مِنَ الدُّخُولِ عَلَى النِّسَاءِ الْأَجَانِبِ " },
{id: "5077 ", parent: "3999", text: "بَابُ جَوَازِ إِرْدَافِ الْمَرْأَةِ الْأَجْنَبِيَّةِ إِذَا أَعْيَتْ فِي الطَّرِيقِ " },
{id: "5078 ", parent: "3999", text: "بَابُ تَحْرِيمِ مُنَاجَاةِ الِاثْنَيْنِ دُونَ الثَّالِثِ بِغَيْرِ رِضَاهُ " },
{id: "5079 ", parent: "3999", text: "بَابُ الطِّبِّ وَالْمَرَضِ وَالرُّقَى " },
{id: "5080 ", parent: "3999", text: "بَابُ السِّحْرِ " },
{id: "5081 ", parent: "3999", text: "بَابُ السُّمِّ " },
{id: "5082 ", parent: "3999", text: "بَابُ اسْتِحْبَابِ رُقْيَةِ الْمَرِيضِ " },
{id: "5083 ", parent: "3999", text: "بَابُ رُقْيَةِ الْمَرِيضِ بِالْمُعَوِّذَاتِ وَالنَّفْثِ " },
{id: "5084 ", parent: "3999", text: "بَابُ اسْتِحْبَابِ الرُّقْيَةِ مِنَ الْعَيْنِ وَالنَّمْلَةِ وَالْحُمَةِ وَالنَّظْرَةِ " },
{id: "5085 ", parent: "3999", text: "بَابُ لَا بَأْسَ بِالرُّقَى مَا لَمْ يَكُنْ فِيهِ شِرْكٌ " },
{id: "5086 ", parent: "3999", text: "بَابُ جَوَازِ أَخْذِ الْأُجْرَةِ عَلَى الرُّقْيَةِ بِالْقُرْآنِ وَالْأَذْكَارِ " },
{id: "5087 ", parent: "3999", text: "بَابُ اسْتِحْبَابِ وَضْعِ يَدِهِ عَلَى مَوْضِعِ الْأَلَمِ مَعَ الدُّعَاءِ " },
{id: "5088 ", parent: "3999", text: "بَابُ التَّعَوُّذِ مِنْ شَيْطَانِ الْوَسْوَسَةِ فِي الصَّلَاةِ " },
{id: "5089 ", parent: "3999", text: "بَابُ لِكُلِّ دَاءٍ دَوَاءٌ وَاسْتِحْبَابِ التَّدَاوِي " },
{id: "5090 ", parent: "3999", text: "بَابُ كَرَاهَةِ التَّدَاوِي بِاللَّدُودِ " },
{id: "5091 ", parent: "3999", text: "بَابُ التَّدَاوِي بِالْعُودِ الْهِنْدِيِّ وَهُوَ الْكُسْتُ " },
{id: "5092 ", parent: "3999", text: "بَابُ التَّدَاوِي بِالْحَبَّةِ السَّوْدَاءِ " },
{id: "5093 ", parent: "3999", text: "بَابُ التَّلْبِينَةُ مُجِمَّةٌ لِفُؤَادِ الْمَرِيضِ " },
{id: "5094 ", parent: "3999", text: "بَابُ التَّدَاوِي بِسَقْيِ الْعَسَلِ " },
{id: "5095 ", parent: "3999", text: "بَابُ الطَّاعُونِ وَالطِّيَرَةِ وَالْكَهَانَةِ وَنَحْوِهَا " },
{id: "5096 ", parent: "3999", text: "بَابُ لَا عَدْوَى، وَلَا طِيَرَةَ، وَلَا هَامَةَ، وَلَا صَفَرَ، وَلَا نَوْءَ، وَلَا غُولَ، وَلَا يُورِدُ مُمْرِضٌ عَلَى مُصِحٍّ " },
{id: "5097 ", parent: "3999", text: "بَابُ الطِّيَرَةِ وَالْفَأْلِ وَمَا يَكُونُ فِيهِ مِنَ الشُّؤْمِ " },
{id: "5098 ", parent: "3999", text: "بَابُ تَحْرِيمِ الْكَهَانَةِ وَإِتْيَانِ الْكُهَّانِ " },
{id: "5099 ", parent: "3999", text: "بَابُ اجْتِنَابِ الْمَجْذُومِ وَنَحْوِهِ " },
{id: "5100 ", parent: "3999", text: "بَابُ قَتْلِ الْحَيَّاتِ وَغَيْرِهَا " },
{id: "5101 ", parent: "3999", text: "بَابُ اسْتِحْبَابِ قَتْلِ الْوَزَغِ " },
{id: "5102 ", parent: "3999", text: "بَابُ النَّهْيِ عَنْ قَتْلِ النَّمْلِ " },
{id: "5103 ", parent: "3999", text: "بَابُ تَحْرِيمِ قَتْلِ الْهِرَّةِ " },
{id: "5104 ", parent: "3999", text: "بَابُ فَضْلِ سَاقِي الْبَهَائِمِ الْمُحْتَرَمَةِ وَإِطْعَامِهَا " },
{id: "4000", parent: "كِتَابُ الْأَلْفَاظِ مِنَ الْأَدَبِ وَغَيْرِهَا ", text: "" },
{id: "5105 ", parent: "4000", text: "بَابُ النَّهْيِ عَنْ سَبِّ الدَّهْرِ " },
{id: "5106 ", parent: "4000", text: "بَابُ كَرَاهَةِ تَسْمِيَةِ الْعِنَبِ كَرْمًا " },
{id: "5107 ", parent: "4000", text: "بَابُ حُكْمِ إِطْلَاقِ لَفْظَةِ الْعَبْدِ، وَالْأَمَةِ، وَالْمَوْلَى، وَالسَّيِّدِ " },
{id: "5108 ", parent: "4000", text: "بَابُ كَرَاهَةِ قَوْلِ الْإِنْسَانِ خَبُثَتْ نَفْسِي " },
{id: "5109 ", parent: "4000", text: "بَابُ اسْتِعْمَالِ الْمِسْكِ وَأَنَّهُ أَطْيَبُ الطِّيبِ وَكَرَاهَةِ رَدِّ الرَّيْحَانِ وَالطِّيبِ " },
{id: "4001", parent: "كِتَابُ الشِّعْرِ ", text: "" },
{id: "5110 ", parent: "4001", text: "كِتَابُ الشِّعْرِ " },
{id: "5111 ", parent: "4001", text: "بَابُ -[1770]- تَحْرِيمِ اللَّعِبِ بِالنَّرْدَشِيرِ " },
{id: "4002", parent: "كِتَابُ الرُّؤْيَا ", text: "" },
{id: "5112 ", parent: "4002", text: "كِتَابُ الرُّؤْيَا " },
{id: "5113 ", parent: "4002", text: "بَابُ قَوْلِ النَّبِيِّ عَلَيْهِ الصَّلَاةُ وَالسَّلَامُ مَنْ رَآنِي فِي الْمَنَامِ فَقَدْ رَآنِي " },
{id: "5114 ", parent: "4002", text: "بَابُ لَا يُخْبِرُ بِتَلَعُّبِ الشَّيْطَانِ بِهِ فِي الْمَنَامِ " },
{id: "5115 ", parent: "4002", text: "بَابٌ فِي تَأْوِيلِ الرُّؤْيَا " },
{id: "5116 ", parent: "4002", text: "بَابُ رُؤْيَا النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "4003", parent: "كِتَابُ الْفَضَائِلِ ", text: "" },
{id: "5117 ", parent: "4003", text: "بَابُ فَضْلِ نَسَبِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ، وَتَسْلِيمِ الْحَجَرِ عَلَيْهِ قَبْلَ النُّبُوَّةِ " },
{id: "5118 ", parent: "4003", text: "بَابُ تَفْضِيلِ نَبِيِّنَا صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ عَلَى جَمِيعِ الْخَلَائِقِ " },
{id: "5119 ", parent: "4003", text: "بَابٌ فِي مُعْجِزَاتِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5120 ", parent: "4003", text: "بَابُ تَوَكُّلِهِ عَلَى اللهِ تَعَالَى، وَعِصْمَةِ اللهِ تَعَالَى لَهُ مِنَ النَّاسِ " },
{id: "5121 ", parent: "4003", text: "بَابُ بَيَانِ مَثَلِ مَا بُعِثَ بِهِ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ مِنَ الْهُدَى وَالْعِلْمِ " },
{id: "5122 ", parent: "4003", text: "بَابُ شَفَقَتِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ عَلَى أُمَّتِهِ وَمُبَالَغَتِهِ فِي تَحْذِيرِهِمْ مِمَّا يَضُرُّهُمْ " },
{id: "5123 ", parent: "4003", text: "بَابُ ذِكْرِ كَوْنِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ خَاتَمَ النَّبِيِّينَ " },
{id: "5124 ", parent: "4003", text: "بَابُ إِذَا أَرَادَ اللهُ تَعَالَى رَحْمَةَ أُمَّةٍ قَبَضَ نَبِيَّهَا قَبْلَهَا " },
{id: "5125 ", parent: "4003", text: "بَابُ إِثْبَاتِ حَوْضِ نَبِيِّنَا صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَصِفَاتِهِ " },
{id: "5126 ", parent: "4003", text: "بَابٌ فِي قِتَالِ جِبْرِيلَ وَمِيكَائِيلَ عَنِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ يَوْمَ أُحُدٍ " },
{id: "5127 ", parent: "4003", text: "بَابٌ فِي شَجَاعَةِ النَّبِيِّ عَلَيْهِ السَّلَامُ وَتَقَدُّمِهِ لِلْحَرْبِ " },
{id: "5128 ", parent: "4003", text: "بَابُ كَانَ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ أَجْوَدَ النَّاسِ بِالْخَيْرِ مِنَ الرِّيحِ الْمُرْسَلَةِ " },
{id: "5129 ", parent: "4003", text: "بَابُ كَانَ رَسُولُ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ أَحْسَنَ النَّاسِ خُلُقًا " },
{id: "5130 ", parent: "4003", text: "بَابُ مَا سُئِلَ رَسُولُ اللهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ شَيْئًا قَطُّ فَقَالَ لَا وَكَثْرَةُ عَطَائِهِ " },
{id: "5131 ", parent: "4003", text: "بَابُ رَحْمَتِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ الصِّبْيَانَ وَالْعِيَالَ وَتَوَاضُعِهِ وَفَضْلِ ذَلِكَ " },
{id: "5132 ", parent: "4003", text: "بَابُ كَثْرَةِ حَيَائِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5133 ", parent: "4003", text: "بَابُ تَبَسُّمِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَحُسْنِ عِشْرَتِهِ " },
{id: "5134 ", parent: "4003", text: "بَابُ فِي رَحْمَةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ لِلنِّسَاءِ وَأَمْرِ السَّوَّاقِ مَطَايَاهُنَّ بِالرِّفْقِ بِهِنَّ " },
{id: "5135 ", parent: "4003", text: "بَابُ قُرْبِ النَّبِيِّ عَلَيْهِ السَّلَامُ مِنَ النَّاسِ وَتَبَرُّكِهِمْ بِهِ " },
{id: "5136 ", parent: "4003", text: "بَابُ مُبَاعَدَتِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ لِلْآثَامِ وَاخْتِيَارِهِ مِنَ الْمُبَاحِ، أَسْهَلَهُ وَانْتِقَامِهِ لِلَّهِ عِنْدَ انْتِهَاكِ حُرُمَاتِهِ " },
{id: "5137 ", parent: "4003", text: "بَابُ طِيبِ رَائِحَةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَلِينِ مَسِّهِ وَالتَّبَرُّكِ بِمَسْحِهِ " },
{id: "5138 ", parent: "4003", text: "بَابُ طِيبِ عَرَقِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَالتَّبَرُّكِ بِهِ " },
{id: "5139 ", parent: "4003", text: "بَابُ عَرَقِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِي الْبَرْدِ وَحِينَ يَأْتِيهِ الْوَحْيُ " },
{id: "5140 ", parent: "4003", text: "بَابٌ فِي سَدْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ شَعْرَهُ وَفَرْقِهِ " },
{id: "5141 ", parent: "4003", text: "بَابٌ فِي صِفَةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَأَنَّهُ كَانَ أَحْسَنَ النَّاسِ وَجْهًا " },
{id: "5142 ", parent: "4003", text: "بَابُ صِفَةِ شَعَرِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5143 ", parent: "4003", text: "بَابٌ فِي صِفَةِ فَمِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَعَيْنَيْهِ وَعَقِبَيْهِ " },
{id: "5144 ", parent: "4003", text: "بَابُ كَانَ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ أَبْيَضَ مَلِيحَ الْوَجْهِ " },
{id: "5145 ", parent: "4003", text: "بَابُ شَيْبِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5146 ", parent: "4003", text: "بَابُ إِثْبَاتِ خَاتَمِ النُّبُوَّةِ، وَصِفَتِهِ، وَمَحَلِّهِ مِنْ جَسَدِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5147 ", parent: "4003", text: "بَابٌ فِي صِفَةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ، وَمَبْعَثِهِ، وَسِنِّهِ " },
{id: "5148 ", parent: "4003", text: "بَابُ كَمْ سِنُّ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ يَوْمَ قُبِضَ؟ " },
{id: "5149 ", parent: "4003", text: "بَابُ كَمْ أَقَامَ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بِمَكَّةَ وَالْمَدِينَةَ؟ " },
{id: "5150 ", parent: "4003", text: "بَابٌ فِي أَسْمَائِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5151 ", parent: "4003", text: "بَابُ عِلْمِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بِاللهِ تَعَالَى وَشِدَّةِ خَشْيَتِهِ " },
{id: "5152 ", parent: "4003", text: "بَابُ وُجُوبِ اتِّبَاعِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5153 ", parent: "4003", text: "بَابُ تَوْقِيرِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ، وَتَرْكِ إِكْثَارِ سُؤَالِهِ عَمَّا لَا ضَرُورَةَ إِلَيْهِ، أَوْ لَا يَتَعَلَّقُ بِهِ تَكْلِيفٌ وَمَا لَا يَقَعُ، وَنَحْوِ ذَلِكَ " },
{id: "5154 ", parent: "4003", text: "بَابُ وُجُوبِ امْتِثَالِ مَا قَالَهُ شَرْعًا، دُونَ مَا ذَكَرَهُ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ مِنْ مَعَايِشِ الدُّنْيَا، عَلَى سَبِيلِ الرَّأْيِ " },
{id: "5155 ", parent: "4003", text: "بَابُ فَضْلِ النَّظَرِ إِلَيْهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ وَتَمَنِّيهِ " },
{id: "5156 ", parent: "4003", text: "بَابُ فَضَائِلِ عِيسَى عَلَيْهِ السَّلَامُ " },
{id: "5157 ", parent: "4003", text: "بَابُ مِنْ فَضَائِلِ إِبْرَاهِيمِ الْخَلِيلِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5158 ", parent: "4003", text: "بَابُ مِنْ فَضَائِلِ مُوسَى صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5159 ", parent: "4003", text: "بَابٌ فِي ذِكْرِ يُونُسَ عَلَيْهِ السَّلَامُ، وَقَوْلِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ «لَا يَنْبَغِي لِعَبْدٍ أَنْ يَقُولَ أَنَا خَيْرٌ مِنْ يُونُسَ بْنِ مَتَّى» " },
{id: "5160 ", parent: "4003", text: "بَابُ مِنْ فَضَائِلِ يُوسُفَ عَلَيْهِ السَّلَامُ " },
{id: "5161 ", parent: "4003", text: "بَابٌ فِي فَضَائِلِ زَكَرِيَّاءَ عَلَيْهِ السَّلَامُ " },
{id: "5162 ", parent: "4003", text: "بَابُ مِنْ فَضَائِلِ الْخَضِرِ عَلَيْهِ السَّلَامُ " },
{id: "4004", parent: "كِتَابُ فَضَائِلِ الصَّحَابَةِ رَضِيَ اللهُ تَعَالَى عَنْهُمْ ", text: "" },
{id: "5163 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي بَكْرٍ الصِّدِّيقِ رَضِيَ اللهُ عَنْهُ " },
{id: "5164 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عُمَرَ رَضِيَ اللهُ تَعَالَى عَنْهُ " },
{id: "5165 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عُثْمَانَ بْنِ عَفَّانَ رَضِيَ اللهُ عَنْهُ " },
{id: "5166 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عَلِيِّ بْنِ أَبِي طَالِبٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5167 ", parent: "4004", text: "بَابٌ فِي فَضْلِ سَعْدِ بْنِ أَبِي وَقَّاصٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5168 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ طَلْحَةَ، وَالزُّبَيْرِ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5169 ", parent: "4004", text: "بَابُ فَضَائِلِ أَبِي عُبَيْدَةَ بْنِ الْجَرَّاحِ رَضِيَ اللهُ تَعَالَى عَنْهُ " },
{id: "5170 ", parent: "4004", text: "بَابُ فَضَائِلِ الْحَسَنِ وَالْحُسَيْنِ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5171 ", parent: "4004", text: "بَابُ فَضَائِلِ أَهْلِ بَيْتِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ " },
{id: "5172 ", parent: "4004", text: "بَابُ فَضَائِلِ زَيْدِ بْنِ حَارِثَةَ وَأُسَامَةَ بْنِ زَيْدٍ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5173 ", parent: "4004", text: "بَابُ فَضَائِلِ عَبْدِ اللهِ بْنِ جَعْفَرٍ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5174 ", parent: "4004", text: "بَابُ فَضَائِلِ خَدِيجَةَ أُمِّ الْمُؤْمِنِينَ رَضِيَ اللهُ تَعَالَى عَنْهَا " },
{id: "5175 ", parent: "4004", text: "بَابٌ فِي فَضْلِ عَائِشَةَ رَضِيَ اللهُ تَعَالَى عَنْهَا " },
{id: "5176 ", parent: "4004", text: "بَابُ ذِكْرِ حَدِيثِ أُمِّ زَرْعٍ " },
{id: "5177 ", parent: "4004", text: "بَابُ فَضَائِلِ فَاطِمَةَ بِنْتِ النَّبِيِّ عَلَيْهَا الصَّلَاةُ وَالسَّلَامُ " },
{id: "5178 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أُمِّ سَلَمَةَ أُمِّ الْمُؤْمِنِينَ رَضِيَ اللهُ عَنْهَا " },
{id: "5179 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ زَيْنَبَ أُمِّ الْمُؤْمِنِينَ، رَضِيَ اللهُ عَنْهَا " },
{id: "5180 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أُمِّ أَيْمَنَ رَضِيَ اللهُ عَنْهَا " },
{id: "5181 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أُمِّ سُلَيْمٍ، أُمِّ أَنَسِ بْنِ مَالِكٍ، وَبِلَالٍ، رَضِيَ اللهُ عَنْهُمَا " },
{id: "5182 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي طَلْحَةَ الْأَنْصَارِيِّ رَضِيَ اللهُ تَعَالَى عَنْهُ " },
{id: "5183 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ بِلَالٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5184 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عَبْدِ اللهِ بْنِ مَسْعُودٍ وَأُمِّهِ رَضِيَ اللهُ تَعَالَى عَنْهُمَا " },
{id: "5185 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أُبَيِّ بْنِ كَعْبٍ، وَجَمَاعَةٍ مِنَ الْأَنْصَارِ رَضِيَ اللهُ تَعَالَى عَنْهُمْ " },
{id: "5186 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ سَعْدِ بْنِ مُعَاذٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5187 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي دُجَانَةَ سِمَاكُ بْنُ خَرَشَةَ رَضِيَ اللهُ تَعَالَى عَنْهُ " },
{id: "5188 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عَبْدِ اللهِ بْنِ عَمْرِو بْنِ حَرَامٍ وَالِدُ جَابِرٍ رَضِيَ اللهُ تَعَالَى عَنْهُمَا " },
{id: "5189 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ جُلَيْبِيبٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5190 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي ذَرٍّ رَضِيَ اللهُ عَنْهُ " },
{id: "5191 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ جَرِيرِ بْنِ عَبْدِ اللهِ رَضِيَ اللهُ تَعَالَى عَنْهُ " },
{id: "5192 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عَبْدِ اللهِ بْنِ عَبَّاسٍ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5193 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عَبْدِ اللهِ بْنِ عُمَرَ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5194 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَنَسِ بْنِ مَالِكٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5195 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ عَبْدِ اللهِ بْنِ سَلَامٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5196 ", parent: "4004", text: "بَابُ فَضَائِلِ حَسَّانَ بْنِ ثَابِتٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5197 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي هُرَيْرَةَ الدَّوْسِيِّ رَضِيَ اللهُ عَنْهُ " },
{id: "5198 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَهْلِ بَدْرٍ رَضِيَ اللهُ عَنْهُمْ وَقِصَّةِ حَاطِبِ بْنِ أَبِي بَلْتَعَةَ " },
{id: "5199 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَصْحَابِ الشَّجَرَةِ أَهْلِ بَيْعَةِ الرُّضْوَانِ رَضِيَ اللهُ عَنْهُمْ " },
{id: "5200 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي مُوسَى وَأَبِي عَامِرٍ الْأَشْعَرِيَّيْنِ رَضِيَ اللهُ عَنْهُمَا " },
{id: "5201 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ الْأَشْعَرِيِّينَ رَضِيَ اللهُ عَنْهُمْ " },
{id: "5202 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أَبِي سُفْيَانَ بْنِ حَرْبٍ رَضِيَ اللهُ عَنْهُ " },
{id: "5203 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ جَعْفَرِ بْنِ أَبِي طَالِبٍ وَأَسْمَاءَ بِنْتِ عُمَيْسٍ وَأَهْلِ سَفِينَتِهِمْ رَضِيَ اللهُ عَنْهُمْ " },
{id: "5204 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ سَلْمَانَ، وَصُهَيْبٍ، وَبِلَالٍ رَضِيَ اللهُ تَعَالَى عَنْهُمْ " },
{id: "5205 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ الْأَنَصَارِ رَضِيَ اللهُ تَعَالَى عَنْهُمْ " },
{id: "5206 ", parent: "4004", text: "بَابٌ فِي خَيْرِ دُورِ الْأَنْصَارِ رَضِيَ اللهُ عَنْهُمْ " },
{id: "5207 ", parent: "4004", text: "بَابٌ فِي حُسْنِ صُحْبَةِ الْأَنْصَارِ رَضِيَ اللهُ عَنْهُمْ " },
{id: "5208 ", parent: "4004", text: "بَابُ دُعَاءِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ لِغِفَارَ وَأَسْلَمَ " },
{id: "5209 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ غِفَارَ، وَأَسْلَمَ، وَجُهَيْنَةَ، وَأَشْجَعَ، وَمُزَيْنَةَ، وَتَمِيمٍ، وَدَوْسٍ، وَطَيِّئٍ " },
{id: "5210 ", parent: "4004", text: "بَابُ خِيَارِ النَّاسِ " },
{id: "5211 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ نِسَاءِ قُرَيْشٍ " },
{id: "5212 ", parent: "4004", text: "بَابُ مُؤَاخَاةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بَيْنَ أَصْحَابِهِ رَضِيَ اللهُ تَعَالَى عَنْهُمْ " },
{id: "5213 ", parent: "4004", text: "بَابُ بَيَانِ أَنَّ بَقَاءَ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ أَمَانٌ لِأَصْحَابِهِ، وَبَقَاءَ أَصْحَابِهِ أَمَانٌ لِلْأُمَّةِ " },
{id: "5214 ", parent: "4004", text: "بَابُ فَضْلِ الصَّحَابَةِ ثُمَّ الَّذِينَ يَلُونَهُمْ ثُمَّ الَّذِينَ يَلُونَهُمْ " },
{id: "5215 ", parent: "4004", text: "بَابُ قَوْلِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: «لَا تَأْتِي مِائَةُ سَنَةٍ، وَعَلَى الْأَرْضِ نَفْسٌ مَنْفُوسَةٌ الْيَوْمَ» " },
{id: "5216 ", parent: "4004", text: "بَابُ تَحْرِيمِ سَبِّ الصَّحَابَةِ رَضِيَ اللهُ عَنْهُمْ " },
{id: "5217 ", parent: "4004", text: "بَابُ مِنْ فَضَائِلِ أُوَيْسٍ الْقَرَنِيِّ رَضِيَ اللهُ عَنْهُ " },
{id: "5218 ", parent: "4004", text: "بَابُ وَصِيَّةِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بِأَهْلِ مِصْرَ " },
{id: "5219 ", parent: "4004", text: "بَابُ فَضْلِ أَهْلِ عُمَانَ " },
{id: "5220 ", parent: "4004", text: "بَابُ ذِكْرِ كَذَّابِ ثَقِيفٍ وَمُبِيرِهَا " },
{id: "5221 ", parent: "4004", text: "بَابُ فَضْلِ فَارِسَ " },
{id: "5222 ", parent: "4004", text: "بَابُ قَوْلِهِ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ: النَّاسُ كَإِبِلٍ مِائَةٍ لَا تَجِدُ فِيهَا رَاحِلَةً " },
{id: "4005", parent: "كِتَابُ الْبِرِّ وَالصِّلَةِ وَالْآدَابِ ", text: "" },
{id: "5223 ", parent: "4005", text: "بَابُ بِرِّ الْوَالِدَيْنِ وَأَنَّهُمَا أَحَقُّ بِهِ " },
{id: "5224 ", parent: "4005", text: "بَابُ تَقْدِيمِ بِرِّ الْوَالِدَيْنِ عَلَى التَّطَوُّعِ بِالصَّلَاةِ وَغَيْرِهَا " },
{id: "5225 ", parent: "4005", text: "بَابُ رَغِمَ أَنْفُ مَنْ أَدْرَكَ أَبَوَيْهِ أَوْ أَحَدَهُمَا عِنْدَ الْكِبَرِ، فَلَمْ يَدْخُلِ الْجَنَّةَ " },
{id: "5226 ", parent: "4005", text: "بَابُ صِلَةِ أَصْدِقَاءِ الْأَبِ وَالْأُمِّ، وَنَحْوِهِمَا " },
{id: "5227 ", parent: "4005", text: "بَابُ تَفْسِيرِ الْبِرِّ وَالْإِثْمِ " },
{id: "5228 ", parent: "4005", text: "بَابُ صِلَةِ الرَّحِمِ وَتَحْرِيمِ قَطِيعَتِهَا " },
{id: "5229 ", parent: "4005", text: "بَابُ النَّهْي عَنِ التَّحَاسُدِ وَالتَّبَاغُضِ وَالتَّدَابُرِ " },
{id: "5230 ", parent: "4005", text: "بَابُ تَحْرِيمِ الْهَجْرِ فَوْقَ ثَلَاثٍ بِلَا عُذْرٍ شَرْعِيٍّ " },
{id: "5231 ", parent: "4005", text: "بَابُ تَحْرِيمِ الظَّنِّ، وَالتَّجَسُّسِ، وَالتَّنَافُسِ، وَالتَّنَاجُشِ وَنَحْوِهَا " },
{id: "5232 ", parent: "4005", text: "بَابُ تَحْرِيمِ ظُلْمِ الْمُسْلِمِ، وَخَذْلِهِ، وَاحْتِقَارِهِ وَدَمِهِ، وَعِرْضِهِ، وَمَالِهِ " },
{id: "5233 ", parent: "4005", text: "بَابُ النَّهْيِ عَنِ الشَّحْنَاءِ وَالتَّهَاجُرِ " },
{id: "5234 ", parent: "4005", text: "بَابٌ فِي فَضْلِ الْحُبِّ فِي اللهِ " },
{id: "5235 ", parent: "4005", text: "بَابُ فَضْلِ عِيَادَةِ الْمَرِيضِ " },
{id: "5236 ", parent: "4005", text: "بَابُ ثَوَابِ الْمُؤْمِنِ فِيمَا يُصِيبُهُ مِنْ مَرَضٍ، أَوْ حُزْنٍ، أَوْ نَحْوِ ذَلِكَ حَتَّى الشَّوْكَةِ يُشَاكُهَا " },
{id: "5237 ", parent: "4005", text: "بَابُ تَحْرِيمِ الظُّلْمِ " },
{id: "5238 ", parent: "4005", text: "بَابُ نَصْرِ الْأَخِ ظَالِمًا أَوْ مَظْلُومًا " },
{id: "5239 ", parent: "4005", text: "بَابُ تَرَاحُمِ الْمُؤْمِنِينَ وَتَعَاطُفِهِمْ وَتَعَاضُدِهِمْ " },
{id: "5240 ", parent: "4005", text: "بَابُ النَّهْيِ عَنِ السِّبَابِ " },
{id: "5241 ", parent: "4005", text: "بَابُ اسْتِحْبَابِ الْعَفْوِ وَالتَّوَاضُعِ " },
{id: "5242 ", parent: "4005", text: "بَابُ تَحْرِيمِ الْغِيبَةِ " },
{id: "5243 ", parent: "4005", text: "بَابُ بِشَارَةِ مَنْ سَتَرَ اللهُ تَعَالَى عَيْبَهُ فِي الدُّنْيَا، بِأَنْ يَسْتُرَ عَلَيْهِ فِي الْآخِرَةِ " },
{id: "5244 ", parent: "4005", text: "بَابُ مُدَارَاةِ مَنْ يُتَّقَى فُحْشُهُ " },
{id: "5245 ", parent: "4005", text: "بَابُ فَضْلِ الرِّفْقِ " },
{id: "5246 ", parent: "4005", text: "بَابُ النَّهْيِ عَنْ لَعْنِ الدَّوَابِّ وَغَيْرِهَا " },
{id: "5247 ", parent: "4005", text: "بَابُ مَنْ لَعَنَهُ النَّبِيُّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ، أَوْ سَبَّهُ، أَوْ دَعَا عَلَيْهِ، وَلَيْسَ هُوَ أَهْلًا لِذَلِكَ، كَانَ لَهُ زَكَاةً وَأَجْرًا وَرَحْمَةً " },
{id: "5248 ", parent: "4005", text: "بَابُ ذَمِّ ذِي الْوَجْهَيْنِ وَتَحْرِيمِ فِعْلِهِ " },
{id: "5249 ", parent: "4005", text: "بَابُ تَحْرِيمِ الْكَذِبِ وَبَيَانِ مَا يُبَاحُ مِنْهُ " },
{id: "5250 ", parent: "4005", text: "بَابُ تَحْرِيمِ النَّمِيمَةِ " },
{id: "5251 ", parent: "4005", text: "بَابُ قُبْحِ الْكَذِبِ وَحُسْنِ الصِّدْقِ وَفَضْلِهِ " },
{id: "5252 ", parent: "4005", text: "بَابُ فَضْلِ مَنْ يَمْلِكُ نَفْسَهُ عِنْدَ الْغَضَبِ وَبِأَيِّ شَيْءٍ يَذْهَبُ الْغَضَبُ " },
{id: "5253 ", parent: "4005", text: "بَابُ خَلْقِ الْإِنْسَانِ خَلْقًا لَا يَتَمَالَكُ " },
{id: "5254 ", parent: "4005", text: "بَابُ النَّهْيِ عَنْ ضَرْبِ الْوَجْهِ " },
{id: "5255 ", parent: "4005", text: "بَابُ الْوَعِيدُ الشَّدِيدُ لِمَنْ عَذَّبَ النَّاسَ بِغَيْرِ حَقٍّ " },
{id: "5256 ", parent: "4005", text: "بَابُ أَمْرِ مَنْ مَرَّ بِسِلَاحٍ فِي مَسْجِدٍ أَوْ سُوقٍ أَوْ غَيْرِهِمَا مِنَ الْمَوَاضِعِ الْجَامِعَةِ لِلنَّاسِ أَنْ يُمْسِكَ بِنِصَالِهَا " },
{id: "5257 ", parent: "4005", text: "بَابُ النَّهْيِ عَنِ الْإِشَارَةِ بِالسِّلَاحِ إِلَى مُسْلِمٍ " },
{id: "5258 ", parent: "4005", text: "بَابُ فَضْلِ إِزَالَةِ الْأَذَى عَنِ الطَّرِيقِ " },
{id: "5259 ", parent: "4005", text: "بَابُ تَحْرِيمِ تَعْذِيبِ الْهِرَّةِ وَنَحْوِهَا مِنَ الْحَيَوَانِ الَّذِي لَا يُؤْذِي " },
{id: "5260 ", parent: "4005", text: "بَابُ تَحْرِيمِ الْكِبْرِ " },
{id: "5261 ", parent: "4005", text: "بَابُ النَّهْيِ عَنْ تَقْنِيطِ الْإِنْسَانِ مِنْ رَحْمَةِ اللهِ تَعَالَى " },
{id: "5262 ", parent: "4005", text: "بَابُ فَضْلِ الضُّعَفَاءِ وَالْخَامِلِينَ " },
{id: "5263 ", parent: "4005", text: "بَابُ النَّهْيِ عَنْ قَوْلِ هَلَكَ النَّاسُ " },
{id: "5264 ", parent: "4005", text: "بَابُ الْوَصِيَّةِ بِالْجَارِ وَالْإِحْسَانِ إِلَيْهِ " },
{id: "5265 ", parent: "4005", text: "بَابُ اسْتِحْبَابِ طَلَاقَةِ الْوَجْهِ عِنْدَ اللِّقَاءِ " },
{id: "5266 ", parent: "4005", text: "بَابُ اسْتِحْبَابِ الشَّفَاعَةِ فِيمَا لَيْسَ بِحَرَامٍ " },
{id: "5267 ", parent: "4005", text: "بَابُ اسْتِحْبَابِ مُجَالَسَةِ الصَّالِحِينَ، وَمُجَانَبَةِ قُرَنَاءِ السُّوءِ " },
{id: "5268 ", parent: "4005", text: "بَابُ فَضْلِ الْإِحْسَانِ إِلَى الْبَنَاتِ " },
{id: "5269 ", parent: "4005", text: "بَابُ فَضْلِ مَنْ يَمُوتُ لَهُ وَلَدٌ فَيَحْتَسِبَهُ " },
{id: "5270 ", parent: "4005", text: "بَابُ إِذَا أَحَبَّ اللهُ عَبْدًا حَبَّبَهُ لِعَبَادِهِ " },
{id: "5271 ", parent: "4005", text: "بَابُ الْأَرْوَاحُ جُنُودٌ مُجَنَّدَةٌ " },
{id: "5272 ", parent: "4005", text: "بَابُ الْمَرْءُ مَعَ مَنْ أَحَبَّ " },
{id: "5273 ", parent: "4005", text: "بَابُ إِذَا أُثْنِيَ عَلَى الصَّالِحِ فَهِيَ بُشْرَى وَلَا تَضُرُّهُ " },
{id: "4006", parent: "كِتَابُ الْقَدَرِ ", text: "" },
{id: "5274 ", parent: "4006", text: "بَابُ كَيْفِيَّةِ خَلْقِ الْآدَمِيِّ فِي بَطْنِ أُمِّهِ وَكِتَابَةِ رِزْقِهِ وَأَجَلِهِ وَعَمَلِهِ وَشَقَاوَتِهِ وَسَعَادَتِهِ " },
{id: "5275 ", parent: "4006", text: "بَابُ حِجَاجِ آدَمَ وَمُوسَى عَلَيْهِمَا السَّلَامُ " },
{id: "5276 ", parent: "4006", text: "بَابُ تَصْرِيفِ اللهِ تَعَالَى الْقُلُوبَ كَيْفَ شَاءَ " },
{id: "5277 ", parent: "4006", text: "بَابُ كُلُّ شَيْءٍ بِقَدَرٍ " },
{id: "5278 ", parent: "4006", text: "بَابُ قُدِّرَ عَلَى ابْنِ آدَمَ حَظُّهُ مِنَ الزِّنَا وَغَيْرِهِ " },
{id: "5279 ", parent: "4006", text: "بَابُ مَعْنَى كُلِّ مَوْلُودٍ يُولَدُ عَلَى الْفِطْرَةِ وَحُكْمِ مَوْتِ أَطْفَالِ الْكُفَّارِ وَأَطْفَالِ الْمُسْلِمِينَ " },
{id: "5280 ", parent: "4006", text: "بَابُ بَيَانِ أَنَّ الْآجَالَ وَالْأَرْزَاقَ وَغَيْرَهَا لَا تَزِيدُ وَلَا تَنْقُصُ عَمَّا سَبَقَ بِهِ الْقَدَرُ " },
{id: "5281 ", parent: "4006", text: "بَابٌ فِي الْأَمْرِ بِالْقُوَّةِ وَتَرْكِ الْعَجْزِ وَالِاسْتِعَانَةِ بِاللهِ وَتَفْوِيضِ الْمَقَادِيرِ لِلَّهِ " },
{id: "4007", parent: "كِتَابُ الْعِلْمِ ", text: "" },
{id: "5282 ", parent: "4007", text: "بَابُ النَّهْيِ عَنِ اتِّبَاعِ مُتَشَابِهِ الْقُرْآنِ، وَالتَّحْذِيرِ مِنْ مُتَّبِعِيهِ، وَالنَّهْيِ عَنِ الِاخْتِلَافِ فِي الْقُرْآنِ " },
{id: "5283 ", parent: "4007", text: "بَابٌ فِي الْأَلَدِّ الْخَصِمِ " },
{id: "5284 ", parent: "4007", text: "بَابُ اتِّبَاعِ سُنَنِ الْيَهُودِ وَالنَّصَارَى " },
{id: "5285 ", parent: "4007", text: "بَابُ هَلَكَ الْمُتَنَطِّعُونَ " },
{id: "5286 ", parent: "4007", text: "بَابُ رَفْعِ الْعِلْمِ وَقَبْضِهِ وَظُهُورِ الْجَهْلِ وَالْفِتَنِ فِي آخِرِ الزَّمَانِ " },
{id: "5287 ", parent: "4007", text: "بَابُ مَنْ سَنَّ سُنَّةً حَسَنَةً أَوْ سَيِّئَةً وَمَنْ دَعَا إِلَى هُدًى أَوْ ضَلَالَةٍ " },
{id: "4008", parent: "كِتَابُ الذِّكْرِ وَالدُّعَاءِ وَالتَّوْبَةِ وَالِاسْتِغْفَارِ ", text: "" },
{id: "5288 ", parent: "4008", text: "بَابُ الْحَثِّ عَلَى ذِكْرِ اللهِ تَعَالَى " },
{id: "5289 ", parent: "4008", text: "بَابٌ فِي أَسْمَاءِ اللهِ تَعَالَى وَفَضْلِ مَنْ أَحْصَاهَا " },
{id: "5290 ", parent: "4008", text: "بَابُ الْعَزْمِ بِالدُّعَاءِ وَلَا يَقُلْ إِنْ شِئْتَ " },
{id: "5291 ", parent: "4008", text: "بَابُ كَرَاهَةِ تَمَنِّي الْمَوْتِ لِضُرٍّ نَزَلَ بِهِ " },
{id: "5292 ", parent: "4008", text: "بَابُ مَنْ أَحَبَّ لِقَاءَ اللهِ أَحَبَّ اللهُ لِقَاءَهُ وَمَنْ كَرِهَ لِقَاءَ اللهِ كَرِهَ اللهُ لِقَاءَهُ " },
{id: "5293 ", parent: "4008", text: "بَابُ فَضْلِ الذِّكْرِ وَالدُّعَاءِ وَالتَّقَرُّبِ إِلَى اللهِ تَعَالَى " },
{id: "5294 ", parent: "4008", text: "بَابُ كَرَاهَةِ الدُّعَاءِ بِتَعْجِيلِ الْعُقُوبَةِ فِي الدُّنْيَا " },
{id: "5295 ", parent: "4008", text: "بَابُ فَضْلِ مَجَالِسِ الذِّكْرِ " },
{id: "5296 ", parent: "4008", text: "بَابُ فَضْلِ الدُّعَاءِ بِاللهُمَّ آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ " },
{id: "5297 ", parent: "4008", text: "بَابُ فَضْلِ التَّهْلِيلِ وَالتَّسْبِيحِ وَالدُّعَاءِ " },
{id: "5298 ", parent: "4008", text: "بَابُ فَضْلِ الِاجْتِمَاعِ عَلَى تِلَاوَةِ الْقُرْآنِ وَعَلَى الذِّكْرِ " },
{id: "5299 ", parent: "4008", text: "بَابُ اسْتِحْبَابِ الِاسْتِغْفَارِ وَالِاسْتِكْثَارِ مِنْهُ " },
{id: "5300 ", parent: "4008", text: "بَابُ اسْتِحْبَابِ خَفْضِ الصَّوْتِ بِالذِّكْرِ " },
{id: "5301 ", parent: "4008", text: "بَابُ التَّعَوُّذِ مِنْ شَرِّ الْفِتَنِ وَغَيْرِهَا " },
{id: "5302 ", parent: "4008", text: "بَابُ التَّعَوُّذِ مِنَ الْعَجْزِ وَالْكَسَلِ وَغَيْرِهِ " },
{id: "5303 ", parent: "4008", text: "بَابٌ فِي التَّعَوُّذِ مِنْ سُوءِ الْقَضَاءِ وَدَرَكِ الشَّقَاءِ وَغَيْرِهِ " },
{id: "5304 ", parent: "4008", text: "بَابُ مَا يَقُولُ عِنْدَ النَّوْمِ وَأَخْذِ الْمَضْجَعِ " },
{id: "5305 ", parent: "4008", text: "بَابُ التَّعَوُّذِ مِنْ شَرِّ مَا عُمِلَ وَمِنْ شَرِّ مَا لَمْ يُعْمَلْ " },
{id: "5306 ", parent: "4008", text: "بَابُ التَّسْبِيحِ أَوَّلَ النَّهَارِ وَعِنْدَ النَّوْمِ " },
{id: "5307 ", parent: "4008", text: "بَابُ اسْتِحْبَابِ الدُّعَاءِ عِنْدَ صِيَاحِ الدِّيكِ " },
{id: "5308 ", parent: "4008", text: "بَابُ دُعَاءِ الْكَرْبِ " },
{id: "5309 ", parent: "4008", text: "بَابُ فَضْلِ سُبْحَانَ اللهِ وَبِحَمْدِهِ " },
{id: "5310 ", parent: "4008", text: "بَابُ فَضْلِ الدُّعَاءِ لِلْمُسْلِمِينَ بِظَهْرِ الْغَيْبِ " },
{id: "5311 ", parent: "4008", text: "بَابُ اسْتِحْبَابِ حَمْدِ اللهِ تَعَالَى بَعْدَ الْأَكْلِ وَالشُّرْبِ " },
{id: "5312 ", parent: "4008", text: "بَابُ بَيَانِ أَنَّهُ يُسْتَجَابُ لِلدَّاعِي مَا لَمْ يَعْجَلْ فَيَقُولُ: دَعَوْتُ فَلَمْ يُسْتَجَبْ لِي " },
{id: "4009", parent: "كِتَابُ الرِّقَاقِ ", text: "" },
{id: "5313 ", parent: "4009", text: "بَابُ أَكْثَرُ أَهْلِ الْجَنَّةِ الْفُقَرَاءُ وَأَكْثَرُ أَهْلِ النَّارِ النِّسَاءُ وَبَيَانِ الْفِتْنَةِ بِالنِّسَاءِ " },
{id: "5314 ", parent: "4009", text: "بَابُ قِصَّةِ أَصْحَابِ الْغَارِ الثَّلَاثَةِ وَالتَّوَسُّلِ بِصَالِحِ الْأَعْمَالِ " },
{id: "4010", parent: "كِتَابُ التَّوْبَةِ ", text: "" },
{id: "5315 ", parent: "4010", text: "بَابٌ فِي الْحَضِّ عَلَى التَّوْبَةِ وَالْفَرَحِ بِهَا " },
{id: "5316 ", parent: "4010", text: "بَابُ سُقُوطِ الذُّنُوبِ بِالِاسْتِغْفَارِ تَوْبَةً " },
{id: "5317 ", parent: "4010", text: "بَابُ فَضْلِ دَوَامِ الذِّكْرِ وَالْفِكْرِ فِي أُمُورِ الْآخِرَةِ وَالْمُرَاقَبَةِ وَجَوَازِ تَرْكِ ذَلِكَ فِي بَعْضِ الْأَوْقَاتِ وَالِاشْتِغَالِ بِالدُّنْيَا " },
{id: "5318 ", parent: "4010", text: "بَابٌ فِي سِعَةِ رَحْمَةِ اللهِ تَعَالَى وَأَنَّهَا سَبَقَتْ غَضَبَهُ " },
{id: "5319 ", parent: "4010", text: "بَابُ قَبُولِ التَّوْبَةِ مِنَ الذُّنُوبِ وَإِنْ تَكَرَّرَتِ الذُّنُوبُ وَالتَّوْبَةُ " },
{id: "5320 ", parent: "4010", text: "بَابُ غَيْرَةِ اللهِ تَعَالَى وَتَحْرِيمِ الْفَوَاحِشِ " },
{id: "5321 ", parent: "4010", text: "بَابُ قَوْلِهِ تَعَالَى: {إِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّئَاتِ} [هود: 114] " },
{id: "5322 ", parent: "4010", text: "بَابُ قَبُولِ تَوْبَةِ الْقَاتِلِ وَإِنْ كَثُرَ قَتْلُهُ " },
{id: "5323 ", parent: "4010", text: "بَابُ حَدِيثِ تَوْبَةِ كَعْبِ بْنِ مَالِكٍ وَصَاحِبَيْهِ " },
{id: "5324 ", parent: "4010", text: "بَابٌ فِي حَدِيثِ الْإِفْكِ وَقَبُولِ تَوْبَةِ الْقَاذِفِ " },
{id: "5325 ", parent: "4010", text: "بَابُ بَرَاءَةِ حَرَمِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ مِنَ الرِّيبَةِ " },
{id: "4011", parent: "كِتَابُ صِفَاتِ الْمُنَافِقِينَ وَأَحْكَامِهِمْ ", text: "" },
{id: "5326 ", parent: "4011", text: "كِتَابُ صِفَاتِ الْمُنَافِقِينَ وَأَحْكَامِهِمْ " },
{id: "4012", parent: "كِتَابُ صِفَةِ الْقِيَامَةِ وَالْجَنَّةِ وَالنَّارِ", text: "" },
{id: "5327 ", parent: "4012", text: "كِتَابُ صِفَةِ الْقِيَامَةِ وَالْجَنَّةِ وَالنَّارِ " },
{id: "5328 ", parent: "4012", text: "بَابُ ابْتِدَاءِ الْخَلْقِ وَخَلْقِ آدَمَ عَلَيْهِ السَّلَامُ " },
{id: "5329 ", parent: "4012", text: "بَابٌ فِي الْبَعْثِ وَالنُّشُورِ وَصِفَةِ الْأَرْضِ يَوْمَ الْقِيَامَةِ " },
{id: "5330 ", parent: "4012", text: "بَابُ نُزُلِ أَهْلِ الْجَنَّةِ " },
{id: "5331 ", parent: "4012", text: "بَابُ سُؤَالِ الْيَهُودِ النَّبِيَّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ عَنِ الرُّوحِ وقَوْلِهِ تَعَالَى: {يَسْأَلُونَكَ عَنِ الرُّوحِ} [الإسراء: 85] الْآيَةَ " },
{id: "5332 ", parent: "4012", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {وَمَا كَانَ اللهُ لِيُعَذِّبَهُمْ وَأَنْتَ فِيهِمْ} [الأنفال: 33] الْآيَةَ " },
{id: "5333 ", parent: "4012", text: "بَابُ قَوْلِهِ: {إِنَّ الْإِنْسَانَ لَيَطْغَى أَنْ رَآهُ اسْتَغْنَى} [العلق: 7] " },
{id: "5334 ", parent: "4012", text: "بَابُ الدُّخَانِ " },
{id: "5335 ", parent: "4012", text: "بَابُ انْشِقَاقِ الْقَمَرِ " },
{id: "5336 ", parent: "4012", text: "بَابُ لَا أَحَدَ أَصْبَرُ عَلَى أَذًى مِنَ اللهِ عَزَّ وَجَلَّ " },
{id: "5337 ", parent: "4012", text: "بَابُ طَلَبِ الْكَافِرِ الْفِدَاءَ بِمِلْءِ الْأَرْضِ ذَهَبًا " },
{id: "5338 ", parent: "4012", text: "بَابُ يُحْشَرُ الْكَافِرُ عَلَى وَجْهِهِ " },
{id: "5339 ", parent: "4012", text: "بَابُ صَبْغِ أَنْعَمِ أَهْلِ الدُّنْيَا فِي النَّارِ وَصَبْغِ أَشَدِّهِمْ بُؤْسًا فِي الْجَنَّةِ " },
{id: "5340 ", parent: "4012", text: "بَابُ جَزَاءِ الْمُؤْمِنِ بِحَسَنَاتِهِ فِي الدُّنْيَا وَالْآخِرَةِ وَتَعْجِيلِ حَسَنَاتِ الْكَافِرِ فِي الدُّنْيَا " },
{id: "5341 ", parent: "4012", text: "بَابُ مَثَلُ الْمُؤْمِنِ كَالزَّرْعِ وَمَثَلُ الْكَافِرِ كَشَجَرِ الْأَرْزِ " },
{id: "5342 ", parent: "4012", text: "بَابُ مَثَلُ الْمُؤْمِنِ مَثَلُ النَّخْلَةِ " },
{id: "5343 ", parent: "4012", text: "بَابُ تَحْرِيشِ الشَّيْطَانِ وَبَعْثِهِ سَرَايَاهُ لِفِتْنَةِ النَّاسِ وَأَنَّ مَعَ كُلِّ إِنْسَانٍ قَرِينًا " },
{id: "5344 ", parent: "4012", text: "بَابُ لَنْ يَدْخُلَ أَحَدٌ الْجَنَّةَ بِعَمَلِهِ بَلْ بِرَحْمَةِ اللهِ تَعَالَى " },
{id: "5345 ", parent: "4012", text: "بَابُ إِكْثَارِ الْأَعْمَالِ وَالِاجْتِهَادِ فِي الْعِبَادَةِ " },
{id: "5346 ", parent: "4012", text: "بَابُ الِاقْتِصَادِ فِي الْمَوْعِظَةِ " },
{id: "4013", parent: "كِتَابُ الْجَنَّةِ وَصِفَةِ نَعِيمِهَا وَأَهْلِهَا ", text: "" },
{id: "5347 ", parent: "4013", text: "كِتَابُ الْجَنَّةِ وَصِفَةِ نَعِيمِهَا وَأَهْلِهَا " },
{id: "5348 ", parent: "4013", text: "بَابُ إِنَّ فِي الْجَنَّةِ شَجَرَةً يَسِيرُ الرَّاكِبُ فِي ظِلِّهَا مِائَةَ عَامٍ لَا يَقْطَعُهَا " },
{id: "5349 ", parent: "4013", text: "بَابُ إِحْلَالِ الرِّضْوَانِ عَلَى أَهْلِ الْجَنَّةِ فَلَا يَسْخَطُ عَلَيْهِمْ أَبَدًا " },
{id: "5350 ", parent: "4013", text: "بَابُ تَرَائِي أَهْلِ الْجَنَّةِ أَهْلَ الْغُرَفِ، كَمَا يُرَى الْكَوْكَبُ فِي السَّمَاءِ " },
{id: "5351 ", parent: "4013", text: "بَابُ فِيمَنْ يَوَدُّ رُؤْيَةَ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ بِأَهْلِهِ وَمَالِهِ " },
{id: "5352 ", parent: "4013", text: "بَابٌ فِي سُوقِ الْجَنَّةِ وَمَا يَنَالُونَ فِيهَا مِنَ النَّعِيمِ وَالْجَمَالِ " },
{id: "5353 ", parent: "4013", text: "بَابُ أَوَّلُ زُمْرَةٍ تَدْخُلُ الْجَنَّةَ عَلَى صُورَةِ الْقَمَرِ لَيْلَةَ الْبَدْرِ وَصِفَاتُهُمْ وَأَزْوَاجُهُمْ " },
{id: "5354 ", parent: "4013", text: "بَابٌ فِي صِفَاتِ الْجَنَّةِ وَأَهْلِهَا وَتَسْبِيحِهِمْ فِيهَا بُكْرَةً وَعَشِيًّا " },
{id: "5355 ", parent: "4013", text: "بَابٌ فِي دَوَامِ نَعِيمِ أَهْلِ الْجَنَّةِ وقَوْلِهِ تَعَالَى: {وَنُودُوا أَنْ تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ} [الأعراف: 43] " },
{id: "5356 ", parent: "4013", text: "بَابٌ فِي صِفَةِ خِيَامِ الْجَنَّةِ وَمَا لِلْمُؤْمِنِينَ فِيهَا مِنَ الْأَهْلِينَ " },
{id: "5357 ", parent: "4013", text: "بَابُ مَا فِي الدُّنْيَا مِنْ أَنْهَارِ الْجَنَّةِ " },
{id: "5358 ", parent: "4013", text: "بَابُ يَدْخُلُ الْجَنَّةَ أَقْوَامٌ أَفْئِدَتُهُمْ مِثْلُ أَفْئِدَةِ الطَّيْرِ " },
{id: "5359 ", parent: "4013", text: "بَابٌ فِي شِدَّةِ حَرِّ نَارِ جَهَنَّمَ وَبُعْدِ قَعْرِهَا وَمَا تَأْخُذُ مِنَ الْمُعَذَّبِينَ " },
{id: "5360 ", parent: "4013", text: "بَابُ النَّارُ يَدْخُلُهَا الْجَبَّارُونَ وَالْجَنَّةُ يَدْخُلُهَا الضُّعَفَاءُ " },
{id: "5361 ", parent: "4013", text: "بَابُ فَنَاءِ الدُّنْيَا وَبَيَانِ الْحَشْرِ يَوْمَ الْقِيَامَةِ " },
{id: "5362 ", parent: "4013", text: "بَابٌ فِي صِفَةِ يَوْمِ الْقِيَامَةِ أَعَانَنَا اللهُ عَلَى أَهْوَالِهَا " },
{id: "5363 ", parent: "4013", text: "بَابُ الصِّفَاتِ الَّتِي يُعْرَفُ بِهَا فِي الدُّنْيَا أَهْلُ الْجَنَّةِ وَأَهْلُ النَّارِ " },
{id: "5364 ", parent: "4013", text: "بَابُ عَرْضِ مَقْعَدِ الْمَيِّتِ مِنَ الْجَنَّةِ أَوِ النَّارِ عَلَيْهِ، وَإِثْبَاتِ عَذَابِ الْقَبْرِ وَالتَّعَوُّذِ مِنْهُ " },
{id: "5365 ", parent: "4013", text: "بَابُ إِثْبَاتِ الْحِسَابِ " },
{id: "5366 ", parent: "4013", text: "بَابُ الْأَمْرِ بِحُسْنِ الظَّنِّ بِاللهِ تَعَالَى عِنْدَ الْمَوْتِ " },
{id: "4014", parent: "كِتَابُ الْفِتَنِ وَأَشْرَاطِ السَّاعَةِ ", text: "" },
{id: "5367 ", parent: "4014", text: "بَابُ اقْتِرَابِ الْفِتَنِ وَفَتْحِ رَدْمِ يَأْجُوجَ وَمَأْجُوجَ " },
{id: "5368 ", parent: "4014", text: "بَابُ الْخَسْفِ بِالْجَيْشِ الَّذِي يَؤُمُّ الْبَيْتَ " },
{id: "5369 ", parent: "4014", text: "بَابُ نُزُولِ الْفِتَنِ كَمَوَاقِعِ الْقَطْرِ " },
{id: "5370 ", parent: "4014", text: "بَابُ إِذَا تَوَاجَهَ الْمُسْلِمَانِ بِسَيْفَيْهِمَا " },
{id: "5371 ", parent: "4014", text: "بَابُ هَلَاكِ هَذِهِ الْأُمَّةِ بَعْضِهِمْ بِبَعْضٍ " },
{id: "5372 ", parent: "4014", text: "بَابُ إِخْبَارِ النَّبِيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ فِيمَا يَكُونُ إِلَى قِيَامِ السَّاعَةِ " },
{id: "5373 ", parent: "4014", text: "بَابٌ فِي الْفِتْنَةِ الَّتِي تَمُوجُ كَمَوْجِ الْبَحْرِ " },
{id: "5374 ", parent: "4014", text: "بَابُ لَا تَقُومُ السَّاعَةُ حَتَّى يَحْسِرَ الْفُرَاتُ عَنْ جَبَلٍ مِنْ ذَهَبٍ " },
{id: "5375 ", parent: "4014", text: "بَابٌ فِي فَتْحِ قُسْطَنْطِينِيَّةَ، وَخُرُوجِ الدَّجَّالِ وَنُزُولِ عِيسَى ابْنِ مَرْيَمَ " },
{id: "5376 ", parent: "4014", text: "بَابُ تَقُومُ السَّاعَةُ وَالرُّومُ أَكْثَرُ النَّاسِ " },
{id: "5377 ", parent: "4014", text: "بَابُ إِقْبَالِ الرُّومِ فِي كَثْرَةِ الْقَتْلِ عِنْدَ خُرُوجِ الدَّجَّالِ " },
{id: "5378 ", parent: "4014", text: "بَابُ مَا يَكُونُ مِنْ فُتُوحَاتِ الْمُسْلِمِينَ قَبْلَ الدَّجَّالِ " },
{id: "5379 ", parent: "4014", text: "بَابٌ فِي الْآيَاتِ الَّتِي تَكُونُ قَبْلَ السَّاعَةِ " },
{id: "5380 ", parent: "4014", text: "بَابُ لَا تَقُومُ السَّاعَةُ حَتَّى تَخْرُجَ نَارٌ مِنْ أَرْضِ الْحِجَازِ " },
{id: "5381 ", parent: "4014", text: "بَابٌ فِي سُكْنَى الْمَدِينَةِ وَعِمَارَتِهَا قَبْلَ السَّاعَةِ " },
{id: "5382 ", parent: "4014", text: "بَابُ الْفِتْنَةُ مِنَ الْمَشْرِقِ مِنْ حَيْثُ يَطْلُعُ قَرْنَا الشَّيْطَانِ " },
{id: "5383 ", parent: "4014", text: "بَابُ لَا تَقُومُ السَّاعَةُ حَتَّى تَعْبُدَ دَوْسٌ ذَا الْخَلَصَةِ " },
{id: "5384 ", parent: "4014", text: "بَابُ لَا تَقُومُ السَّاعَةُ حَتَّى يَمُرَّ الرَّجُلُ بِقَبْرِ الرَّجُلِ، فَيَتَمَنَّى أَنْ يَكُونَ مَكَانَ الْمَيِّتِ مِنَ الْبَلَاءِ " },
{id: "5385 ", parent: "4014", text: "بَابُ ذِكْرِ ابْنِ صَيَّادٍ " },
{id: "5386 ", parent: "4014", text: "بَابُ ذِكْرِ الدَّجَّالِ وَصِفَتِهِ وَمَا مَعَهُ " },
{id: "5387 ", parent: "4014", text: "بَابٌ فِي صِفَةِ الدَّجَّالِ، وَتَحْرِيمِ الْمَدِينَةِ عَلَيْهِ وَقَتْلِهِ الْمُؤْمِنَ وَإِحْيَائِهِ " },
{id: "5388 ", parent: "4014", text: "بَابٌ فِي الدَّجَّالِ وَهُوَ أَهْوَنُ عَلَى اللهِ عَزَّ وَجَلَّ " },
{id: "5389 ", parent: "4014", text: "بَابٌ فِي خُرُوجِ الدَّجَّالِ وَمُكْثِهِ فِي الْأَرْضِ، وَنُزُولِ عِيسَى وَقَتْلِهِ إِيَّاهُ، وَذَهَابِ أَهْلِ الْخَيْرِ وَالْإِيمَانِ، وَبَقَاءِ شِرَارِ النَّاسِ وَعِبَادَتِهِمُ الْأَوْثَانَ، وَالنَّفْخِ فِي الصُّورِ، وَبَعْثِ مَنْ فِي الْقُبُورِ " },
{id: "5390 ", parent: "4014", text: "باب قصة الجساسة " },
{id: "5391 ", parent: "4014", text: "بَابٌ فِي بَقِيَّةٍ مِنْ أَحَادِيثِ الدَّجَّالِ " },
{id: "5392 ", parent: "4014", text: "بَابُ فَضْلِ الْعِبَادَةِ فِي الْهَرْجِ " },
{id: "5393 ", parent: "4014", text: "بَابُ قُرْبِ السَّاعَةِ " },
{id: "5394 ", parent: "4014", text: "بَابُ مَا بَيْنَ النَّفْخَتَيْنِ " },
{id: "4015", parent: "كِتَابُ الزُّهْدِ وَالرَّقَائِقِ ", text: "" },
{id: "5395 ", parent: "4015", text: "كِتَابُ الزُّهْدِ وَالرَّقَائِقِ " },
{id: "5396 ", parent: "4015", text: "بَابُ لَا تَدْخُلُوا مَسَاكِنَ الَّذِينَ ظَلَمُوا أَنْفُسَهُمْ، إِلَّا أَنْ تَكُونُوا بَاكِينَ " },
{id: "5397 ", parent: "4015", text: "بَابُ الْإِحْسَانِ إِلَى الْأَرْمَلَةِ وَالْمِسْكِينِ وَالْيَتِيمِ " },
{id: "5398 ", parent: "4015", text: "بَابُ فَضْلِ بِنَاءِ الْمَسَاجِدِ " },
{id: "5399 ", parent: "4015", text: "بَابُ الصَّدَقَةِ فِي الْمَسَاكِينِ " },
{id: "5400 ", parent: "4015", text: "بَابُ مَنْ أَشْرَكَ فِي عَمَلِهِ غَيْرَ اللهِ " },
{id: "5401 ", parent: "4015", text: "بَابُ التَّكَلُّمِ بِالْكَلِمَةِ يَهْوِي بِهَا فِي النَّارِ " },
{id: "5402 ", parent: "4015", text: "بَابُ عُقُوبَةِ مَنْ يَأْمُرُ بِالْمَعْرُوفِ وَلَا يَفْعَلُهُ، وَيَنْهَى عَنِ الْمُنْكَرِ وَيَفْعَلُهُ " },
{id: "5403 ", parent: "4015", text: "بَابُ النَّهْيِ عَنْ هَتْكِ الْإِنْسَانِ سِتْرَ نَفْسِهِ " },
{id: "5404 ", parent: "4015", text: "بَابُ تَشْمِيتِ الْعَاطِسِ، وَكَرَاهَةِ التَّثَاؤُبِ " },
{id: "5405 ", parent: "4015", text: "بَابٌ فِي أَحَادِيثَ مُتَفَرِّقَةٍ " },
{id: "5406 ", parent: "4015", text: "بَابٌ فِي الْفَأْرِ وَأَنَّهُ مَسْخٌ " },
{id: "5407 ", parent: "4015", text: "بَابُ لَا يُلْدَغُ الْمُؤْمِنُ مِنْ جُحْرٍ مَرَّتَيْنِ " },
{id: "5408 ", parent: "4015", text: "بَابُ الْمُؤْمِنُ أَمْرُهُ كُلُّهُ خَيْرٌ " },
{id: "5409 ", parent: "4015", text: "بَابُ النَّهْيِ عَنِ الْمَدْحِ، إِذَا كَانَ فِيهِ إِفْرَاطٌ وَخِيفَ مِنْهُ فِتْنَةٌ عَلَى الْمَمْدُوحِ " },
{id: "5410 ", parent: "4015", text: "بَابُ مُنَاوَلَةِ الْأَكْبَرِ " },
{id: "5411 ", parent: "4015", text: "بَابُ التَّثَبُّتِ فِي الْحَدِيثِ وَحُكْمِ كِتَابَةِ الْعِلْمِ " },
{id: "5412 ", parent: "4015", text: "بَابُ قِصَّةِ أَصْحَابِ الْأُخْدُودِ وَالسَّاحِرِ وَالرَّاهِبِ وَالْغُلَامِ " },
{id: "5413 ", parent: "4015", text: "بَابُ حَدِيثِ جَابِرٍ الطَّوِيلِ وَقِصَّةِ أَبِي الْيَسَرِ " },
{id: "5414 ", parent: "4015", text: "بَابٌ فِي حَدِيثِ الْهِجْرَةِ وَيُقَالُ لَهُ حَدِيثُ الرَّحْلِ بِالْحَاءِ " },
{id: "4016", parent: "كِتَابُ التَّفْسِيرِ ", text: "" },
{id: "5415 ", parent: "4016", text: "كِتَابُ التَّفْسِيرِ " },
{id: "5416 ", parent: "4016", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللهِ} [الحديد: 16] " },
{id: "5417 ", parent: "4016", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {خُذُوا زِينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ} [الأعراف: 31] " },
{id: "5418 ", parent: "4016", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {وَلَا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَاءِ} [النور: 33] " },
{id: "5419 ", parent: "4016", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {أُولَئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلَى رَبِّهِمُ الْوَسِيلَةَ} [الإسراء: 57] " },
{id: "5420 ", parent: "4016", text: "بَابٌ فِي سُورَةِ بَرَاءَةٌ وَالْأَنْفَالِ وَالْحَشْرِ " },
{id: "5421 ", parent: "4016", text: "بَابٌ فِي نُزُولِ تَحْرِيمِ الْخَمْرِ " },
{id: "5422 ", parent: "4016", text: "بَابٌ فِي قَوْلِهِ تَعَالَى: {هَذَانِ خَصْمَانِ اخْتَصَمُوا فِي رَبِّهِمْ} [الحج: 19] " }
])
